css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.bing.com") {
  #sbox {
    position: fixed !important;
    top: 40% !important;
    left: 0 !important;
    width: 100% !important;
    text-align: center;
    margin: 2em;
  }
  .hp_sw_logo  {
    position: fixed !important;
    top: calc(40% - 30px);
    left: calc(50% - 66px);
  }
  .sa_n .sa_s  {
    width: 100%;
  }
  #sb_form_q {
    text-align: center;
  }
  
  
  
  /*remove_bottom*/
  #musCardContent {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.55) !important;
  }
  #sb_foot {
    background-image: none;
    background-color: rgba(0, 0, 0, 0.7) !important;
  }
  #sw_footL a, #sw_footL span{
    color: rgba(255,255,255,0.5) !important;
  }
  /*#sb_foot {
    height: 5px;
    transition: height 0.25s;
  }
  #sb_foot:hover {
    height: 30px;
  }*/
  #hp_bottomCell {
    bottom: -38px;
    transition: bottom 0.5s cubic-bezier(0.165, 0.84, 0.44, 1);
    
  }
  #hp_bottomCell:hover {
    bottom: 0;
  }
  
  
  
  
  
  
  
  /*Animation*/
  .sa_as {
    display: block !important;
    height: 0;
    transition: height 0.25s;
  }
  .sa_as[style="display: block;"] {
    height: 283px;
  }

  
  * {
    min-width: 0 !important;
  }

  body {
    min-width: 100% !important;
  }
  
  
  

.b_searchbox {
    background-color: transparent;
    border-color: rgba(255,255,255,0.75) !important;
  }
  .b_searchboxForm, .sa_as { /*search bar*/
    background-color: rgba(255,255,255,0.75) !important;
  }
  #sb_form_q {
    transition: width 0.4s;
    background-color: transparent !important;
  }
  .sa_sg {
    transition: background-color 0.3s;
  }  

  .sa_as{
    animation: shake 0.5s;
  }
  

  #hp_container {
   min-height: 0;
   max-height: none;
  }

  #bgDiv {
    position: fixed;
    top: 0 !important;
    left: 0 !important;
    height: 100% !important;
    width: 100% !important;
    max-width: none;
    max-height: none;
    animation: myfirst 1s;
  }


  /*Menu*/
  #sc_hdu > li {
    /*background-color: #333;
    padding: 5px 0;*/
    border-radius: 5px;
    margin: 0px;
  }
  #hp_sw_hdr {
    position: fixed;
    left: 0;
    min-width: 1000px !important;
    
    background-color: rgba(0,0,0,0.5);
  }
  #sb_foot {
    background-color: rgba(0,0,0,0.8);
  }
  
  .sw_pref {
    border-width: 0px;
    position: relative;
    top: -5px;
  }
  
  /* Standard syntax */
  @keyframes myfirst {
    0% {
      /*transform:skew(10deg);*/
      opacity: 0;
      filter: grayscale(1);
    } 
    100% {
      opacity: 1;
      filter: grayscale(0);
    }
  }




}
