css:
@-moz-document url-prefix("http://userstyles.org/"), url-prefix("http://forum.userstyles.org/"), url-prefix("https://forum.userstyles.org"), url-prefix("https://userstyles.org/") {


* {color: #BBBBBB;}

a:link {color:#9E9E9E  !important;}      
a:visited {color:#4E4E4E  !important;}  
a:hover {color:#979797  !important;}  
a:active {color:#979797  !important;} 

h1, h2, h3, h4, h5, h6 {color: #535353!important;} 
.MenuItems a, .MenuItems a:link, .MenuItems a:visited, .MenuItems a:active,.TextColor,select,div.QuoteText,input.InputBox,textarea,strong,.Box h4,.Button,span.Title,.Warning,.Activities li.Condensed .Excerpt{color: #9C9C9C!important;} 

::-webkit-scrollbar {height: 12px!important;width: 12px!important;background: -webkit-linear-gradient(40deg , #000000 , #1e1e1e , #000000 100%)!important;}
::-webkit-scrollbar-thumb {background-color: #840000!important;border-radius: 16px!important;}


/*Border Remover*/
.FlyoutMenu,.Button,hr,.Flyout:after,.Flyout:before,.MenuItems,.NavButton,select,.Foot,.EmoticonBox,.EmoticonContainer,code,.Item,body.Profile ul.Activities,.Attachment:hover .FileHover,h2,.Warning,form,.MessageForm,.TabLink, .TabLink:link,.PanelInfo,.PanelInfo li,a.BigButton,.DataList .Item,.Tabs,pre,textarea,input,.more,#screenshots {border:0 !important; border-left:0 !important; border-right:0 !important; border-top:0 !important; border-bottom:0 !important;}

/*Text-Shadow Remover*/
.Button,.NavButton,.Button,a.BigButton {text-shadow: 0 0 0 !important;}

/*Box-Shadow Remover*/
.EmoticonContainer,.Box {box-shadow: 0 0 0 !important;-webkit-box-shadow: 0 0 0 !important;}

/*Background Transparent Remover*/
table.PreferenceGroup tbody tr:hover td,.Button,.MenuItems,.Tag,.NavButton,.listing-no-screenshot,.Foot,.EmotifyDropdownActive,.EmoticonBox,.EmoticonContainer,code,.edit-setting,#MessageForm,.Preview,.Attachment:hover .FileHover,.SubTab,.Warning,.Body,h2,.errorExplanation,.MessageForm,form,#DiscussionForm,.DataList .Meta .Announcement,strong,.Meta.MessageForm,div.Comment blockquote.UserQuote,.DataList .Meta a.Category,.DataList li.Mine .Meta a.Category,.DataList li.Mine .Meta a.Category,.Tabs li a span,.Active .TabLink,.PanelInfo li,.PanelInfo .Active,.Box,a.BigButton,.Mine,.Tabs,#Head,body,.lb-outerContainer{background-color: transparent !important;}

/*Transparent Gradient Remover*/
code{background-image: linear-gradient(to bottom, rgba(0, 0, 0, 0) 0%, rgba(0, 0, 0, 0) 100%)!important;}

/*Background*/
html {background-image: url("https://scontent-sjc2-1.xx.fbcdn.net/hphotos-xap1/t31.0-8/10355421_10204305389583425_7513189854322664998_o.jpg");background-attachment: fixed;}

/*Column*/
.FlyoutMenu,#Panel,.Attachment:hover .FileHover,.Tabs li a span,.Menu,#Content,#main-header, #content-wrapper {background-image: linear-gradient(to right bottom, #050505 52%, #000000 53%, #000000 100%)!important;border-radius: 10px !important;box-shadow: 0 0 34px #000 !important;}

/*Column 2*/
section#obsoletion-message,blockquote,.MenuItems,.Tag,.screenshot-thumbnail,.listing-no-screenshot,.EmoticonContainer,.Warning,.errorExplanation,.lb-outerContainer,#left-sidebar>* {background-image: linear-gradient(to right bottom, #2B2B2B 52%, #000000 53%, #3A3A3A 100%)!important;background-color: transparent !important;border-radius: 10px !important;box-shadow: 0 0 34px #000 !important;}

/*Column 3*/
.Item {background-image: linear-gradient(to right bottom, rgba(37, 27, 27, 0.08) 52%, #000000 53%, rgba(71, 71, 71, 0.44) 100%)!important;background-color: transparent !important;border-radius: 10px !important;box-shadow: 0 0 34px #000 !important;}

/*Input*/
#remove_screenshot_9128,input {outline: none!important;background-image: linear-gradient(to right bottom, #535353 0%, #000000 100%) !important;background-color: transparent !important;border-radius: 10px !important;box-shadow: 0 0 34px #000 !important;}

/*Button*/
.Button,.NavButton,.no-install,select,.DataList .Meta a.Category,.TabLink, .TabLink:link,a.BigButton,.install-info,.install {background-image: linear-gradient(to right bottom, #3F3F3F 0%, #000000 100%) !important;background-color: transparent !important;border-radius: 43px 8px 43px 8px !important;box-shadow: 0 0 34px #000 !important;}

/*Button Hover*/
.Button:hover,.NavButton:hover,.no-install:hover,.install-info:hover,.install:hover {background-image: linear-gradient(to right bottom, #535353 0%, #000000 100%) !important;background-color: transparent !important;border-radius: 43px 8px 43px 8px !important;box-shadow: 0 0 34px #000 !important;}

/*Text Field*/
pre,textarea {outline: none!important; background-color: transparent !important;box-shadow: 0 0 34px #000 !important;border-radius: 7px !important;background-image: linear-gradient(to right bottom, #181818 52%, #000000 53%, #1D1D1D 100%)!important;}

/*ScreenShot Image*/
.screenshot-thumbnail{border-radius: 7px!important; box-shadow: 0 0 34px #000 !important;}

/*Margin-Top*/
.Item{margin-top:10px !important; box-shadow: 0 0 34px #000 !important;}
.ContentColumn{margin: 15px 0px 0px 230px!important;}

/*Box-Shadow Remover*/
.EmotifyDropdownActive {box-shadow: 0 0 10px #000 !important;}

/*padding*/
.FlyoutMenu,blockquote,#main-article,#Panel,.FilterMenu,.Box,.ContentColumn{padding: 10px!important;}

/*Other*/
.Border {background: none repeat scroll 0 0 rgba(0, 0, 0, 0.7)!important;}
.EmoticonBox{width:43px!important;}
.FlyoutMenu, #Panel, .Attachment:hover .FileHover, .Tabs li a span, .Menu, #Content, #main-header, #content-wrapper{border-top: 1px dotted #5C5C5C !important;border-bottom: 1px dotted #990000 !important;}
.Item .OptionsTitle {visibility: visible!important;}

/*Bottom Logo Icon*/
.PoweredByVanilla {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/vanillaforums.png)!important;
background-repeat: no-repeat !important;
}

/*Globe Icon*/
.SpNotifications {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/spritesred.png)!important;
background-position: -32px -212px !important;
background-repeat: no-repeat !important;
}

/*Mail Icon*/
.SpInbox {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/spritesred.png)!important;
background-position: 0px -100px !important;
background-repeat: no-repeat !important;
}

/*Star Icon*/
.SpBookmarks {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/spritesred.png)!important;
background-position: -81px -244px !important;
background-repeat: no-repeat !important;
}

/*Gear Icon*/
.SpOptions {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/spritesred.png)!important;
background-position: 0px -83px !important;
background-repeat: no-repeat !important;
}

/*Edit Icon*/
.OptionsTitle {
background-image: url(http://files.enjin.com/106810/websitestyles/userstyles/spritesred.png)!important;
background-position: 6px -83px !important;
background-repeat: no-repeat !important;
}

.MItem.UserAgent:before {content: "Browser " !important;}    
#main-header>h1 {font-size: 23px !important;}     
time:after,.MItem.UserAgent:after {content: " - " attr(title) !important;}
time:after,.MItem.UserAgent:before,.MItem.UserAgent:after{font-size: 9px !important;color:#D30000 !important;  }
.DiscussionMeta {line-height: 2 !important;}
}
