css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("instasync.com") {
body, body.dim, .chat, .nav-tabs, .chat-tabs, .chat-messages, .user-list, .navbar, .navbar-default, .navbar-fixed-top, #cin, .tab-content, .room-container .room-component-row, .room-container .room-info-row, .playlist, .playlist li, .poll, .footer, .dropdown-menu, #join_username, .active > a, .nav > li > a:hover, .nav > li > a:active, .nav > li > a:focus, .form-control, .modal-header, .modal-body, .modal-footer, .panel, .panel-heading, .page-container {
  background-color: #000 !important;
  background-image: none !important;
  border-color: #333 !important;
}
body.dim, body {
  background-color: #111 !important;
  width: 90%
}
.navbar-brand, .navbar-nav > li > a {
  text-shadow: none !important;
}
.navbar-default .navbar-nav > .open > a, .navbar-default .navbar-nav > .open > a:focus, .dropdown-menu > li > a:hover, .dropdown-menu > li > a:focus {
  background-color: #transparent !important;
  color: #f00 !important;
}
.dropdown-menu > li > a:hover {
  background-image: linear-gradient(to bottom, #000 0px, #111 0%) !important;
  color: #666 !important;
}
.dropdown-menu, .dropdown-menu {
  background-color: rgba(0,0,0, 0.9) !important;
}
p, i, b {
  color: #333 !important;
}
.navbar-default {
  border-color: #333 !important;
  position: absolute !important;
}
.nav-tabs > li.active > a, .nav-tabs > li.active > a:hover, .nav-tabs > li.active > a:focus {
  border-color: #333 #333 transparent !important;
}
.nav-tabs {
    border-bottom: 1px solid #333;
}
.poll, .poll-votes {
  border: 1px solid #333 !important;
}
.playlist li.active {
  background-color: #111 !important;
}
#cin {
  border-color: #333 !important;
  border-style: solid solid solid !important;
  border-width: 1px 0px 1px !important;
}
.playlist, li {
  border-color: #333 !important;
}
.video-controls {
  border-right-color: #333 !important;
}
.room-container .room-component-row .chat {
  border-bottom: none;
}
.room-container .room-component-row .column {
  border-top: 1px solid #333;
}
.room-container .room-info-row .overview {
  border-bottom: 1px solid #333;
}
a:hover {
  color: rgba(255,255,255, 0.8) !important;
}
.room-container .room-component-row .chat .chat-tabs li.tabs-users:hover, .room-container .room-component-row .chat .chat-tabs li.tabs-users.visible {
  background-color: rgba(255,255,255,0.1)
}
.registered, a:hover, .navbar-brand:hover, li.active, .fa:hover {
  color: #666 !important;
}
.unregistered, .navbar-default, .navbar-default .navbar-brand:focus, a, a:focus, a:hover {
  color: #333 !important;
}
li.mod, .username.mod-message {
  color: #933 !important;
}
.btn-default, .poll-votes {
  background-image: linear-gradient(to bottom, #111 0px, #000 100%) !important;
  background-color: #000 !important;
  color: #333 !important;
  text-shadow: none !important;
  border-color: #333 !important;
  box-shadow: none !important;
}
.poll-votes:hover {
  background-image: none !important;
}
.user-list {
  background-color: rgba(0,0,0, 0.5) !important;
}
.badge {
  color: #ccc !important;
}
.dropdown-toggle, .avatar {
  background-color: transparent !important;
}
.user-list li:hover {
  background-color: transparent !important;
}
.navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
  color: #666 !important;
  background-color: transparent !important;
}
/* size hacks for cakeu! */
.room-container .room-component-row {
  width: 90% !important;
  position: absolute !important;
  left: 5% !important;
}  
.footer {
 visibility: hidden !important;
}
.page-container .home {
    border-bottom: 4px solid #111;
}
}
