css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("m.tv.sohu.com") {
  .head{position:static  !important;}
  .main_container,.body_wrap{position:relative !important;width:auto!important;margin-left:100px !important;margin-right: 100px !important;}
  
  #sohu_player{height:600px!important;}
  
        #svp_main_1{background-image:none!important;background-color:black !important;}
  
        .player_main.svp_player_main{position:relative!important; height:100% !important; width:100% !important;}
  
                 .player_main.svp_player_main > .video{position:absolute !important;
                                                       display:block !important;
                                                       height:auto !important;
                                                       top:0px !important;   bottom:0px!important;
                                                       z-index: 1 !important;}
                  /*标题·海报*/
                  .video_title.svp_title,.poster.svp_poster.hidden{display:none !important;}
                   /*控制栏*/
                 .player_controls.svp_ctrl{position:absolute !important; 
                                           cursor:default !important; 
                                           display:block !important;                  
                                           background-color:rgba(255,255,255,0) !important; }
  
                               .mid.svp_mid,.ad-img.svp_ad_pause{display:none!important;}
                               
                    /*进度条*/  .slider_bar.svp_ctrl_bar{display:block !important;cursor:pointer !important;position:absolute !important;opacity:0 !important;z-index: 1 !important;max-height: 30px!important;max-width: 110px !important;right:0px!important;left:unset!important;bottom:29px!important;border-radius: 2px!important;}   
  
                                  .slider_bar.svp_ctrl_bar:hover{opacity:0.8 !important;}       
                                  /*.left_btn_play,*/
                                  .action_trackBar.svp_ctrl_track_bar,.time.svp_ctrl_time{display:none!important;}
                                        
                    /*视频质量*/.quality_button.quality_container.svp_mid_mode{display:block !important;
                                                                              cursor:pointer !important; 
                                                                              width:55px!important;margin-right: -10px !important;
                                                                              opacity:0 !important;}
                              .quality_button.quality_container.svp_mid_mode:hover{opacity:1 !important;}
  
  .user_favor,.js_sohu_app_banner,.pgc_recommend.js_star_recommend,.recommend,.load_more,#player_recommend,.fixed_pinner.hots_pinner.show,.serach_list.js_search_list.show,#js_nav_maskLayer,.js_sh_app_dl_index{display: none!important;}
  
  
  #page_box{overflow-x:scroll !important;min-height: 50px!important;}
  #series > .title{margin-left:10px !important; }
  #page_list{width:auto!important;left:0px!important;}
  #page_list > li:hover{color:black!important; cursor:pointer !important;}
  #series_list{overflow-x:scroll !important;}
}
