css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("popgo.org") {
  #logobar,.inde_tab_hot{display: none !important;}
  .topbar{
    position: relative !important;
    padding: 0px 0px 0px 30px !important;
    top: -2px;
    height: 30px;
  } /*首页*/
  .links{margin-right: 10px;} /*登录等链接*/
  .flicker{margin-right: 20px;} /*微博*/
  .links > a{margin-right:15px;} /*登录等间隔*/
  #index_friendsgroup, #inde_playlist{
    position: relative !important;
    top:73px;
    font-family: Georgia, serif;
    font-size: 16px !important;  
  } /*更新列表框*/  
  #bopen{
    margin-left: 15px;
  }  /*番组列表[+]　　联盟列表[+]*/
  .bangumi_list a:link, .bangumi_list a:visited{
    font-family:  Georgia, serif;
    font-size: 14px !important;    
    margin-left: 15px;
  }  /*更新列表*/
  #index_hotsearch{display: none !important;}
  /* 热门搜索
  #index_hotsearch{
    font-family:  Georgia, serif;    
  } 
  #index_hotsearchlist li{
    font-family:  Georgia, serif;
    padding-left: 15px;  font-weight: bold;
    margin-right: -15px;
  } 
  */
  #index_anoc{
    position: absolute !important;
    top:-22px;
    left: 120px;
    width:30% !important;
    height: 10% !important;
    text-align: left !important;    
  } /*欢迎申请加入发布组*/
  #index_anoc > A:last-child{
    position: absolute !important;
    top:20px;
    margin-left: 150px;
  } /*POPGO新论坛*/
  #inde_sorttable{  z-index: 0 !important;
    font-family: Georgia, serif; 
    position: absolute !important;
    top: 30px;
    left:13px;
    width:97.95% !important;
  }  /*分类索引*/
  #inde_sorttable > TBODY > TR:first-child > TH{padding-left:20px;} /*分类索引*/
  #index_searchsite{
    position: absolute !important; z-index: 1 !important;  
    top: 17px;
    right:10px;
    width:50% !important;
  } /*搜索框*/
  #index_search{position:relative !important; width: 500px!important;margin-left: 40px;} /*搜索框*/
  #index_searchsite select{position:relative !important; margin-left: -10px;} /*搜索框内容*/
  #index_maintable{
    top:32px;
    font-family: Georgia, serif;
    font-size: 16px !important;
    font-style: normal;
    text-indent: 1em;
    white-space: normal;
    font-weight: normal;
    background-color: rgba(204,255,207,0.5) !important;
    position: relative !important; width: 85% !important; left: 85px;
  } /*内容框*/
  #index_maintable th{
    text-align: center !important;
    padding-left: 0px;
    padding-right: 15px;
    font-family: 宋体;
  } /*项目*/
  #index_maintable td{
    text-align: center !important;
    padding-right: 5px !important;
    padding-left: 10px !important;  
  } /*项目内容*/
  /*#inde_tab_time{width: 90px;} 发布时间*/
  #inde_tab_size{width: 90px;} /*文件大小*/
  /*#inde_tab_seeds{width: 50px;}种子*/
  #inde_tab_down{width: 70px;} /*下载者*/
  /*#inde_tab_com{width: 50px;} 完成*/
  #inde_tab_download{width: 50px;}  /*下载*/
  #index_maintable{text-decoration: none !important;}
  
  a:link, a:visited, a:active{
    text-decoration: none !important;
    line-height: 200%!important;
    margin-left: -15px;
  } /*链接*/
 #page{position: relative !important;top: -13px; right: 105px; font-size: 18px;} /*页数*/  
  #page > SPAN{margin-right: 5px;}
  #page > SPAN > SPAN{margin-right: 15px;}
  #page > SPAN > A{margin-right: 15px;}  
}

@-moz-document url-prefix("http://share.popgo.org/program"){
  #index_friendsgroup, #inde_playlist{
    top: 0px;
  }
  #profilepass{
    width: 80% !important;
    font-family: Georgia, serif;
    font-size: 18px;
  }
}
