css:
@-moz-document domain("loliquent.wordpress.com"), domain("kirikotranslations.wordpress.com"), domain("yoraikun.wordpress.com"), domain("typemoondb.wordpress.com"), domain("blazingtranslations.com"), domain("pummels.wordpress.com"), domain("insigniapierce.wordpress.com"), regexp(".*xcrossj.blogspot.*") {
* {
	background: hsl(50, 50%, 95%) !important;
    color: hsl(50, 100%, 15%) !important;
    margin: 0 !important;
    padding: 0 !important;
    border: none !important;
    min-width: 0 !important;
    font-family: sans-serif !important;
    font-size: 16pt  !important;
    line-height: 1.5em !important;
    max-width: 1000px !important;
    margin: auto !important;
}

p {
	margin: 1.5em 0 !important;
}

img {
	display: none !important;
}

a:visited {
	text-decoration: line-through !important;
    color: hsl(50, 15%, 75%) !important;

}

body {
	padding: 33% 2em !important;
}

body::before {
	content: '';
    position: fixed;
    z-index: 100000000;
    left: 0;
    right: 0;
    top: 0;
    height: 20%;
    background: linear-gradient(180deg, hsla(50, 50%, 95%, 1), transparent 100%);
}

body::after {
	content: '';
    position: fixed;
    z-index: 100000000;
    left: 0;
    right: 0;
    bottom: 0;
    height: 20%;
    background: linear-gradient(0deg, hsla(50, 50%, 95%, 1), transparent 100%);
}

#content, .post-entry {
	width: inherit;
}

::-webkit-scrollbar { width: 0 !important }
}

@-moz-document domain("kirikotranslations.wordpress.com"), domain("loliquent.wordpress.com"), domain("yoraikun.wordpress.com"), domain("typemoondb.wordpress.com"), domain("blazingtranslations.com"), domain("pummels.wordpress.com"), domain("insigniapierce.wordpress.com") {
#primary, #secondary, #header, .navigation, #comments, #entry-author-info, #jp-post-flair, .entry-utility, #footer, #bit,#secondary, #comments, #jp-post-flair, .entry-footer, .navigation, #colophon, #bit, #masthead, .custom-header, .entry-meta, #nav-below, #branding, .post-date, .post-info, .post-extras, #menu-toggle-nav, .entry-format
{display:none!important}

}

@-moz-document domain("loliquent.wordpress.com"), domain("blazingtranslations.com"), domain("insigniapierce.wordpress.com") {
#primary {display: block !important}
}

@-moz-document regexp(".*xcrossj.blogspot.*") {
.fauxcolumn-outer, header, .navbar, .tabs-outer, .column-left-outer, .column-right-outer, .post-footer, .comments, .blog-pager, footer, .post-feeds, .blog-feeds, .date-header
{display:none}
}
