css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("kintsugi.seebs.net") {
  
#logo img {
    display: none;
  }
  #logo {
    background-image: url('http://i.imgur.com/VeZ3ksM.png');
    background-repeat: no-repeat;
    width: 100%;
    height: 93px;
    margin-top: 5px;
    margin-bottom: 5px;

  }

  html {
    background-image: url('http://i.imgur.com/3rvko1N.png');
  }
  
  #header, #headerProxy {
    background-color: #322c39 !important;
  }
  
  .navTabs .navTab.selected .tabLinks, .navTabs {
    background: #322c39 !important;
  }
  
  .navTabs {
    border: none;
  }
  
  .navTabs .navTab.PopupClosed .navLink
	{
		color: rgb(222, 183, 101);
	}
	
		.navTabs .navTab.PopupClosed:hover
		{
			background-color: #322c39;
		}
		
			.navTabs .navTab.PopupClosed .navLink:hover
			{
				color: rgb(202, 244, 248);
			}

  .navTabs .navTab.selected .tabLinks a:link {
    color: #f8bf26;
  }
  
  .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    background-color: #322c39;
    color: #fff;
    text-shadow: none;
    border: none !important;

	}
  
  .quote, .quoteExpanded, .quoteContainer, .attribution {
    background-color: #d7bb61 !important;
    background-image: none !important;
  }
  
  .quoteExpanded, .quoteContainer, .attribution {
    box-shadow:inset 4px 4px 17px -3px #c3a33e,
    inset -4px -4px 17px -3px #c3a33e;
  }
  
  
  .attribution {
    border-bottom: 1px solid #ad9033 !important;
  }
  
  .bbCodeQuote {
    border: 1px solid #ad9033 !important;
    box-shadow: 3px 3px 0px #857053;
  }
  
  
  .navTabs .navTab.selected .navLink, .navTabs .navTab.selected .tabLinks a:hover {
    position: relative;
    font-weight: bold;
    background: none !important;
    border-style: solid solid none;
    border: 0 !important;
    color: #ffeb99;
    text-shadow: -1px -1px 0 #19161d,  
    1px -1px 0 #19161d,
    -1px 1px 0 #19161d,
    1px 1px 0 #19161d,
    0px 0px 6px #ffeb99;
}
  
  .navTabs .navTab.PopupClosed .navLink {
    color: #ffeb99;
    text-shadow: -1px -1px 0 #19161d,  
    1px -1px 0 #19161d,
    -1px 1px 0 #19161d,
    1px 1px 0 #19161d;
}
  
  .navTabs .navTab.PopupClosed .navLink:hover {
    color: #e5960b;
}
  
  .textCtrl {
    font-size: 13px;
    font-family: Calibri,"Trebuchet MS",Verdana,Geneva,Arial,Helvetica,sans-serif;
    color: #000;
    background: #fff url("http://i.imgur.com/RljyPQT.png") repeat scroll 0% 0%;
    padding: 3px;
    margin-bottom: 2px;
    border-width: 1px;
    border-style: solid;
    border-color: #C0C0C0 #E9E9E9 #E9E9E9 #C0C0C0;
    border-radius: 4px;
    outline: 0px none;
  }
  
  .navTabs .navTab.selected .tabLinks a:hover, .navTabs .navTab.selected .tabLinks a:focus {
    text-decoration: none;
    background-color: transparent;
    padding: 0px 9px;
    border: 1px solid #CBA555;
    border-radius: 5px;
    color: #ffeb99;
    text-shadow: -1px -1px 0 #19161d,  
    1px -1px 0 #19161d,
    -1px 1px 0 #19161d,
    1px 1px 0 #19161d;
}
.navTabs .navTab.selected .tabLinks a {
    font-size: 11px;
    padding: 1px 10px;
    display: block;
    color: #ffeb99;
    text-shadow: -1px -1px 0 #19161d,  
    1px -1px 0 #19161d,
    -1px 1px 0 #19161d,
    1px 1px 0 #19161d;
    line-height: 19px;
}
  
.blockLinksList a:hover, .blockLinksList a:focus, .blockLinksList li.kbSelect a, .blockLinksList label:hover, .blockLinksList label:focus, .blockLinksList li.kbSelect label {
    text-decoration: none;
    background-color: #FBE1A9;
}
.blockLinksList a, .blockLinksList label {
    color: #755B25;
    padding: 5px 10px;
    border-radius: 5px;
    display: block;
    outline: 0px none;
}
.secondaryContent a {
    color: #755B25;
}
a:link, a:visited {
    color: #755B25;
    text-decoration: none;
}
  
  .nodeList .categoryStrip, .discussionList .sectionHeaders {
    color: #ffeb99 !important;
    background: #8f3c3d ;
    border-top: 1px solid #c15758;
    border-bottom: 1px solid #6e2c2c;
    -webkit-box-shadow: inset 9px 9px 39px -13px rgba(83,45,57,0.5), inset -9px -9px 39px -13px rgba(83,45,57,0.5);
    -moz-box-shadow: inset 9px 9px 39px -13px rgba(83,45,57,0.5), inset -9px -9px 39px -13px rgba(83,45,57,0.5);
    box-shadow: inset 9px 9px 39px -13px rgba(83,45,57,0.5), inset -9px -9px 39px -13px rgba(83,45,57,0.5);
}
  
    .nodeList .categoryStrip a:link,  .nodeList .categoryStrip a:visited{
    color: #ffeb99 !important;
}
  
  .nodeList .categoryStrip .nodeDescription {
    color: #ffeb99 !important;
}
    
  .PageNav a{
    color: #ffeb99 !important;
    text-decoration: none;
    background-color: #8f3c3d;
    border-top: 1px solid #c15758;
    border-left: 1px solid #c15758;
    border-bottom: 1px solid #6e2c2c;
    border-right: 1px solid #6e2c2c;
    border-radius: 3px;
    text-align: center;
    width: 19px;
}

  body .itemPageNav a {
    color: #ffeb99 !important;
    background-color: #8f3c3d;
    border-top: 1px solid #c15758;
    border-left: 1px solid #c15758;
    border-bottom: 1px solid #6e2c2c;
    border-right: 1px solid #6e2c2c;
    border-radius: 3px;
    text-align: center;
}
  
    body .itemPageNav a:hover {
    color: #8f3c3d !important;
    text-decoration: none;
    background-color: #ffeb99;
    border-top: 1px solid #fff6d0;
    border-left: 1px solid #fff6d0;
    border-bottom: 1px solid #d0be73;
    border-right: 1px solid #d0be73;
    border-radius: 3px;
    text-align: center;
}
  
  
.PageNav .pageNavHeader, .PageNav a, .PageNav .scrollable {
    display: block;
    float: left;
    margin-right: 3px;
}
a:link, a:visited {
    color: #755B25;
    text-decoration: none;
}

  .pairs dt, .pairsInline dt, .pairsRows dt, .pairsColumns dt, .pairsJustified dt, .subForumsPopup .dt {
    color: #000;
    font-weight: bold;
    padding-left: 5px;
  }
  
  .node .forumNodeInfo.unread .nodeIcon, .node .categoryForumNodeInfo.unread .nodeIcon {
    background-image: url("http://i.imgur.com/2kL36E2.png") !important;
    background-position: 0px 0px;
    background-repeat: no-repeat;
}
  
.node .forumNodeInfo .nodeIcon, .node .categoryForumNodeInfo .nodeIcon {
    background-image: url("http://i.imgur.com/FVbbiif.png");
    background-repeat: no-repeat;
    background-position: 0px 0px;
}
  
  .node .nodeIcon {
    margin: 10px 0px 10px 10px;
    float: left;
    width: 36px;
    height: 36px;
}

  .PageNav a.currentPage, .PageNav a:hover {
    color: #8f3c3d !important;
    text-decoration: none;
    background-color: #ffeb99;
    border-top: 1px solid #fff6d0;
    border-left: 1px solid #fff6d0;
    border-bottom: 1px solid #d0be73;
    border-right: 1px solid #d0be73;
    border-radius: 3px;
    text-align: center;
    width: 19px;
}
  
  .footerLegal, .footerLegal a:link, .footerLegal a:visited, .footerLegal span {
    color: #000 !important;
  }
  
  .footerLegal span {
    font-weight: bold;
  }
  
  .node .tinyIcon{
    background: transparent url("http://i.imgur.com/UMnPDM9.png") no-repeat;
    margin: 1px 4px;
    display: block;
    white-space: nowrap;
    text-indent: 9999px;
    overflow: hidden;
    opacity: 0.25;
    width: 16px;
    height: 16px;
}
 
  .footerLinks a.globalFeed {
    width: 16px;
    height: 16px;
    display: block;
    text-indent: -9999px;
    white-space: nowrap;
    background: transparent url("http://i.imgur.com/UMnPDM9.png") no-repeat ;
    padding: 0px;
    margin: 5px;
}
  
  .footer a, .footer a:visited {
    color: #DEB765 !important;
    padding: 5px;
    display: block;
}
 
  dl span:hover {
    background-color: #8f3c3d;
  }
  dl span {
    color:  #ffeb99;
  }
  
  body .faint, body a.faint, body .faint a {
    color: #000;
}
body .muted, body a.muted, body .muted a {
    color: #000;
}
  
  body .muted, body a.muted, body .muted a {
    color: #000 !important;
    font-style: italic;
}
  
  .subHeading {
    font-size: 11px;
    padding: 5px 10px;
    margin: 3px auto 0px;
    
    color: #ffeb99 !important;
    text-decoration: none;
    background-color: #8f3c3d;
    border-top: 1px solid #c15758;
    border-left: 1px solid #c15758;
    border-bottom: 1px solid #6e2c2c;
    border-right: 1px solid #6e2c2c;
    border-radius: 3px;
    text-align: center;
}
 
  .ugc a:hover {
    color: #755B25;
    font-style: italic;
    background-color: transparent;
    box-shadow: unset;
  }
  
  a.callToAction span {
    color: #000;
  }
  
  .visitorText{
    height: 100px !important;
  }
  
  #searchBar {
    top: -15px !important;
  }
  
  .formPopup, #QuickSearch {
    background-color: unset;
}
}
