@namespace url(http://www.w3.org/1999/xhtml);



@-moz-document domain("basilmarket.com") {



/* Cursor. */

/* body {cursor: url(http://www.basilmarket.com/img/hand_shadow.cur), auto !important;}

A {cursor: url(http://www.basilmarket.com/img/hand_anim_shadow.ani), url(http://www.basilmarket.com/img/hand1.cur), auto !important;} */





/* Change top banner */

.innerWrapday {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/innerWrapDayorNightwithblackoutline.png) !important;}

.innerWrapnight	{background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/innerWrapDayorNightwithblackoutline.png) !important;}

.innerWrapPP1 {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/InnerWrapPP1.png) !important}

.innerWrapPP2 {width: 800px !important;}

.innerWrapPP2 {background-position: 220px -6px !important;}



/* Top smoothing */

.bwnight {background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Halloween%20Theme/body-wrap.png") top no-repeat #444444 !important;}

.bwday	{background: url("http://i370.photobucket.com/albums/oo142/Dumbfugi/BasilMarket%20-%20Halloween%20Theme/body-wrap.png") top no-repeat #444444 !important;}



/* Top gradient */

.night {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/TopGradient.png) top repeat-x #191919 !important;}

.day {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/TopGradient.png) top repeat-x #191919 !important;}



/* Improved title bars */

.right {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/Headerright.png) !important;}

.left {background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/Headerleft.png) !important;}

.topicBar, .bbTop {color:#fff !important;padding:10px !important;background:url(http://i359.photobucket.com/albums/oo32/x2bishyz/Topic-bar-header.png) top no-repeat #282828 !important;}

.topicBar a, .bbTop a 	{color:#fff !important;}



/* Changed sticky colour */

.stickyrow {background-color:#ff6e00 !important;}

.stickytopic {font-weight:normal !important;}

.stickytopic a, .stickytopic a:visited, .stickytopic a:active, .stickytopic a:link {color:#000000 !important;}

.stickytopic a:hover {font-weight:bold !important;}



/* Remove side module on forums

.sideModule {display:none !important} 

.flHeader{display:none !important}

.flForum{display:none !important}

.middleColumn>div.np {width:790px !important}

.sideModule.np {display:none !important} */





/* Destroy search background*/

.misc1.mb10 {display:none !important}



/*Better post background*/

.postSubn, .rf.small.normal, div.lh {background:url("http://i359.photobucket.com/albums/oo32/x2bishyz/post.png") top repeat-x #963200 !important;}



/*Transparent menus */

.sub_menu{opacity: 0.8 !important} 

.sub_menu a:hover {font-weight:bold !important; color:#000000 !important; background:#ffba6f!important}



/* Top notices */

.topMsg {background-color:#000000 !important}

.topMsg {font-weight:normal !important; color:#ffffff !important}

.topMsg {color:#ffffff !important;}

.topMsg {border:1px solid #808080!important;}



/* Blue "I like this notices" */

.likeit {background:#ff9728 !important}

.likeit:hover {color:#ff9728 !important; background:#ffcd98!important}



/* Friends list on mail*/

.error3 {background-color: #ffb668 !important }



/* Forum areas on main page */

.normal.center.misc1{background: #969696 !important}



/*Forum Background Color*/

.mainPage {background:#222222 !important }





}
