css:
@-moz-document url-prefix("https://play.google.com/music/listen") {
.settings-card {
    width: 88%;
    max-width: 88%;
	min-width : 88%;
}


#material-breadcrumbs, #material-breadcrumbs-inner, .tab-text{
	max-height : 60px;
}

 .search-icon{
	max-height : 30px;

}

#app-bar-text-protection{
	max-height : 35px	
}

#queue-overlay {
	min-width : 97%;
	min-height : 86.5%;
	top : 1.5%;
	right : 3%;
	left : 0%;
}

#mini-queue-details{
	background-color : darkorange;
}

#player {
	top : 93%;
}
.player-progress-wrapper, #material-player-progress{
	max-height : 15px;	
}

 #material-player-left-wrapper,.material-player-middle, #material-player-right-wrapper {
	max-height : 70%
}

#nav-content-container{
	min-height : 93%
}




}
