css:
@namespace url("http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul");
@-moz-document url("chrome://browser/content/browser.xul") {
/*标签定义开始*/
.tab-background-end::after, .tab-background-start::after {
	content: none !important;
}
.tabbrowser-tab:hover > .tab-stack > .tab-background:not([selected=true]) {
    opacity: 0.3 !important;
}
.tabs-newtab-button{
	background: none !important;
}
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before{display: none !important;}

.tab-background-middle[selected],
*|*#tab-background-fill {
	background-color: #FAFAFA !important;
	background-image: none, none, none !important;
}
   
/*标签栏背景色*/
#toolbar-menubar:not(:-moz-lwtheme),
#TabsToolbar:not(:-moz-lwtheme){
    padding: 0 !important;
}

/*通用标签样式*/
.scrollbutton-up, .scrollbutton-down, .tabbrowser-tab, .tabs-newtab-button, #TabsToolbar[currentset*="tabbrowser-tabs,new-tab-button"] #new-tab-button, #alltabs-button, #tabs-closebutton{
    padding: 0 !important;
    box-shadow: none !important;
    background: none !important;
    border: none !important;
    border-radius: 0 !important;
}

.tabbrowser-tab[pinned] .tab-stack,/*固定标签宽度*/
.tabs-newtab-button,/*新建按钮宽度*/
#TabsToolbar .toolbarbutton-1{
    width: 34px !important;
    padding: 0 !important;
    margin: 0 !important;
}
/*隐藏标签页上方的1px间距*/
#tabbrowser-tabs {margin-top:-1px!important;}   

/*载入动画*/
.tab-throbber[busy] {
  list-style-image: url("data:image/png;base64,");
}
.tab-throbber[progress] {
  list-style-image: url("data:image/png;base64,");
}
.tab-throbber:not([busy]),
.tab-throbber[busy] + .tab-icon-image:not([src]):not([pinned]){
    display:none !important;
}
    
/*标签默认图标*/
.tab-icon-image:not([src]):not([pinned]){
    display:block !important;
    list-style-image: url("data:image/png;base64,") !important;
    -moz-image-region: rect(0 16px 16px 0) !important;
}
.tabbrowser-tab:not([src]):not([pinned])[selected] .tab-icon-image{
    -moz-image-region: rect(0 32px 16px 16px) !important;
}
/*关闭按钮*/
.tabbrowser-tab:not([selected]) .tab-close-button{display:none!important;}/*非当前页关闭按钮自动隐藏*/
.tabbrowser-tab:not([pinned]):hover .tab-close-button{display:inherit!important;}
.tab-close-button{
    list-style-image: url("data:image/png;base64,") !important;
    -moz-image-region: rect(0 16px 16px 0) !important;
}
.tab-close-button[selected]{
    -moz-image-region: rect(0 32px 16px 16px) !important;
}
.tab-close-button:hover{
    -moz-image-region: rect(0 48px 16px 32px) !important;
}

/*显示全部标签按钮*/
#TabsToolbar #alltabs-button { visibility: visible !important; width: 26px !important; border-right: none !important;}
#alltabs-button:after { content: counter(tabs); text-align: center; display: block;}/*插入标签计数*/
.tabbrowser-tab:not(:-moz-any([pinned],[concealed])){ counter-increment: tabs;}
#main-window { counter-reset: tabs;} 
#alltabs-button > :-moz-any(label, image) { display: none;}/*隐藏下拉三角*/

/*更改此站点的弹出阻止设置*/
#page-report-button {display:none !important;}
    
/*隐藏书签红心图标旁边的竖线*/
#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker {visibility:hidden;}
#bookmarks-menu-button .toolbarbutton-menubutton-dropmarker .dropmarker-icon{visibility:visible;}

/*下拉窗口为直角*/
.panel-arrowcontainer{
    padding: 0 !important;
    margin: 4px 0 0 !important;
}
#nav-bar toolbarbutton menupopup{
    padding: 4px !important;
    /*margin: 0 !important;*/
    border: none !important;
}
.panel-arrowcontent{
    border: 1px solid rgb(215, 215, 215) !important;
    border-radius: 0 !important;
}
.panel-arrow{
    display: none !important;
}

/*隐藏内容框顶部1px边框*/
#navigator-toolbox:after{
    display: none !important;
}
#navigator-toolbox{
    border-bottom: 1px solid rgb(215, 215, 215) !important;
}
/*隐藏内容框两侧及底部1px边框*/
#browser-border-start,#browser-border-end,#browser-bottombox{
    display: none !important;
}
/*隐藏工具栏下拉三角按钮*/
toolbarbutton:not(:-moz-any(#bookmarks-menu-button)) > dropmarker{
    display: none !important;
}
/*隐藏状态类分隔符*/
statusbarpanel { display: none !important;}

/*导航栏*/
#nav-bar{
    margin: 0 !important;
    min-height: 34px !important;
    background-color: white !important;
    box-shadow: none !important;
    border: none !important;
    border-radius: 0 !important;
}
#urlbar-wrapper{
    padding: 0 !important;
    margin: 0 !important;
    clip-path: none !important;
}
/*地址栏、搜索栏*/
#nav-bar textbox,
#PanelUI-contents .searchbar-textbox{
    height: 26px !important;
    padding: 0 !important;
    margin: 4px 7px !important;
    background-color: rgb(252, 252, 252) !important;
    -moz-appearance: none !important;
    border: 1px solid rgb(235,235,235) !important;
    /*border-radius: 13px !important;/*圆角*/
    font-size: 13px !important;/*修改地址栏、搜索栏字体大小*/
    overflow-x: hidden !important;
}
#nav-bar textbox[focused],
#PanelUI-contents .searchbar-textbox[focused]{
    background-color: rgb(255, 255, 255) !important;
    border-color: rgb(91, 177, 236) !important;
}
/*搜索下拉面板*/
#PopupSearchAutoComplete{
	border:none !important;
}
/*全局定义按钮、按钮图标*/
#nav-bar .toolbarbutton-1,/*导航栏地址栏按钮*/
#nav-bar .toolbarbutton-1 toolbarbutton,
#nav-bar .toolbarbutton-1 .toolbarbutton-icon,/*导航栏地址栏按钮图标*/
#nav-bar #bookmarks-menu-button > dropmarker > image/*书签*/{
    padding: 0 !important;
    margin: 0 !important;
    -moz-appearance: none !important;
    background: none !important;
    border: none !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    outline: none !important;
}

/*导航栏按钮间距*/
#nav-bar .toolbarbutton-1:not([class="bookmark-item"]):not(:-moz-any(#bookmarks-menu-button,.box-inherit)){
    width: 24px !important;
    max-width: 24px !important;
    height: 24px !important;
}
/*导航栏按钮图标 normal*/
#nav-bar .toolbarbutton-1:not(:-moz-any(#bookmarks-menu-button,#PanelUI-menu-button)) .toolbarbutton-icon,
#nav-bar #bookmarks-menu-button > toolbarbutton > image,
#nav-bar #bookmarks-menu-button > dropmarker > image{
    padding: 3px 3px !important;
    border: 1px solid transparent !important;
    border-radius: 3px !important;
}
/*:hover*/
#nav-bar .toolbarbutton-1:not(:-moz-any(#bookmarks-menu-button,#PanelUI-menu-button)):not([disabled="true"]):hover .toolbarbutton-icon {
    background-color: #F2F2F2 !important;
    border-color: #F2F2F2 !important;
}
/*:active*/
#nav-bar .toolbarbutton-1:not(:-moz-any(#bookmarks-menu-button,#PanelUI-menu-button)):not([disabled="true"]):-moz-any(:hover:active,[open]) .toolbarbutton-icon {
    background-color: #E8E8E8 !important;
    border-color: #E8E8E8 !important;
}
    
/*当书签栏在导航栏时*/
#nav-bar #personal-bookmarks toolbarbutton{
    min-height: 26px !important;
    max-height: 26px !important;
    padding: 4px 5px !important;
    margin: 4px 2px !important;
    -moz-appearance: none !important;
    background: none !important;
    border: 1px solid transparent !important;
    border-radius: 3px !important;
    box-shadow: none !important;
    outline: none !important;
}
#nav-bar #personal-bookmarks toolbarbutton:hover{
    background-color: rgb(245, 245, 245) !important;
    border-color: rgb(245, 245, 245) !important;
}
#nav-bar #personal-bookmarks toolbarbutton:-moz-any(:hover:active,[open]){
    background-color: rgb(240, 240, 240) !important;
    border-color: rgb(240, 240, 240) !important;
}

/*地址栏按钮*/
#nav-bar #urlbar toolbarbutton:not([disabled="true"]){
    width: 24px !important;
    height: 24px !important;
}
/*地址栏按钮图标*/
#nav-bar #urlbar toolbarbutton >.toolbarbutton-icon{
    padding: 0 !important;
    border: none !important;
    border-radius: 0 !important;
}
/*:hover*/
#nav-bar #urlbar toolbarbutton:not([disabled="true"]):hover >.toolbarbutton-icon {
    background-color: transparent !important;
    border-color: transparent !important;
}
/*:active*/
#nav-bar #urlbar toolbarbutton:not([disabled="true"]):-moz-any(:hover:active,[open]) >.toolbarbutton-icon {
    background-color: transparent !important;
    border-color: transparent !important;
}

/*PanelUI按钮:hover:active*/
#nav-bar .toolbarbutton-1[id="PanelUI-menu-button"]:-moz-any(:hover, :hover:active, [open]){/*PanelUI按钮鼠标划过背景色*/
    background-color: rgb(91, 177, 236) !important;
}

/*去除前进后退按钮圆圈*/
#nav-bar #back-button > .toolbarbutton-icon,
#nav-bar #forward-button > .toolbarbutton-icon{
   -moz-appearance: none !important;
   border: 0px solid transparent !important;
   box-shadow: none !important;
}
/* 前进后退按钮显示 */
#forward-button[disabled="true"] {display: none !important}
#back-button[disabled="true"]:hover {background:none !important;}
/* 显示前进按钮 */
#forward-button { opacity: 1 !important; visibility: visible !important; -moz-box-align: center !important;}
    
#notification-popup-box,/*弹窗信息框*/
#identity-box/*身份识别信息框*/{ 
    padding: 0 2px !important; 
    margin: 0 !important; 
    background: none !important; 
    border: none !important; 
    border-right: 0px solid rgb(235, 235, 235) !important;
}
.textbox-input-box.urlbar-input-box{
    padding: 0 4px !important; 
}
#urlbar-search-splitter{
    width: 12px !important;
    display: none !important;/*如显示搜索栏则注销此行*/
}
/*书签按钮微调*/
#nav-bar #bookmarks-menu-button > toolbarbutton > image{
    border-radius: 3px 0 0 3px !important;
}
#nav-bar #bookmarks-menu-button > dropmarker > image{
    border-radius: 0 3px 3px 0 !important;
}
#nav-bar #bookmarks-menu-button > dropmarker{
    padding: 0 !important;
    margin-left: -1px !important;
}
#nav-bar #bookmarks-menu-button > dropmarker:before{
    border-left: 1px solid rgb(235, 235, 235) !important;
}
/*hover*/
#nav-bar #bookmarks-menu-button:not([disabled="true"]) > .toolbarbutton-menubutton-button[open] + .toolbarbutton-menubutton-dropmarker > .dropmarker-icon,
#nav-bar #bookmarks-menu-button:not([disabled="true"]):-moz-any(:hover,[open]) > toolbarbutton > image,
#nav-bar #bookmarks-menu-button:not([disabled="true"]):hover > dropmarker > image{
    background-color: transparent !important;
    border-color: rgb(235, 235, 235) !important;
}
#nav-bar #bookmarks-menu-button > toolbarbutton:not([disabled="true"]):not([open]):not(:active):hover > image,
#nav-bar #bookmarks-menu-button:not([buttonover]):not([open]):not(:active):hover > dropmarker:not([disabled]) > image{
    background-color: rgb(245, 245, 245) !important;
    border-color: rgb(245, 245, 245) !important;
}
/*active*/
#nav-bar #bookmarks-menu-button > toolbarbutton:not([disabled="true"]):-moz-any(:hover:active, [open]) > image,
#nav-bar #bookmarks-menu-button[open] > dropmarker:not([disabled="true"]) > image{
    background-color: rgb(240, 240, 240) !important;
    border-color: rgb(240, 240, 240) !important;
}
    
/*隐藏地址栏、搜索栏多余图标*/
.ac-type-icon,.autocomplete-history-dropmarker,.search-go-button,.search-go-button-stack,.search-go-button ,.searchbar-dropmarker-image{
     display: none !important
}
/*粘贴并转到、搜索置顶*/
.urlbar-textbox-container menuitem[anonid="paste-and-go"]:not([disabled="true"]) {-moz-box-ordinal-group: 0 !important;}
.searchbar-textbox menuitem[anonid="paste-and-search"]:not([disabled="true"]) {-moz-box-ordinal-group: 0 !important;}
#context-pasteandgo-forms:not([disabled="true"]) {-moz-box-ordinal-group: 0 !important;}
    
/*//////////////////////PanelUI菜单//////////////////////*/
/*去除PanelUI菜单左侧分隔符*/
#PanelUI-button{ background: none !important;}
/*PanelUI宽度、背景*/
#PanelUI-contents,
#PanelUI-contents-scroller {
    width: 272px !important;
    padding: 0 !important;
    margin: 0 !important;
    background-color: rgb(252, 252, 252) !important;
}
#PanelUI-contents-scroller{
    padding: 0 7px !important;
}
/*PanelUI按钮全局定义*/
#PanelUI-mainView toolbaritem,
#PanelUI-mainView .toolbarbutton-1 {
    padding: 0 !important;
    margin: 0 !important;
    -moz-appearance: none !important;
    background: none !important;
    border: 0 !important;
    border-radius: 0 !important;
    box-shadow: none !important;
    outline: none !important;
}
/*编辑控制、缩放控制定义*/
#PanelUI-mainView toolbaritem{
    margin-left: -7px !important;
    width: 272px !important;
    min-height: 29px !important;
    border-bottom: 1px solid rgb(245, 245, 245) !important;
}
#PanelUI-mainView toolbaritem separator{
    background-color: rgb(245, 245, 245) !important;
}
#PanelUI-mainView toolbaritem .panel-combined-button{
    padding-left: 15px !important;
    min-width: 90px !important;
    max-width: 90px !important;
    min-height: 28px !important;
    max-height: 28px !important;
}
#PanelUI-mainView toolbaritem .toolbarbutton-1{
    min-width: 28px !important;
    max-width: 28px !important;
    min-height: 28px !important;
    max-height: 28px !important;
}
#PanelUI-mainView toolbaritem toolbarbutton:not([disabled="true"]):hover{
    background-color: rgb(255, 255, 255) !important;
}
#PanelUI-mainView toolbaritem toolbarbutton:not([disabled="true"]):hover:active{
    background-color: rgb(245, 245, 245) !important;
}
/*PanelUI菜单按钮*/
#PanelUI-contents > .toolbarbutton-1{
    width: 82px !important; 
    height: 75px !important;
    margin: 2px !important;
    border: 1px solid transparent !important;
    border-radius: 5px !important;
}
#PanelUI-contents > .toolbarbutton-1:nth-child(-n+3){
    margin-top: 8px !important;
}
#PanelUI-contents > .toolbarbutton-1:nth-last-child(-n+3){
    margin-bottom: 8px !important;
}
#PanelUI-contents > .toolbarbutton-1 .toolbarbutton-icon,
#PanelUI-contents > toolbarpaletteitem .toolbarbutton-1 .toolbarbutton-icon,
#customization-palette .toolbarbutton-1 .toolbarbutton-icon{
    width: 28px !important;
    min-width: 28px !important;
    height: 28px !important;
    min-height: 28px !important;
}

#PanelUI-contents > .toolbarbutton-1 .toolbarbutton-icon~* {}
#PanelUI-contents > .toolbarbutton-1:hover{
    background-color: rgb(240, 240, 240) !important;
    border-color: rgb(240, 240, 240) !important;
}
#PanelUI-contents > .toolbarbutton-1:hover:active{
    background-color: rgb(240, 240, 240) !important;
    border-color: rgb(240, 240, 240) !important;
}

/*登录同步、定制、帮助、退出定义*/
#PanelUI-footer{
    background-color: rgb(245, 245, 245) !important;
}
#PanelUI-fxa-status,#PanelUI-footer-inner{
    border-top: 1px solid rgb(235, 235, 235) !important;
}
#PanelUI-footer-inner > toolbarseparator{
    border-left: 1px solid rgb(235, 235, 235) !important;
}
#PanelUI-multiView :-moz-any(#PanelUI-fxa-status,#PanelUI-customize,#PanelUI-help,#PanelUI-quit):not([disabled]):hover{
    background-color: rgb(235, 235, 235) !important;
    outline: none !important;
}
#PanelUI-multiView :-moz-any(#PanelUI-fxa-status,#PanelUI-customize,#PanelUI-help,#PanelUI-quit):not([disabled]):hover:active{
    background-color: rgb(225, 225, 225) !important;
    outline: none !important;
    box-shadow: none !important;
}
#PanelUI-multiView #PanelUI-quit:not([disabled="true"]):hover {
    background-color: rgb(216, 75, 75) !important;
    outline: none !important;
}
#PanelUI-multiView #PanelUI-quit:not([disabled="true"]):hover:active {
    background-color: rgb(173, 52, 52) !important;
    outline: none !important;
}

}
