css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bugzilla.redhat.com") {
  
/** HEADER **/   
   #header .links {
    background-color: #222;}
  
  #header .links a {
    color: #858585;}
  
  #header {
    border-radius: 5px!important;
    box-shadow: 0 0 2px 2px #9E292B;}

  

/** FOOTER **/
  #footer {
    background-color: #222;
    border-top: 1px solid #222;
    border-bottom: 1px solid #222;
    box-shadow: 0 0 2px 2px #9E292B;}
  
  #footer a {
    color: #858585;}
    

    
/**  COMMENTS  **/      
  .bz_comment {
    background-color: #f5f5f5;
    margin: 1px 1px 10px;
    border-radius:3px;
    border-width: 1px;
    border-style: solid;
    border-color: #C0C0C0;
    padding: 10px;
    color: #555;}
   
  .bz_comment_text {
    font-size: 1.6em;}



/** BODY **/
  #bugzilla-body {
    background: #EEE;
    color: #4A4A4A;
    border: 1px solid #111;}

  body {
    background: #444;}
  
  .tabs td {
    border-color: #CECECE;}
  
  .tabbed .tabbody {
    border-color: #D7D7D7;
    border-width: 1px;}
  
  #summary_field {
    border: 1px solid #A1A1A1;
    background-color: #C0C0C0;}
  
  a:link, a:link:hover {
    color: #9E292B;}

}
