css:
@-moz-document
domain("reddit.com") ,
    url-prefix("http://reddit.com/r/ukraina"),
    url-prefix("https://reddit.com/r/ukraina"),
    url-prefix("http://www.reddit.com/r/ukraina"),
    url-prefix("https://www.reddit.com/r/ukraina"),
    url-prefix("http://reddit.com/r/politota"),
    url-prefix("https://reddit.com/r/politota"),
    url-prefix("http://www.reddit.com/r/politota"),
    url-prefix("https://www.reddit.com/r/politota") {
.arrow.up,
.arrow.upmod,
.arrow.down,
.arrow.downmod {
    display: block;
    text-align: center;
    height: 17px;
    background-image: none !important;
    width: 17px;
    background-color: #F4F4F2;
    position: absolute;
    text-decoration: none;
    line-height: 17px;
    font-size: 14px;
    color: #D4D4D4;
    border: 1px solid #E0E0E0 !important;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
    font-weight: bold;
    font-family: Tahoma, sans-serif;
    -webkit-box-shadow: inset 0 3px 1px -2px #FFF;
    -moz-box-shadow: inset 0 3px 1px -2px #FFF;
}
.arrow.up:before {
    content: "+";
    color: #ccc;
}
.arrow.upmod:before {
    content: "+";
    color: #666;
}
.arrow.down:before {
    content: "-";
    color: #ccc;
}
.arrow.downmod:before {
    content: "-";
    color: #666;
}
.entry {
    position: relative;
    padding-bottom: 55px;
}
.author {
    font-weight: normal !important;
    font-family: verdana, sans-serif !important;
    letter-spacing: normal !important;
    color: #888 !important;
    margin-right: 15px !important;
    border-bottom: 1px dotted #999 !important;
    font-size: 11px !important;
}
.author:hover {
    text-decoration: none !important;
    border-bottom: 1px solid #999 !important;
}
.flair:before {
    display: none !important;
}
.flair {
    padding: 2px 7px 4px;
    border-radius: 5px;
    border-bottom: none;
    border-top: none;
    margin-right: 10px;
    color: #888;
}
.flat-list.buttons {
    position: absolute;
    bottom: 8px;
}
.entry .tagline {
    position: absolute;
    bottom: 30px;
    padding-bottom: 5px;
    border-bottom: 1px dashed #e2e2e2;
}
.titlebox .tagline {
    position: static !important
}
.score {
    font-weight: bold;
    padding-right: 5px;
    font-size: 15px;
}
.entry .buttons a[onclick*="reply"] {
    color: #999 !important;
    font-weight: normal !important;
}
.entry .buttons a[onclick*="reply"]:hover,
.entry .buttons li a:hover {
    color: #646464 !important;
}
blockquote {
    font-style: italic;
    color: #888;
}
.usertext-body a {
    color: #556E8C;
    text-decoration: underline;
}
.usertext-body a:visited {
    color: #666;
    text-decoration: underline;
}
.usertext-edit textarea {
    border: 1px solid #E9E9E9;
    border-top-color: #CCC;
    height: 200px;
    width: 100%;
    padding: 4px 5px;
    background-color: #E9DFC3;
    font-size: 15px;
    width: 100%;
    -webkit-box-sizing: border-box;
    -moz-box-sizing: border-box;
    box-sizing: border-box;
    margin: 0 0 2px;
    color: #888;
    -moz-border-radius: 2px;
    -webkit-border-radius: 2px;
    border-radius: 2px;
}
.usertext-edit {
    width: 75%;
    margin-left: 10px;
    margin-bottom: 15px
}
.usertext-edit md {
    width: 100%
}
.usertext-edit textarea:focus {
    outline: 1px solid #556E8C;
    outline-offset: -2px;
    color: #000;
    border: none
}
.panestack-title {
    padding-left: 15px;
    padding-bottom: 10px;
    padding-top: 15px;
    border: none
}
.panestack-title a {
    color: #666
}
.menuarea * {
    font-size: 13px
}
.commentarea {
    padding-top: 20px
}
.menuarea .dropdown.lightdrop .selected {
    color: #888
}
.menuarea .dropdown.lightdrop .selected:hover {
    color: #000
}
.drop-choices {
    padding: 10px
}
.drop-choices a {
    display: block;
    top: 373px;
    margin-bottom: 5px;
    color: #888 !important;
    padding: 2px 4px !important;
}
.drop-choices a:hover {
    background: #e8e8e8 !important
}
.usertext-buttons button[type="submit"]:before,
.usertext-buttons button.cancel:before {
    content: "YARRR!";
    display: block;
    width: 75px;
    position: absolute;
    background-color: #e5e5e5;
    border: 1px solid #CCC;
    top: -2px;
    padding: 3px 10px;
    left: 0;
    border-radius: 3px;
    cursor: pointer
}
.usertext-buttons button.cancel {
    position: relative
}
.usertext-buttons button.cancel:before {
    content: "NOPE";
    left: -2px;
}
.usertext-buttons button.cancel {
    margin-left: 60px
}
.usertext-buttons button[type="submit"] {
    border: none;
    box-shadow: none;
    position: relative
}
a.reddiquette,
.help-toggle a {
    color: #666 !important;
    text-decoration: underline
}
a.reddiquette:hover,
.help-toggle a:hover {
    color: #000 !important
}
.siteTable .arrow {
    position: static
}
.midcol .score {
    margin-top: 25px;
}
.listing-page .arrow.down {
    margin-top: 3px
}
.commentarea .arrow.down {
    margin-top: 30px
}
.entry .title {
    color: #556E8C !important;
    font-size: 21px;
    font-weight: normal;
    font-family: arial, sans-serif;
    line-height: 1.2
}
.entry .title:visited {
    color: #666 !important
}
.entry .title:hover,
.entry .title a:hover {
    text-decoration: none;
    color: #556E8C !important;
}
a.thumbnail {
    margin: 5px 15px 0 5px
}
.listing-page .first a,
.link .first a {
    color: #666 !important;
    text-decoration: underline
}
.listing-page .thing {
    margin-bottom: 0;
    padding-bottom: 30px;
    padding-top: 10px;
    border-bottom: none !important
}
.listing-page .title {
    width: 75%
}
#siteTable div.thing ul {
    visibility: visible !important;
}
a[href="#irony"] {
    color: #f00 !important;
    text-decoration: none !important;
    font-style: normal
}
.usertext-body.may-blank-within {
    margin-bottom: 20px;
}
}
