css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*
about:addons
chrome://mozapps/content/extensions/extensions.xul
*/
@-moz-document url("about:addons") {
    #addons-page,
    .global-info-container,
    .alert,
    richboxlist,
    #header-utils-btn,
    .detail-view-container,
    #list-view,
    #discover-view,
    .featured-addons,
    spacer,
    #header-search,
    .addon,
    .sorter,
    .view-header,
    .category[selected],
    #search-filter
    {
        background: #000020 !important;
    }
    label,
    #detail-name,
    #detail-desc,
    #detail-fulldesc,
    #header-search
    {
     	color: steelblue !important; 
    }
    .search-filter-radio[selected]
    {
        background: #000090
    }
}

@-moz-document url("about:config") {
    treechildren::-moz-tree-cell-text
    {
        color: steelblue !important;
    }
}

@-moz-document url("about:preferences") {
    treechildren::-moz-tree-cell-text
    {
        color: steelblue !important;
    }
}
