css:
@-moz-document url-prefix("https://tower.im/projects/"), url-prefix("https://hk.tower.im/projects/") {
.header .nav li a {
  text-shadow: none;
}

.simple-stack .page {
  width: 960px;
  min-height: 600px;
  border: 1px solid #dbdfd6;
  box-shadow: 0 0 5px rgba(0, 0, 0, 0.15);
}

.header {
  width: 960px;
  margin-top: 2px;
  margin-bottom: 5px;
  height: 30px;
  position: relative;
}

#link-feedback,  #nav-upgrade, #nav-ad-zhiren {
  display: none;
}

.header .nav li a {
  float: left;
  color: #9E9E9E;
}

.simple-stack .page {
  width: 745px;
}

.simple-stack .page.page-2 {
  border-radius: 7px;
  box-shadow: 0px 2px 7px rgba(210, 210, 210, 1);
}

body .wrapper {
  min-width: 980px;
}

.docs-view {
  width: 100%;
  margin: 30px 0 0 0;
  position: relative;
}

.docs-view.grid-view .doc-item:nth-child(3n) {
  margin-right: 40px;
}

div.comment.comment-form.new {
  width: 765px;
  position: fixed;
  margin-left: -50px;
  bottom: 10px;
  z-index: 2;
	background: rgba(194, 159, 159, 0.27);
	border: 1px solid #c8c6c6;
	border-radius: 5px;
}

div.comment.comment-form.new:hover {
  background: #fff;
	transition: 0.3s;
}

div.comment .avatar-wrap img.avatar {
  display: none;
}

div.comment .comment-main {
  margin-left: 10px;
  margin-right: 10px;
}

.btn.btn-x {
  background-color: rgba(89, 89, 89, 1);
  font-size: 12px;
  color: #E4DDDD;
  outline: none;
  width: 25%;
}

.btn.btn-x:hover {
  color: #000;
}

.btn.btn-primary {
  color: #fff;
  background-color: #076E57;    
}

.btn.btn-primary:hover {
  background-color: #055745;
}

.fake-textarea {
  cursor: pointer;
  border: none;
}

.header .command-bar {
  float: right;
  margin-right: 205px;
}

body.linux {
  background: #303030;
}

.form.form-editor .notify-title {
  display: none;
}

.simditor .simditor-toolbar>ul>li>.toolbar-item span {
  opacity: 1;
}

.simple-dialog .simple-dialog-buttons .btn {
  width: 45%;
}

.simple-stack .page {
  margin-left: 12px;
}

.zoom-meeting {
  display: none!important;
}

.tp-launchpad {
  margin-top: 35px;
  position: fixed;
  width: 75px;
  padding: 0;
  z-index: 1;
  left: 55%;
  background: rgba(216, 216, 216, 0.49);
}

.files-view {
  width: 680px;
}

.tp-team .tp-btn-refresh {
  display: none;
}

.messages .message .message-link {
  margin-left: -115px;
}

#page-project .messages .message .name {
  display: none;
}

#page-project .messages .message .avatar {
  display: none;
}

.simple-stack .page.page-1.page-behind {
  top: 8px;
}

.simple-stack .page.page-2 {
  top: 0;
  margin-top: 52px;
}

.simple-popover.popover-actions-menu .actions-menu li a {
  font-size: 14px;
}

.simple-popover.popover-actions-menu .actions-menu {
  padding: 7px 3px;
  min-width: 60px;
}

.detail-actions {
  top: 40px
}
.detail-actions .item {
  width: 40px;
}

.detail-star-action {
  width: 45px;
}

.kanban-todos {
  overflow-x: visible;
}

.kanban-todos .todolist, .kanban-todos .todolist-form {
  background-color: #F2F2F2;
  border-top: 4px solid #9E9F9C;
}

#page-todolist .progress .bar {
  height: 4px;
  background-color: #dad7d7;
}
#page-todolist .progress {
  width: 270px;
}

}
