css:
@-moz-document url-prefix("http://www.zhihu.com") {
.zu-main {
  min-height: 340px;
  padding: 25px 20px 50px;
    background: #fff;
}
body{
  background: #F2F2F2;
}
.zu-top {
  background: rgba(0, 0, 0, 0.6)!important;
  border-bottom: 1px solid #6F6F6F;
}  
.zu-top-add-question {
  background: #67696E!important;
  border: 1px solid #7C7C7C;
    box-shadow:none;
    border-radius:0;
}
.zu-top-nav-li.current{
	background-image: -webkit-linear-gradient(top,#4C4C4C,#515151);
  	background-color: #8A8B8C!important;        
      }

.zu-top-search-input {
    border: 1px solid #f2f2f2;
  box-shadow:none;}

.top-nav-profile a,.top-nav-profile:hover .zu-top-nav-userinfo {
  background-color: #808080!important;
    background-image:none!important;
}    

html.no-touch .top-nav-dropdown a:hover{
  background-color:#545353;
}

.top-nav-dropdown li a {
    border-top:0;
}    
}
