css:
@-moz-document domain("japtem.com") {
* {
	background: hsl(50, 50%, 95%) !important;
    color: hsl(50, 100%, 15%) !important;
    margin: 0 !important;
    padding: 0 !important;
    border: none !important;
    min-width: 0 !important;
    font-family: serif !important;
    font-size: 16pt  !important;
    line-height: 1.5em !important;
    max-width: 1000px !important;
    margin: auto !important;
}

p {
	margin: 1.5em 0 !important;
}

a:visited {
	text-decoration: line-through !important;
    color: hsl(50, 15%, 75%) !important;
}

body {
	padding: 40% 5vw !important;
}

body::before {
	content:	'';
    position: 	fixed;
    z-index: 	100000000;
    left: 		0;
    right: 		0;
    top: 		0;
    height: 	20%;
    background: linear-gradient(180deg, hsla(50, 50%, 95%, 1), transparent 100%);
    cursor: 	none;
}

body::after {
	content:	 '';
    position: 	fixed;
    z-index: 	100000000;
    left: 		0;
    right: 		0;
    bottom: 	0;
    height: 	20%;
    background: linear-gradient(0deg, hsla(50, 50%, 95%, 1), transparent 100%);
    cursor:		none;
}

#content, .post-entry {
	width: inherit;
}

::-webkit-scrollbar { width: 0 !important }
}

@-moz-document domain("japtem.com") {
.fusion-header-wrapper, .fusion-page-title-bar, #sidebar, iframe, .fusion-footer, .share-box, .fusion-meta-info, .flexslider, #disqus_thread
{display:none}
}
