css:
@-moz-document domain("www.douyutv.com") {
#broadcast_show a {
    border-style: none;
    display: none;
}

#header {
    border-color: #2a2b2e;
    border-style: outset;
    height: 0;
}

#js_head_game+[data-css="menu_up"] {
    display: none ;
}

#js_head_webgame a {
    display: none;
}

#room_gg div p {
    display: none;
}

#sendmsg {
    font-size: 25px;
    line-height: 50px;
}

.c_cont .c_text li p.cqback {
    background: rgba(219, 233, 238, 0);
}

.c_text .text_cont {
    color: #FFFFFF;
}

.chat_mem {
    background: url(http://i.imgur.com/LvzkxSL.jpg) 0 0 no-repeat ;
}

.chat_mem h3 a {
    color: #F00;
}

.colours_box {
    margin-left: 75px;
    margin-top: -52px;
}

.cs_textarea {
    background: #222;
    color: #FFF;
}

.g_mn5c {
    background: #444;
}

.get_yu .close1, .get_yu .close2, .get_yu .close3, .get_yu .close4, .get_yu .close5 {
    background: url(http://i.imgur.com/sChgXcp.gif) center -10px no-repeat;
    margin-top: 11px;
    padding-top: 5px;
}

.get_yu .close6 {
    background: url(http://i.imgur.com/0SvOKx4.gif) center -10px no-repeat;
    margin-top: 11px;
}

.get_yu .get1 {
    background: url(http://i.imgur.com/86t00A7.gif) center -10px no-repeat;
    margin-top: 11px;
    padding-top: 5px;
}

.get_yu .get2 {
    background: url(http://i.imgur.com/vUYtlwn.gif) center center no-repeat;
    margin-left: -6px;
    margin-top: 11px;
    padding: 5px;
}

.get_yu .get3 {
    background: url(http://i.imgur.com/VzjFv5E.gif) center center no-repeat;
    margin-top: 11px;
    padding-top: 11px;
}

.get_yu .get4 {
    background: url(http://i.imgur.com/5O5CWTk.gif) 0 0 no-repeat ;
    margin-top: 11px;
    padding-top: 13px;
}

.get_yu .get5 {
    background: url(http://i.imgur.com/aHVhcJR.gif) center center no-repeat;
    margin-top: 11px;
    padding-top: 13px;
}

.get_yu a .box_img {
    height: 35px;
}

.head .header_his .his {
    color: #FFF;
}

.head .header_nav li a {
    color: #FFF;
}

.head .logo  /*LOGO图标位置*/{
    background: url(http://i.imgur.com/Ur6MWZQ.gif) 0 0 no-repeat;
    height: 63px;
    margin: 5px;
}
.head .phoneapp .attent {
    color: #fff;
}

.room_mes {
    margin-bottom: -2px;
    margin-top: -18px;
}

.room_mes .r_else li {
    color: #FFF;
}

.viewport li.chartli .nick {
    color: #41C500;
}

a#left_close {
    background: #616161;
}

a#right_close {
    background: #616161;
}

a.btn {
    background-color: #333;
}

a.right_close {
    margin-right: 15px;
}

body {
    background: url(http://i.imgur.com/iKK8fVe.gif) 0 0 no-repeat ;
    color: #fff;
    margin-top: -7px;
}

div.b_btn {
    height: 127px;
}

div.b_mes_up.clearfix {
    font-weight: bold;
    margin-top: -67px;
}

div.chat_ann {
    display: none;
}

div.chat_cls {
    top: 85px;
}

div.cq_fans {
    display: none;
}

div.cq_lead_cont {
    display: none;
}

div.face_list {
    margin-left: 4px;
    margin-top: -52px;
}

div.get_fc.get_fc2 {
    display: none;
}

div.poptip.game_cas.js_head_show {
    background-color: #444;
    height: 245px;
}

div.poptip_nr h3 {
    color: #ffffff;
}

div.room_nav {
    height: 12px;
    margin-top: -2px;
    padding-left: 60px;
}

div.shie_gift label {
    color: #eb0e9e;
    margin-right: 120px;
}

div.stats_and_actions.mb15.js_live_height.clearfix {
    margin-top: -9px;
}

li.room_nav_tit {
    margin-top: -23px;
}

p.r_num {
    background: #444;
}

span.get_time {
    color: #fff;
}

span.js_tipsy.glyphicon.glyphicon_followers {
    color: #fff;
}

span.js_tipsy.glyphicon.glyphicon_kg {
    color: #fff;
}

span.sign_posid a img {
    display: none;
}

textarea.cs_textarea {
    background: url(http://i.imgur.com/4b0O2K9.jpg) 0 0 no-repeat ;
    height: 115px;
    margin-left: -5px;
}

.c_cont {
    background: #444;
}
div.r_focus .r_num {
    background: #444;
}
#cq_fans,/*移除酬勤榜，删掉“#cq_fans”还原*/
#broadcast_div/*移除喇叭“#broadcast_div”还原*/
{display:none!important }
body ::selection {
    color:#FFFFFF;
    background:#E84893;/*选中文字颜色*/
    text-shadow:none;
}
.room_mes .headline {
color: #fafafa;
letter-spacing: 0;
text-shadow: 0px 1px 0px #999, 0px 2px 0px #888, 0px 3px 0px #777, 0px 4px 0px #666, 0px 5px 0px #555, 0px 6px 0px #444, 0px 7px 0px #333, 0px 8px 7px #001135 }/*标题3D阴影*/
#sendmsg {
    font-size: 20px;
    line-height: 20px;
}/*发送键*/
div.adv_live.sign_posid a img,div.chat_top_pic.sign_posid a img,div.dy_top_gg {display:none}/*去广告*/
}
