@-moz-document domain("www.bilibili.com"), domain("member.bilibili.com"), domain("account.bilibili.com") {
.header .logo {
    background: url(http://i.imgur.com/6DH8PEM.png) no-repeat left center;
}/*logo图标*/
.header {
    background-image: url(http://i.imgur.com/INkhnrw.jpg)!important;}/*顶图片*/
/* background image */
body {
background: url("http://i.imgur.com/BTrKfVM.jpg") no-repeat fixed !important;
background-size: cover !important;
}
body ::selection {
    color:#FFFFFF;
    background:#E84893;/*选中文字颜色*/
    text-shadow:none;
}
html, body, .footer, .z_top, .header .num, #dyn_wnd .dyn_list, #dyn_wnd .dyn_list li .history-tag, .uns_box li .i_menu, .mini-wnd, .uns_box li.u-i.b-post .s-menu, .b-top.search, .a_layer, .table-normal thead tr td, .h_f_top, .bgm-calendar .b-tab ul li.on span{
	background-color: #111111;
    color: #C0C0C0;
    border-color: #111111;
}

input, .header .num .search .search-keyword {
	color: #fff;
}
a, .bangumi_list li .bangumi .bangumi_item .title:hover {
	color: #25A8EA;
}

.side-navi a:hover,  .header .num li .i_num li a:hover {
	color: #000000;
}

.bangumi_nav .nav_header .t1 p, .t2 p{
	color: white;
}

