	color: #C0C0C0;
}

.nomore {
	background-color: #222222 !important;
    text-shadow: #AAAAAA !important;
}

.b-line.history-t {
	background-color: #444444 !important;
}

.s_v_l li .comprehensive_intro, ul.tag li a, .main_list ul li:nth-of-type(2n) .q, .sb-info, .b-slt .list li:hover, .v_bgm_list .sbs-num{
	color: #A0A0A0;
}

body, #index_container .container-row .b-r, .top_bg, .security-top, .footer, .bangumi_list li .bg {
	background: none;
}

.num a.on {
	background: none;
    background-color: #333333;
    border-color: #333333;
}



.header .num .search {
	border: none;
    text-align: center;
}

.header .num .search button.search-submit {
	background-color: #111111;
    border: none;
}

.fcname {
    border-color: #444444;                                       
}

.bangumi-info {
	border-color: #222222;
}
.stm-ly .stm-lst li:hover .share {
	display: none;
}

.box-title {
    text-shadow: #303030 1px 1px;
}

.b-btn.w, .b-slt, .b-slt.on:hover, .b-slt:hover {
	background-image: none;
    background-image: linear-gradient(to top, #444444, #444444);
    border-color: #444444
}

.zt-i-s .autoheight {
	border-radius: 4px;
}
#a_ds_57 img,#a_ds_58 img,.ad-f{display: none;}/*页游广告*/
}
