css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(en.miui.com)

  {

.pls .md_ctrl 
{
    display: none !important;
}

.sign 
{
    display: none !important;
}

.ad .pls {
   height: 6px;
   background-color:transparant;
}

.ad .plc {
   height: 6px;
   background-color:transparant;
}


/*Username On Left Side*/
.xw1 {
    font-weight: 600;
    color: #000066;
    font-size: 14px;
    --text-transform : uppercase;
}
/* End of Section */
    
#postlist .pls .pi .authi {
    font-size: 15px;
}

/*Username Font Size on Comment Section */
#postlist .pcb .pstl .psti .xi2 {
    margin-right: 6px;
    font-size: 13px;
}
    
.sub-tit .xst {
    font-family: Tahoma;
    font-size: 14px;
}

.sub-tit {
    font-family: Tahoma;
    color: #000;
    font-size: 14px;
}
/*End Section*/
    
.ts {
    display: inline;
    font: 700 17px Tahoma;
}

.thread_cate {
    color: #3893EB !important;
    font-size : 14px;
    font-weight : bold;
}

.uinfo_total_tip {
    border-radius:  3px; 
}

.avatarbox-info .sub-tit span.icon {
    vertical-align: center;
    margin-top: 5px;
    padding: 1px;
}

#um .avt img {
    display: none;
}

.miui_bm img {
    border-radius: 5px;
}

#nv {
    height: 45px;
    margin-top: 5px;
}

#nv_fixed {
    --height: 45px;
    --background: transparent url("http://static.bbs.miui.com/static/image/miui/base/nv.png") repeat-x scroll 0px 0px;
    opacity: 0.95;
}

#nv li {
    --height: 45px;
    --line-height: 45px;
    font-size: 12px;
    font-family: tahoma;
    font-weight: bold;
}

#nv li a {
    --padding: 4px 16px;
}

}
