css:
@-moz-document domain("atlassian.net") {
    #summary-val, .issue-body-content, .command-bar, .toggle-title, html, body, p, div, h1, h2, h3, h4, h5, h6, img, pre, form, fieldset, .inactive, .activity-item-description blockquote, #issuetable>tbody>tr, #issuetable>tfoot>tr, .saved-search-selector{
        background-color: black !important;
        color: #B3B1B1 !important;
    }

    .aui-badge, .aui-toolbar .toolbar-group .toolbar-trigger, .aui-toolbar .aui-dd-parent .aui-dd-trigger, .lozenge, .jira-feedback-link , #footer-comment-button, .ghx-label-9{
        border: 1px solid #333333 !important;
        background: #0064C6 !important;
        color: black !important;
        text-shadow: 0 0px 0 black !important;
    }

    a {
        color: #007AF2 !important;
    }
    
    .dashboard-item-header > h3 {
        background-color: #00274C !important;
    }
    
    #issuetable, .issue-table {
        background-color: black !important;
        border: 1px solid #B3B1B1 !important;

    }

    .issue-table td, .issue-table th{
        border-color: #B3B1B1 !important;
        background-color: black !important;
    }
   
    .active{
        background-color: #FFFFFF !important;
    }
    
    .active > strong {
        background-color: #00274C !important;
        color: #828287 !important;
    }

    .operations > li {
        text-shadow: 0 0px 0 black !important;
    }
      
    .tabwrap>ul.tabs>li a, .tabwrap>ul.tabs>li a:link, .tabwrap>ul.tabs>li a:visited, .aui-page-header {
        background-color: black !important;
    }

    #create_link {
        color: #828287 !important;
    }

    .editable-field .overlay-icon {
        background-color: #38393b !important;
        color: #DF013A !important;
    }
        
    .focused, .active, .aui-button, .criteria-wrap, .searcherValue, aui-header-logo-custom {
        background-color: #00274C !important;
    }
    
    .issuerow {
        background-color: #38393b !important;
        color: #B3B1B1 !important;
    }
    
    .gadget-container {
        border-color: #00274C !important;
    }
    
    .ghx-label-7 {
        background-color: #0064C6 !important;
        border: 1px solid #333333 !important;
        color: black !important;
    }

    #logo > a {
        background-color: #00274C !important;
    }
    
    .google-visualization-table-table {
        background-color: #00274C !important;
    }
    
    .google-visualization-table-table > thead {
        border: 1px solid #333333 !important;
    }
    
    .google-visualization-table-table > tbody > tr {
        background-color: black !important;
        border: 1px solid #A4A4A4 !important;
    }

    .dashboardList > thead {
        background-color: #00274C !important;
        border: 1px solid #A4A4A4 !important;
    }
    
    .dashboardList > tbody > tr {
        background-color: black !important;
        border: 1px solid #A4A4A4 !important;
    }
    
    .google-visualization-table-td-number {
        background-color: black !important;
    }
    
    .aui > tbody > tr {
        background-color: black !important;
        border: 1px solid #A4A4A4 !important;
    }
    
    .aui-progress-indicator {
        background-color: #38393b !important;
    }
    
    .stats-gadget-numeric {
        color: #0064C6 !important;
    }
    
    .advanced-search {
        background-color: #00274C !important;
        color: #B3B1B1 !important;
        border: 1px solid #A4A4A4 !important;
    }
    
    .ui-sidebar {
        background-color: rgba(0, 0, 0, 0 ) !important;
    }
    
    .navigator-sidebar, .list-results-panel {
        border-right-color: #A4A4A4 !important;
    }
        
    .filter-panel-section {
        border-top-color: #A4A4A4 !important;
    }
    
    .issue-list > li, .navigator-search, .saved-search-selector, #issue-tabs, .activity-comment {
        border-bottom-color: #A4A4A4 !important;
    }
    
    .save-as-new-filter, .new-search, .aui-button-light {
        border: 1px solid #A4A4A4 !important;
    }
    
    #issue-tabs > li > a {
        border: 1px solid #A4A4A4 !important;
    }
    
    .aui-group.pagination-view.count-pagination {
        border-top-color: #A4A4A4 !important;
    }
    
    .rowHeader > th {
        border-bottom-color: #A4A4A4 !important;
    }
}
