@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("chan.sankakucomplex.com") {
body, body.en, body.ja {
    padding: 0px;
    margin: 0px;
    background: #222 none repeat scroll 0% 0%;
    min-width: 940px;
    color: #fff;
}
    div.status-notice {
    border: 2px solid #333;
    padding: 0.5em;
    margin: 0em 1em 1em;
    background: #252525 none repeat scroll 0% 0%;
    text-align: center;
}
    div#header {
    margin-bottom: 1em;
    background: #222 none repeat scroll 0% 0%;
    overflow: hidden;
    min-width: 940px;
}
    div#header ul#subnavbar {
    background-color: #252525;
    border: 1px solid #333;
}
    div#news-ticker {
    padding: 5px;
    background: #222 none repeat scroll 0% 0%;
    border-bottom: 2px solid #333;
    overflow: hidden;
    font-size: 1.5em;
}
    div.sidebar {
    border: 1px solid #333;
}
    ul.flat-list {
    background: #222 none repeat scroll 0% 0%;
}
    div#header ul#navbar li.current-page {
    background-color: #252525;
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #333 #333 #252525;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
}
    div#header ul#navbar li:hover > ul {
    display: block;
    position: absolute;
    background: #252525 none repeat scroll 0% 0%;
    height: 3.5em;
    width: 100%;
    font-size: 0.6em;
    padding-top: 0.5em;
    border-bottom: medium none;
    margin-top: 4px;
}
    div#header ul#navbar li:hover {
    background-color: #252525;
    border-width: 1px 1px 2px;
    border-style: solid;
    border-color: #555 #555 #252525;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
}
    div#header ul#navbar li {
    font-size: 130%;
    padding: 0px 10px 2px;
    margin: 0px;
    border-width: 1px 1px medium;
    border-style: solid solid none;
    border-color: #333 #333 -moz-use-text-color;
    -moz-border-top-colors: none;
    -moz-border-right-colors: none;
    -moz-border-bottom-colors: none;
    -moz-border-left-colors: none;
    border-image: none;
}
    input#tags {
    border: 1px solid #333;
    background: #222 linear-gradient(#111, #222) repeat scroll 0% 0%;
    border-radius: 35px;
    width: 182px;
    height: 20px;
    padding: 2px 8px;
    overflow: hidden;
    color: #fff;
}
    input#tags:focus {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
    input#tags:active {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
    input#tags:hover {
    background: #333 none repeat scroll 0% 0%;
    outline: medium none;
    box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.5) inset;
}
    #recommendations-paginator > span.recommended-prev {
    font-size: 2.5em;
    float: left;
    margin: 0.1em 0.25em 0.1em 45%;
    padding: 2px 6px;
    font-weight: bold;
    border: 1px solid #555;
    color: #888;
}
    #recommendations-paginator > span.recommended-next {
    font-size: 2.5em;
    float: left;
    margin: 0.1em 0px 0.1em 0%;
    padding: 2px 6px;
    font-weight: bold;
    border: 1px solid #555;
    color: #888;
}
    a:hover {
    color: #aaa;
    text-decoration: none;
}
    .no-comments {
    color: #888;
    font-size: 1.4em;
    font-weight: bold;
}
    img.preview:hover {
    padding: 0px;
    border: 2px solid #555;
}
    #add-to-favs, #remove-from-favs {
    background-image: url("https://drive.google.com/uc?export=download&confirm=no_antivirus&id=0B5R-7zz4EJVMUm95NEhjdUl4RjA");
    background-repeat: no-repeat;
}
    .unit-rating {
    list-style: outside none none;
    margin: 0px;
    padding: 0px;
    width: 170px;
    height: 34px;
    position: relative;
    background: transparent url("https://drive.google.com/uc?export=download&confirm=no_antivirus&id=0B5R-7zz4EJVMYXJrWl9qTGg5aGc") repeat-x scroll left center;
}
    .unit-rating li {
    background: transparent url("https://drive.google.com/uc?export=download&confirm=no_antivirus&id=0B5R-7zz4EJVMYXJrWl9qTGg5aGc") repeat scroll left top;
    height: 34px;
    display: block;
    text-indent: -9000px;
    z-index: 1;
}
    .unit-rating li a:hover {
    background: transparent url("https://drive.google.com/uc?export=download&confirm=no_antivirus&id=0B5R-7zz4EJVMYXJrWl9qTGg5aGc") repeat scroll left bottom;
    z-index: 2;
    left: 0px;
}
  /*[[hcfg]]*/
    
}

