css:
@media screen and (max-width: 1024px)
{
	#window .entry-main
	{
		margin: 60px auto;
		padding: 15px;
		text-align: center;
		min-width: 0;
	}
	
	#window
	{
		min-width: 0;
		height: 100%;
	}

	.qrcode
	{
		top: 115px;
	}
	
	#platforms
	{
		display: none;
	}

	#side
	{
		position: absolute;
		left: -38%;
		top: 0;
		transition: left 0.5s ease-in-out;
	}
	
	#side:hover
	{
    	left: 0;
	}
	
	.pane
	{
		overflow: visible;
	}
	
	#side .pane-header
	{
		width: 130%;
	}
	
	.pane-list-controls
	{
		flex: 1;
	}
	
	#side > .pane-header
	{
		outline: 1px solid rgba(0,0,0,.08);
    	position: relative;
    	z-index: 200;
	}
	
	#side > .pane-header > .pane-list-controls > .menu > span > .menu-item:nth-of-type(2):after
	{
		content: "";
		height: 26px;
		width: 26px;
		background-color: #eee;
		background-image: url(http://web.whatsapp.com/img/4e597b3956c4de4e1607f6f48e06a6fa.svg);
		background-repeat: no-repeat;
		background-size: 3025px 3104px;
		background-position: -486px -486px;
		position: absolute;
		top: 7px;
		right: -37px;
		transition: all .24s cubic-bezier(.4,0,.2,1);
	}
	
	#side:hover > .pane-header > .pane-list-controls > .menu > span > .menu-item:nth-of-type(2):after
	{
	    transform: rotate(180deg);
	}
	
	.drawer-container-panel
	{
		right: auto !important;
	}
	
	.cont-input-search .input-placeholder
	{
		width: 22%;
	}
}
