css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("clubic.com") {
  .header-content {
    padding-right: 177px !important;
  }
  .header-logo {
    height: 35px !important;
    width: 66px !important;
    font-size: 1.3rem !important;
    line-height: 35px !important;
  }
  .header-primary-nav li, .header-icon li,
  .header-primary-nav li.has-dropdown .fa-chevron-down, .header-primary-nav li.has-directlink .fa-chevron-down, .header-icon li.has-dropdown .fa-chevron-down, .header-icon li.has-directlink .fa-chevron-down {
    line-height: 35px !important;
  }
  .header-primary-nav, .header-icon,
  .header-primary-nav li .icon, .header-icon li .icon{
    height: 35px !important;
  }
  .header-primary-nav ul ul, .header-icon ul ul{
    right: 213px !important;
  }
  .header-search {
    left: 340px !important;
    right: 177px !important;
  }
  .header-search input {
    height: 35px !important;
    padding: 0.5rem !important;
    padding-right: 35px !important;
  }
  .header-primary-nav .header-topics, .header-icon .header-topics {
    width: 274px !important;
    left: 66px !important;
  }
  .header-primary-nav .header-topics li, .header-icon .header-topics li {
    line-height: 35px !important;
    height: 35px !important;
    font-size: 2rem !important;
  }
  .header-secondary-nav {
    height: 30px !important;
  }
  .header-secondary-nav li {
    line-height: 30px !important;
  }
  .header-content .header-autopromo {
    width: 177px !important;
    height: 65px !important;
    text-align: right !important;
  }
  .header-content .header-autopromo img {
    max-height: 65px !important;
  }
  #modal-newsletter {
    display: none !important; /* NOPE NOPE NOPE */
  }
  .mt40, .mt35, .mt30, .mt25, .mt20 {
    margin-top: 0px !important; /* We don't need more space */
  }
  h1 .fs10e {
    font-size: 0.7em !important; /* We're not that blind, y'know */
  }
  .fs28 {
    font-size: 18px !important; /* Thanks. */
  }
  #ob_holder, #outbrain_widget_0 {
    display: none !important; /* Remove Outbrain recommendation */
  }
  .input-footer input, .input-footer a,
  footer .footer-social, footer .footer-social a .fa {
    height: 40px !important;
    line-height: 40px !important;
  }
  .input-footer i {
    top: 28% !important;
  }
}
