Dark: rgb(37,37,37)!important;
Last post icon: url('data:image/gif;base64,R0lGODlhEAANAOMLABxkmx1lnBxmmh1mnS1zqS90qVKGtV6KtoKYwoKZxba2tgAAAAAAAAAAAAAAAAAAACH5BAEKAA8ALAAAAAAQAA0AAARO8ElkSiAmyb3PEKAADAcnHWEKlhsSpEoaaJIRAoASh8ZGiLrgTlDYvATCoCiwKYCSw2JNFNLhQL3J8SlDcFAgAFXM+m5BgbKJYikYvKYIADs=')!important;
Inacio's colors:
#AAA; #CCC; #1b1b1b; #282828; #1462c1 (blue); whatever else I felt like at the moment
*/
/* Facepunch Header Logo Change: */

#logo a {
	margin-left: -244px !important;
}
#logo a:after {
	content: url('http://i.imgur.com/3vINNru.png') !important;
}
/* Header "Facepunch" Text */

#lastelement {
	color: rgb(204, 204, 204)!important;
	text-shadow: none!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
/* Header Links */

#header a:link,
#header a:visited {
	color: rgb(204, 204, 204);
}
/* Header Background */

.above_body {
	background: rgb(37, 37, 37) !important;
}
/* Header Border and Shadow */

#header {
	border-bottom: none!important;
	box-shadow: none!important;
}
/* Header Popup Box */

.popupbox {
	border-radius: 4px 4px 4px 4px;
	border: 1px solid rgb(120, 120, 120)!important;
	background-color: rgb(62, 62, 62)!important;
}
/* Header Popup Box Text */

#more > div:nth-child(2) {
	color: rgb(150, 150, 150)!important;
}
/* Header Popup Box Links */

#more > div:nth-child(2) > a:nth-child(6),
#more > div:nth-child(2) > a:nth-child(8),
#more > div:nth-child(2) > a:nth-child(4),
#more > div:nth-child(2) > a:nth-child(2) {
	color: rgb(204, 204, 204)!important;
}
/* Header Search Box */

#navbarlinks > div:nth-child(1) > form > input[name=q] {
	background: rgb(50, 50, 50)!important;
	border: none !important;
	color: rgb(182, 182, 182);
	background-position: 1px 1px!important;
}
/* Header Search Button */

#navbarlinks > div:nth-child(1) > form > button {
	background: rgb(75, 75, 75)!important;
	border: none !important;
	color: rgb(206, 206, 206)!important;
	background-position: 2px 1px!important;
	height: 22px!important;
}
/* Background */

body {
	background: rgb(27, 27, 27)!important;
	background-position: 0px 1px!important;
}
/* Frontpage Table Headers */

.forumhead.foruminfo.L1 {
	width: auto;
	height: auto;
	background-color: rgb(37, 37, 37)!important;
	margin-bottom: 2px !important;
	box-shadow: 0px 2px 2px -2px #000 !important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 200;
}
/* Frontpage Table Header Links */

.forumhead a:link,
.forumhead a:visited {
	color: #CCC!important;
}
.forumhead a:link:hover,
.forumhead a:visited:hover {
	color: #fff!important;
	text-decoration: none!important;
}
/* Frontpage Table Cell Links */

.forumbit_post .foruminfo .forumdata .forumtitle a,
.forumbit_nopost .forumbit_nopost .forumrow .forumtitle {
	color: #CCC!important;
}
#userinfo > span.usertitle a {
	color: rgb(144, 144, 144)!important;
	text-decoration: none!important;
}
#userinfo > span.usertitle a:hover {
	color: #fff!important;
	text-decoration: none!important;
}
.forumbit_post .foruminfo .forumdata .forumtitle a:hover,
.forumbit_nopost .forumbit_nopost .forumrow .forumtitle:hover {
	color: #fff!important;
	text-decoration: none!important;
}
/* Frontpage Subforum Viewcount Text */

.forumbit_post .foruminfo .forumdata,
.forumbit_post .foruminfo .forumtitle,
.forumbit_post .foruminfo .viewing {
	color: #858585!important;
}
/* Frontpage Subforum Description Text */

TABLE.forums DIV.forumdata P.forumdescription {
	color: #9D9D9D!important;
}
/* Last Post Links */

.forumbit_post .forumlastpost .lastposttitle a,
.forumbit_post .forumlastpost .lastpostby a {
	color: #CCC!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
/* Last Post Timestamps */

td.alt.forumlastpost.last_post_column > p.lastpostdate {
	color: #858585!important;
}
/* =========================================================== */
/*					  Frontpage Table Cells					   */
/* =========================================================== */

table.forums {
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 400;
}
/* Frontpage Tables */

table.forums tr[id^='forum'] td.foruminfo {
	background-color: #323232 !important;
}
/* =========================================================== */
/*				          End of Cells					       */
/* =========================================================== */
/* Frontpage Table Border */

.forumbit_post {
	border: 1px solid #282828!important;
}
/* Frontpage Table Border */

.forumhead {
	border: none!important;
}
/* Frontpage Table Border */

TABLE.forums TR:last-child:not(I) {
	border-bottom: none!important;
}
/* Frontpage Table Column */

table.forums .forumbit_post td.alt {
	width: inherit;
	height: inherit;
	background-color: rgb(45, 45, 45)!important;
	background-position: 0px 2px!important;
}
/* =========================================================== */
/*						 Frontpage Fixes					   */
/* =========================================================== */
/* Frontpage Misaligned Cell Texture Fix */

#forum421,
#forum46 {
	height: 32px;
}
/* Inactive Forum Opacity Fix */

table.forums .forumbit_post.inactiveforum td>* {
	opacity: 1!important;
}
/* News Node Description Fix */

#forum51 > td.foruminfo > div > p > font {
	color: rgb(166, 166, 166)!important;
}
/* Gold Members Column Fix */

#forum46 > td.alt.forumlastpost.last_post_column {
	color: rgba(0, 0, 0, 0.0)!important;
}
/* Bug Report Column Fix */

#forum421 > td.alt.forumlastpost.last_post_column {
	color: rgba(0, 0, 0, 0.0)!important;
}
/* =========================================================== */
/*				     	  End of Fixes	     				   */
/* =========================================================== */
/* Frontpage Info */

#content_inner > center {
	color: rgb(135, 135, 135)!important;
}
/* Time Info */

.footer_time {
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 200;
	color: #AAA!important;
}
/* Time */

.time {
	color: #AAA!important;
}
/* Footer Background */

div#footer {
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 200;
	background: rgb(17, 17, 17)!important;
}
/* Footer Text */

div#footer a {
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 200;
	color: rgb(186, 186, 186)!important;
}
div#footer a:hover {
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: 200;
	color: #fff!important;
	text-decoration: none!important;
}
.ratingslist {
	-icab-box-shadow: 0px 0px 0px #999!important;
	-khtml-box-shadow: 0px 0px 0px #999!important;
	-moz-box-shadow: 0px 0px 0px #999!important;
	-o-box-shadow: 0px 0px 0px #999!important;
	-webkit-box-shadow: 0px 0px 0px #999!important;
	border-top: 0px #999!important;
	box-shadow: 0px 0px 0px 0px #999!important;
}
/* =========================================================== */
/*				        End of Frontpage	     			   */
/* =========================================================== */
/* New Thread Button */

a.newcontent_textcontrol {
	border: 1px solid #AAA!important;
	color: rgb(210, 210, 210)!important;
	background-color: #333333!important;
}
/* Page Nav */

div#above_threadlist div.threadpagenav span>a,
div#below_threadlist div.threadpagenav span>a,
div#above_postlist div.pagination_top span>a,
div#below_postlist div.pagination_bottom span>a,
div#above_postlist span>a,
div#pagination_top span>a,
div#below_postlist div#pagination_bottom span>a,
div#below_searchresults span>a {
	color: #0066cc!important;
	width: inherit;
	height: inherit;
	background-color: #252525!important;
	background-repeat: repeat!important;
}
td.threadinfo > div > h3 > span {
	cursor: default!important;
}
.loginbutton {
	background-color: rgb(75, 75, 75)!important;
	color: rgb(182, 182, 182)!important;
	border: 1px solid rgb(75, 75, 75)!important;
}
.loginbutton:hover {
	background-color: rgb(75, 75, 75)!important;
	color: #fff!important;
	border: 1px solid rgb(75, 75, 75)!important;
	cursor: pointer !important;
}
#breadcrumb a:hover {
	color: #FFFFFF!important;
	text-decoration: none!important;
}
#more a:hover {
	color: #FFFFFF!important;
	text-decoration: none!important;
}
#more > div:nth-child(2) > a:nth-child(6):hover,
#more > div:nth-child(2) > a:nth-child(8):hover,
#more > div:nth-child(2) > a:nth-child(4):hover,
#more > div:nth-child(2) > a:nth-child(2):hover {
	color: #FFFFFF!important;
}
.postcontent A {
	color: #2B72C7!important;
}
.postcontent A:hover {
	color: #5B91D4!important;
}
#header #navbarlinks a:hover {
	color: #FFFFFF!important;
	text-decoration: none!important;
}
div#below_threadlist div.threadpagenav a.popupctrl:hover,
div#below_threadlist div.threadpagenav a.popupctrl:hover,
div#above_threadlist div.threadpagenav a.popupctrl:hover,
div#above_threadlist div.threadpagenav a.popupctrl:hover,
div#above_postlist div.pagination_top a.popupctrl:hover,
div#above_postlist div.pagination_top a.popupctrl:hover,
div#below_postlist div.pagination_bottom a.popupctrl:hover {
	cursor: default !important;
	color: #124c93!important;
}
/* Page Nav Current Page */

div#above_threadlist div.threadpagenav span.selected a,
div#below_threadlist div.threadpagenav span.selected a,
div#above_postlist div.pagination_top span.selected a,
div#below_postlist div.pagination_bottom span.selected a,
div#above_postlist span.selected a,
div#pagination_top span.selected a,
div#below_postlist div#pagination_bottom span.selected a,
div#below_searchresults span.selected a {
	color: rgb(0, 173, 238)!important;
	width: inherit;
	height: inherit;
	background-color: #2a2a2a!important;
	background-repeat: repeat!important;
	background-image: none!important;
}
/* Page Nav Border */

div#above_threadlist div.threadpagenav a.popupctrl,
div#below_threadlist div.threadpagenav a.popupctrl,
div#above_postlist div.pagination_top a.popupctrl,
div#below_postlist div.pagination_bottom a.popupctrl,
div#above_postlist a.popupctrl,
div#pagination_top a.popupctrl,
div#below_postlist div#pagination_bottom a.popupctrl,
div#below_searchresults a.popupctrl {
	border: none !important;
}
/* Page Nav Border */

div#above_threadlist div.threadpagenav span>a,
div#below_threadlist div.threadpagenav span>a,
div#above_postlist div.pagination_top span>a,
div#below_postlist div.pagination_bottom span>a,
div#above_postlist span>a,
div#pagination_top span>a,
div#below_postlist div#pagination_bottom span>a,
div#below_searchresults span>a {
	border: none!important;
}
/* page nav brackets */

table#threads tr.threadbit td.threadinfo .threadpagenav {
	color: rgb(182, 182, 182)!important;
}
/* Thread List Header Part 1 */

#threads > thead > tr > td.threadicon {
	background-color: #252525!important;
}
/* Thread List Header Part 2 */

#threads > thead > tr > td.threadinfo {
	background-color: #252525!important;
}
/* Thread List Header Part 3 */

#threads > thead > tr > td.threadlastpost {
	background-color: #252525!important;
	background-position: 2px 0px!important;
}
/* Thread List Header Part 4 */

#threads > thead > tr > td.threadreplies {
	background-color: #252525!important;
	background-position: 1px 0px!important;
}
/* Thread List Header Part 5 */

#threads > thead > tr > td.threadviews {
	background-color: #252525!important;
	background-position: 1px 0px!important;
}
/* Thread List Background */

table#threads tr.threadbit {
	background-color: #303030!important;
}
/* Thread List Columns */

table#threads tr.threadbit td.alt {
	background-color: #303030!important;
	background-position: 0px 2px!important;
}
/* Thread List Border */

div#threadlist div.boxshadow,
table#threads tr.threadbit td {
	border: 1px solid #222222!important;
}
/* Thread List Texture Alignment Fix */

table#threads tr.threadbit td {
	height: 36px;
}
/* Thread Title */

td.threadinfo > div > h3 a:visited,
td.threadinfo > div > h3 body_avisited,
td.threadinfo > div > h3 a:link,
td.threadinfo > div > h3 body_alink {
	color: #DADADA!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
	font-weight: normal;
}
/* Thread Font Weight Fix */

table#threads tr.threadbit.new h3.threadtitle>a,
table#threads tr.threadbit.old h3.threadtitle>a {
	font-weight: 400!important;
}
/* Thread Pages */

td.threadinfo > div > h3 > span a:link,
td.threadinfo > div > h3 > span body_alink,
td.threadinfo > div > h3 > span a:visited {
	color: #00ccff!important;
}
/* New Posts Notification */

table#threads tr.threadbit td.threadinfo .newposts {
	background-color: transparent!important;
	background-color: rgb(62, 62, 62)!important;
	border: 0px!important;
	padding: 4px!important;
	border-radius: 0px!important;
}
/* New Posts Notification */

table#threads tr.threadbit td.threadinfo .newposts:hover {
	background-color: transparent!important;
	background-color: rgb(75, 75, 75)!important;
	border: 0px!important;
	padding: 4px!important;
	border-radius: 0px!important;
}
/* New Posts Notification Icon */

table#threads tr.threadbit td.threadinfo .newposts img {
	position: relative;
	top: 1px!important;
}
/* Thread Author */

table#threads tr.threadbit td.threadinfo .author a {
	color: #A1A1A1!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
table#threads tr.threadbit td.threadinfo .author a:hover {
	color: #FFFFFF!important;
}
/* Dividers */

table#threads tr.threadbit td.threadinfo .author {
	color: #656565!important;
}
/* Mark Unread */

table#threads tr.threadbit td.threadinfo .author a:nth-child(3) {
	color: rgb(129, 129, 129)!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
/* Thread Reading */

table#threads tr.threadbit td.threadinfo .author .viewers {
	color: #8D8D8D!important;
}
/* Last Post Username */

td.alt.threadlastpost a:visited,
td.alt.threadlastpost body_avisited,
td.alt.threadlastpost a:link,
td.alt.threadlastpost body_alink {
	color: #00ccff!important;
}
/* Last Post Timestamp */

td.alt.threadlastpost > dl {
	color: rgb(129, 129, 129)!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
td.alt.forumlastpost.last_post_column > p.lastpostdate > a > img,
td.alt.threadlastpost > dl > dd:nth-child(3) > a.lastpostdate.understate > img {
	padding: 16px 22px 0px 0px!important;
	background: url("data:image/png;base64,")!important;
	background-size: auto!important;
	width: 0!important;
	height: 0!important;
	background-repeat: no-repeat!important;
}
/* Thread Replies */

td.threadreplies > a {
	color: #00ccff!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
/* Thread Views */

td.threadviews.alt > span {
	color: #A1A1A1!important;
	font-family: "Open Sans", Roboto, Helvetica, "Trebuchet MS", sans-serif;
}
/* User List */

#bottom_box {
	background-color: #3D3D3D!important;
	border: none!important;
}
/* User List Left Column */

#bottom_box div.img {
	background-color: rgb(45, 45, 45)!important;
}
/* User List Left Column Border */

#bottom_box div.td {
	border: none!important;
}
/* User List Normal Member Color */

#whos_reading a {
	color: #aaa!important;
}
/* Thread Header */

div.threadhead {
	background-color: #282828!important;
	margin-bottom: 5px;
	box-shadow: 0px 2px 2px -2px #111 !important;
}
/* Thread Footer */

div.threadfoot {
	background-color: #252525!important;
}
/* Post Number */

ol#posts .postbitlegacy .posthead .nodecontrols a {
	color: #1462c1!important;
}
/* Thread Post Timestamp */

div.posthead > span.postdate.old > span {
	color: #aaa!important;
}
/* Gold Member Status */

div.postdetails > div.userinfo > span {
	color: rgb(144, 144, 144)!important;
}
/* User Stats */

ol#posts .postbitlegacy .postdetails .userinfo #userdata #userstats {
	color: rgb(144, 144, 144)!important;
}
/* Username Text */

ol#posts .postbitlegacy .postdetails .userinfo .username_container a.username {
	color: #D0D0D0!important;
}
/* Ratings Text */

ol#posts .postbitlegacy .postfoot .rating_results {
	color: #D0D0D0!important;
}
/* List Ratings */

ol#posts .postbitlegacy .postfoot .rating_results a.rating_users {
	color: #1462c1!important;
}
/* Ratings Box Normal User Names */

div.ratingsbox a:visited,
div.ratingsbox a:link {
	color: #D0D0D0!important;
}
.information A {
	color: rgb(169, 169, 169)!important;
}
.popupbox {
	color: rgb(129, 129, 129)!important;
}
/* New Post */

ol#posts .postbitnew div.postdetails,
ol#announcements .postbitnew div.postdetails,
div#showpm>ol .postbitnew div.postdetails,
ol#message_list .postbitnew div.postdetails,
ol#posts .postbitnew div.postfoot,
ol#announcements .postbitnew div.postfoot,
div#showpm>ol .postbitnew div.postfoot,
ol#message_list .postbitnew div.postfoot {
	background: #202020!important;
}
/* Reply Button */

ol#posts .postbitlegacy .postfoot .postcontrols a.post_button {
	background-color: rgb(75, 75, 75)!important;
	border: none!important;
	color: rgb(182, 182, 182)!important;
	background-position: 1px 0px!important;
}
/* Edited: */

blockquote > font > b {
	color: rgb(144, 144, 144)!important;
}
/* Edited: in quote */

blockquote > div > div.message > font > b {
	color: rgb(144, 144, 144)!important;
}
/* Old Post */

ol#posts .postbitold div.postdetails,
ol#announcements .postbitold div.postdetails,
div#showpm>ol .postbitold div.postdetails,
ol#message_list .postbitold div.postdetails,
ol#posts .postbitold div.postfoot,
ol#announcements .postbitold div.postfoot,
div#showpm>ol .postbitold div.postfoot,
ol#message_list .postbitold div.postfoot {
	background: #181818!important;
}
/* text editor stuff */

.formcontrols .blockrow {
	background: 181818!important;
	border: none!important;
}
/* text editor stuff */

#cke_vB_Editor_QR_editor {
	border: none!important;
}
/* text editor stuff needs testing */

.texteditor.forum .cke_skin_kama textarea.cke_source,
.texteditor.forum textarea {
	color: rgb(188, 188, 188);
	background: rgb(50, 50, 50)!important;
}
/* text editor stuff */

.cke_skin_kama .cke_wrapper.cke_ltr,
.cke_skin_kama .cke_wrapper.cke_rtl {
	background: rgb(75, 75, 75)!important;
}
/* timestamp text */

.postbit .posthead,
.postbitlegacy .posthead,
.eventbit .eventhead {
	color: #282828!important;
}
/* posting box area */

.blockfoot,
.blocksubfoot {
	background: #191919!important;
}
.quote .information {
	background: #303030!important;
}
.quote .message {
	background: #292929!important;
}
#quick_reply .wysiwyg_block {
	border: 0px!important;
	background: #1B1B1B!important;
}
.quote {
	border: 1px solid #353535!important;
	box-shadow: 0px 3px 3px -3px #111111 !important;
}
/* post text color */

ol#posts .postbitlegacy .postdetails .postbody .postrow,
ol#announcements .postbitlegacy .postdetails .postbody .postrow,
div#showpm>ol .postbitlegacy .postdetails .postbody .postrow,
ol#message_list .postbitlegacy .postdetails .postbody .postrow,
ol#posts .postbit .postdetails .postbody .postrow,
ol#announcements .postbit .postdetails .postbody .postrow,
div#showpm>ol .postbit .postdetails .postbody .postrow,
ol#message_list .postbit .postdetails .postbody .postrow,
ol#posts .postbitdeleted .postdetails .postbody .postrow,
ol#announcements .postbitdeleted .postdetails .postbody .postrow,
div#showpm>ol .postbitdeleted .postdetails .postbody .postrow,
ol#message_list .postbitdeleted .postdetails .postbody .postrow {
	color: #ccc!important;
}
ol#posts .postbitlegacy .posthead,
ol#announcements .postbitlegacy .posthead,
div#showpm>ol .postbitlegacy .posthead,
ol#message_list .postbitlegacy .posthead,
ol#posts .postbit .posthead,
ol#announcements .postbit .posthead,
div#showpm>ol .postbit .posthead,
ol#message_list .postbit .posthead,
ol#posts .postbitdeleted .posthead,
ol#announcements .postbitdeleted .posthead,
div#showpm>ol .postbitdeleted .posthead,
ol#message_list .postbitdeleted .posthead {
	background: #252525!important;
	border: none!important;
}
div.threadhead,
div.threadfoot {
	border: none!important;
}
ol#posts .postbitlegacy,
ol#announcements .postbitlegacy,
div#showpm>ol .postbitlegacy,
ol#message_list .postbitlegacy,
ol#posts .postbit,
ol#announcements .postbit,
div#showpm>ol .postbit,
ol#message_list .postbit,
ol#posts .postbitdeleted,
ol#announcements .postbitdeleted,
div#showpm>ol .postbitdeleted,
ol#message_list .postbitdeleted {
	border: none!important;
}
.blockhead {
	background: #282828!important;
	border: none!important;
