css:
@-moz-document url-prefix("http://play.google.com/store"), url-prefix("https://play.google.com/store") {
body {
    background-color: #454545 !important;
    color: #ccc !important;
}

/* Scrollbars */
.device-selector-dropdown-children::-webkit-scrollbar {
    height: 12px!important;width: 12px!important;
    background: -webkit-linear-gradient(40deg , #2a2b2a , #454545 , #2a2b2a 100%)!important;
}

.device-selector-dropdown-children::-webkit-scrollbar-thumb {
    background: -webkit-linear-gradient(40deg , #2a2b2a , #454545 , #2a2b2a 100%)!important;
    border-radius: 0px!important;
    border-style: solid!important;
    border-width: 1px!important;
    border-color: #757474;
}

/* Loading Spinner */
.body-content-loading-spinner {
background:url(http://i.imgur.com/EwKC589.gif) no-repeat center center !important;
}

/* Text Resize */
.card.medium .title {
    font-size: 15px !important;
}

/* Black Border */
#butterbar {
   border-color: #000 !important;
}

/* Transition Background */
.overflow-menu-item:hover, .menu-link.id-no-menu-change.not-selected:hover,  .dropdown-child:hover, .leaf-submenu-link-wrapper:hover, .parent-submenu-link-wrapper:hover, .child-submenu-link-wrapper:hover, .action-bar-dropdown:hover, .action-bar-link:hover, .nav .secondary-sub-nav-option:hover {
    transition: background-color 0.1s ease-in!important;
}
.card .cover:hover
{
    background-color: #454545 !important;
    transition: background-color 0.5s ease!important;
}

/* Card Grid Size */
.card.tiny.square-cover {
    width: 250px !important;
    height: 175px !important;
}

.card.small.square-cover {
    width: 250px !important;
}

.card {
    margin: 1px !important;
    margin-top: 5px !important;
}

.card.medium {
    margin-top: 6px !important;
}

/* Dark Background #2a2b2a */
.card-content, .card .cover, .card.devices .cover, .card.people .cover, .card.tiny .acquired-label, .card .price, .footer, .action-bar, .dropdown-submenu, .dropdown-sub-submenu, .gb_5b, .nav-container.hover-zone, .details-section, .rating-bar-container.four .bar, .rating-bar-container.two .bar, .card .placeholder-background, #footer-content, .overflow-menu-button:hover, .cluster.single-trusted-source, .menu-link.id-no-menu-change.not-selected, .nav .store .sub-nav, .dropdown-menu-children, .dropdown-menu-container.open .dropdown-menu, .dropdown-menu:active, .dropdown-menu:hover, #widget_bounds, .modal-dialog, .device-selector-dropdown-children, .season-selector-dropdown, #season-selector, .gb_6b, .entity-info-cluster-gap-filler {
    background-color: #2a2b2a !important;
}

/* Gray Background #454545 */
.gbqfqw, .show-all-hover-zone, .card .reason-set, .details-info, .app-compatibility-device-list, .rating-box, .details-section .card-content.id-track-click.id-track-impression,  .details-section .card .cover, .details-section .card .price, .review-panel-content, .details-section .card.tiny .acquired-label, .overflow-menu, .popularity-circle-empty, .track-list-row:hover td, .track-list-row.playing td, .track-list-row.loading td, .card.no-rationale .stars-container .price-container .apps.is-price-tag.buy-button-container > button, .card.no-rationale .stars-container .price-container .movies.is-price-tag.buy-button-container > button, .card.no-rationale .stars-container .price-container .books.is-price-tag.buy-button-container > button, .card.no-rationale .stars-container .price-container .magazines.is-price-tag.buy-button-container > button, .card.no-rationale .stars-container .price-container .music.is-price-tag.buy-button-container > button, .device-selected, .device-selector-dropdown-child:hover, #season-selector:hover {
    background-color: #454545 !important;
}
 
/* White Text #ccc */
.card .title, #action-dropdown-children-Categories a, a.title-link.id-track-click, .cluster-heading .subtitle, .nav .shop-link, .nav .library-link, .nav .wishlist-link, .secondary-sub-nav-option .sub-nav-link, .info-container .document-title, .details-section .heading, .details-section.description.simple .text-body, .score-container .reviews-stats, .rating-box .rating-bar-container, .rating-box .bar-number, .whatsnew .recent-change, .meta-info .title, .leaf-submenu-link, .parent-submenu-link, .child-submenu-link, .cast-credit .cc-row-label, .overflow-menu, .track-list-row:hover td, .track-list-row.playing td, .track-list-row.loading td, .about-the-author .details-section-contents, .dropdown-child, .centered-text, .how-to-read-contents {
    color: #ccc !important;
}

/* Black Text #000 */
.play-button.apps, .buy-button-container.play-button .price, .buy-button-container.play-button .info-label, .play-button, .rating-bar-container.five .bar-number, .rating-bar-container.three .bar-number, .rating-bar-container.one .bar-number, .overflow-menu-item:hover, .menu-link.id-no-menu-change.not-selected:hover .title, .dropdown-child.selected, .dropdown-child:hover, #action-dropdown-children-Categories a:hover, .action-bar-link a:hover, #action-dropdown-parent-Categories:hover .title, li.nav-list-item.id-track-click.apps.visible-item span.title:hover, .nav .secondary-sub-nav-option:hover .sub-nav-link, #butterbar, .leaf-submenu-link:hover, .season-selector-dropdown-item.selected-season, .season-selector-dropdown-item:hover, .action-bar-dropdown:hover button, .child-submenu-link-wrapper:hover .child-submenu-link, li.sub-nav-option.id-track-click:hover a, .nav .chosen:hover {
    color: #2a2b2a !important;
}

/* Bold Text */
.menu-link.id-no-menu-change.not-selected:hover .title, .leaf-submenu-link-wrapper:hover, .parent-submenu-link-wrapper:hover, .child-submenu-link-wrapper:hover, .action-bar-link a:hover, .nav .secondary-sub-nav-option:hover .sub-nav-link, .gbqfif, .gbqfsf, #butterbar, .action-bar-dropdown:hover button, li.sub-nav-option.id-track-click:hover a, .nav .chosen:hover, .overflow-menu-item:hover, li.nav-list-item.id-track-click.apps.visible-item span.title:hover {
    font-weight: bold !important;
}

/* Border Radius */
.entity-avatar-image, .person-image {
    border-radius: 25px;
}

/* Display None */
.write-review-triangle-container {
display: none !important;
}

/* Gradient Transparent */
.paragraph-end {
    background-image: -webkit-gradient(linear,left top,right top,color-stop(0%,rgba(255,255,255,0)),color-stop(100%,rgba(255,255,255,0)));
    background-image: -webkit-linear-gradient(left,rgba(255,255,255,0),rgba(255,255,255,0));
    background: linear-gradient(to right,rgba(255,255,255,0),rgba(255,255,255,0));
    background-image: -webkit-linear-gradient(left,rgba(255,255,255,0),rgba(255,255,255,0));
}

/* Gradient Dark */
.show-more-end, .paragraph-end.details-light {
    background-image: -webkit-gradient(linear,left top,left bottom,color-stop(0%,rgba(245,245,245,0)),color-stop(100%,rgba(245,245,245,1)));
    background-image: -webkit-linear-gradient(top,rgba(245,245,245,0),rgba(42,43,42,1));
}

/* Buffer Space */
.topic-details .entity-header-buffer-space {
    max-height: 220px;
    min-height: 0px;
}


/* Image Brighten */
.gb_2 {
     -webkit-filter: brightness(0%);  
    -webkit-filter: contrast(0%);
}

/* Image Darken */
#gbqfb .gb_2 {
     -webkit-filter: brightness(-100%);  
    -webkit-filter: contrast(-100%);
}



/* Accent Loading Overlay */
.body-content-loading-overlay {
        background-color: rgba(255, 152, 158, 0.50) !important;
}

/* Accents Color Text */
.card .consume-link, .card .price, button, .nav .chosen, .nav .chosen .sub-nav-link, .action-bar-link.active, .contains-text-link a, .contains-text-link a:visited, .text-body a, .text-body a:visited, .fake-link, .score-container .score, .featured-review .author-name a, .featured-review .author-name, #gb#gb a.gb_la, .censor-sticker, .review-info .author-name, .review-info .author-name a, .action-bar-link.active a, .gbqfif, .gbqfsf, .header-star-badge, .apps.base-dialog .title, .apps.base-dialog .purchase-complete-link, .apps.base-dialog .voucher-message, .base-dialog.apps .purchase-header .price, .ep-feature-desc, .movies.base-dialog .title, .movies.base-dialog .purchase-complete-link, .movies.base-dialog .voucher-message, .purchase-disambiguation-container.tv .purchase-tv-title, .highlighted-review-panel .review-author-name a {
    color: #FF989E !important;
}

/* Accents Color Background */
.nav .selected .hover-target, .nav .icon-container, .play-button.apps, .play-button, .FP, .rating-bar-container.five .bar, .rating-bar-container.three .bar, .rating-bar-container.one .bar, .action-bar-spacer, .card .cover, .overflow-menu-item:hover, .overflow-menu-button-highlighted, .popularity-circle-full, .menu-link.id-no-menu-change.not-selected:hover, .dropdown-child.selected, .dropdown-child:hover, .leaf-submenu-link-wrapper:hover, .parent-submenu-link-wrapper:hover, .child-submenu-link-wrapper:hover, .action-bar-dropdown:hover, .action-bar-link:hover, .nav .secondary-sub-nav-option:hover, #butterbar, .season-selector-dropdown-item.selected-season, .season-selector-dropdown-item:hover, .entity-info, li.sub-nav-option.id-track-click:hover, .nav .chosen:hover {
    background-color: #FF989E !important;
}

/* Accents Color Border */
.censor-sticker, .dropdown-menu-children, .person-image, .entity-avatar-image {
   border-color: #FF989E !important;
}

/* Accents Color Border Top */
.quick-rating-container, .nav .store .sub-nav {
   border-top-color: #FF989E !important;
}

/* Accents Color Border Bottom */
.details-info, .details-section-divider, .details-section-pre-divider {
   border-bottom-color: #FF989E !important;
}

/* Accent Images */ 
.card .acquired-icon, .card.magazines .acquired-icon, .card.movies .acquired-icon, .card.tv .acquired-icon {
    background-image: url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAABAAAAAQCAYAAAAf8/9hAAAAiklEQVQ4y2P4//8/AyWYgWoGwMCm05YCQNwAxA+A+D8UP4CKCcDUYTUAqMAAiD8gaUTHIDkDrAZAbcanGdkQAWwGNODRdAGKYfwGbAY8wKMZ5LoC5DDBZgC6M5E1J6AbjM+AA1BNC3BpxmUAshcSoLHigMNbD4gJxAY8sdJA/WikOCFRnJQHLDcCAJI/mHSckhKUAAAAAElFTkSuQmCC);
     -webkit-filter: hue-rotate(279deg) !important;
    filter: hue-rotate(279deg) !important;
}

.id-app-compatibility-icon.compatibility-image.compatibility-info-img, .card.apps .medium-star .current-rating, .card.apps .medium-star .star-common:hover {
     -webkit-filter: hue-rotate(279deg) !important;
    filter: hue-rotate(279deg) !important;
}

/* Accent Button Gradient */
#gbqfb {
background: -webkit-linear-gradient(top,#FF989E,#FF989E) !important;
}

/* Accent Box Shadow Top */
.dropdown-menu-container.open .dropdown-menu, .dropdown-menu:active, #season-selector.open, #season-selector:active {
    box-shadow: inset 0 2px 0 #FF989E;
}

}
