css:
@-moz-document url-prefix("http://www.roblox.com"), url-prefix("https://www.roblox.com"), url-prefix("https://web.roblox.com"), url-prefix("http://web.roblox.com") {
#header {
        background-color: #00FF00;
    }
    
.navigation .notification-icon {
        background: #00FF00;
    }

.forum-table-header, body .forum-table-header th, body .forum-table-footer, #ctl00_cphRoblox_Message1_ctl00_CenterColumn > table > tbody > tr:nth-child(1) > th, #ctl00_cphRoblox_CenterColumn > table > tbody > tr:nth-child(1) > th {
        background: #b71c1c !important;
    }

.rbx-upgrade-now #upgrade-now-button, .rbx-btn-secondary-xs.btn-more, #AjaxCommentsContainer > div > div.comment-form > form > button {
  		background-color: #00FF00 !important;
   		border-color: #00FF00;
    }

.rbx-upgrade-now #upgrade-now-button:hover, .rbx-btn-secondary-xs.btn-more:hover, #AjaxCommentsContainer > div > div.comment-form > form > button:hover {
        background: #00FF00 !important;
        border-color: #00FF00;
    }

#TopAbpContainer {
  		display: none;
    }

.ad-annotations, #LeftGutterAdContainer, #RightGutterAdContainer, #Skyscraper-Adp-Right, #Skyscraper-Adp-Left {
  		display: none;
    }

    
   
#party_none_title, div.friend_dock_chatbox_titlebar.chat-header-blink-off {
		background: linear-gradient(to bottom,#00FF00,#00FF00);
    }
#ctl00_cphRoblox_RightColumn, #Body > table > tbody > tr > td:nth-child(3), #Body > table > tbody > tr > td:nth-child(4) {
  		display: none;
    }
body {
  	font-family: Segoe UI;
    }
#GamesPageLeftColumn { width: auto; margin-right: 10px !important; margin-left: 10px !important; }
#GamesListsContainer > div {
	background-color: transparent !important;
	width: 100% !important;
    }

body {
    background: transparent !important;
  }
  .content {
    background: transparent !important;
  }
  /*background*/
  #fb-root {
    width: 100% !important;
    height: 100% !important;
    position: fixed !important;
    background-image: url("http://i.imgur.com/8EyM8Md.jpg") !important;
    background-image: url("http://i.imgur.com/8EyM8Md.jpg") !important; /*custom banner*/
    background-size: cover !important;
    background-attachment: fixed !important;
    filter: blur(5px) !important;
	-webkit-filter: blur(2px) !important;
    z-index: -1 !important;
  }
  h1, h2, h3, h4 {
    mix-blend-mode: difference;
    color: #FFFFFF !important;
  }
  .home-header-content a {
    color: #00FF00 !important;
  }
  .blog-news .news .news-link {
    background: none !important;
  }
  .blog-news .news .news-link::after {
    background: none !important;
  }
  /*navigation bar*/
  .rbx-header {
    background-color: rgba(0, 0, 0, 0.8) !important;
  }
  /*.rbx-header .rbx-navbar-search .rbx-navbar-search-option.selected {
    box-shadow: 4px 0px 0px 0px red inset !important;
  }*/
  .section {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
  .game .game-item {
    background-color: rgba(255, 255, 255, 0.9) !important;
  }
/*Trim content backgrounds, fill in new ones*/
  #Body, #BodyWrapper {
    background: transparent !important;
  }
  /*groups page*/
  #mid-column {
    background-color: #FFF !important;
  }
  /*develop page*/
  .BuildPageContent {
    background-color: #FFF !important;
    float: none !important;
  }
  #MyCreationsTab {
    width:792px !important;
  }
  .content-area {
    border-right: none !important;
  }
  /*devex page*/
  #DevExLeftColumn {
    height: 684px !important;
  }
  /*account settings*/
  #AccountPageRight {
    background-color: #FFF !important;
  }
  .settings-section {
    padding: 10px !important;
  }
  .divider-right {
    border: none !important;
  }
  .StandardPanelContainer .StandardPanelWhite {
    padding: 0 !important;
    padding-left: 6px !important;
  }
  /*games page*/
  #GamesPageRightColumnSidebar {
    background: none !important;/*#FFF !important;*/
    padding-top: 20px !important;
  }
  .sidebar-no-ad {
    top: 25px !important;
  }
  .games-list-container {
    border-right: 5px solid #FFF !important;
    border-left: 5px solid #FFF !important;
  }
  #DivToHideOverflowFromLastGamesList { /*lol*/
    background: transparent !important;
  }
  
  /*new games page*/
  .games-page-right-sidebar {
    display: none !important;
  }
  .games-page-left {
    margin-right: 10px !important;
  }
  .games-page-lists-container {
    background: none !important;
  }
  .games-list-container {
    border-left: 10px solid rgba(255,255,255,0) !important;
    border-right: 10px solid rgba(255,255,255,0) !important;
    background-color: rgba(255,255,255,0.9) !important; 
    margin-bottom: 60px !important;
  }
  .games-list {
    min-height: 275px !important;
  }
  /* Games page ad support - disabled.
  #GamesListContainer0, #GamesListContainer8 {
    margin-right: 315px !important;
  }
  */
  /*catalog*/
  div#catalog {
    background-color: #FFF !important;
    padding-left: 10px !important;
    padding-bottom: 5px !important;
  }
  .right-content {
    padding-left: 10px !important;
  }
  /*profile page*/
  #UserContainer {
    background-color: #FFF !important;
    padding-left: 5px !important;
  }
  .divider-right, .divider-left {
    background-color: #FFF !important;
  }
  #ctl00_cphRoblox_rbxHeaderPane_statusBox, #ctl00_cphRoblox_OBCmember {
    background-color: #FFF !important;
  }
  .game-main-content .game-calls-to-action .game-name {
    background: transparent !important;
  }
  .game-main-content .game-calls-to-action .game-name::after {
    background: transparent !important;
  }
  /*item page*/
  #Item {
    background-color: #FFF !important;
    padding-top: 5px !important;
    padding-right: 5px !important;
  }
  /*forums*/
  .table-header .first {
    border-left: none !important;
  }
  .table td, .table th {
    padding-left: 10px !important;
  }
  /*place configuration*/
  .boxed-body {
    background-color: #FFF !important;
    padding-bottom: 10px !important;
  }
  #navbar {
    height: auto !important;
  }
.ad{
display:none!important;    
}
.ad-annotations{
  display:none!important;}
}
