css:
@-moz-document url-prefix(http://www.ign.com/boards/) {
#NavSearch {
background-color:#1e1e1e
}

#NavSearch .secondaryControls {
background-color:#1e1e1e
}

#content .pageContent {
background-color:#1e1e1e
}

#header-container {
display:none
}

.Menu {
background-color:#1e1e1e;
border:none
}

.avatarCropper {
background:none
}

.bbCodeBlock {
-webkit-border-radius:0;
border-color:#4b4b4b;
border-radius:0
}

.bbCodeQuote .attribution {
border-bottom:none;
color:#ccc
}

.bbCodeQuote .quoteContainer,.bbCodeQuote .attribution {
background:#151515
}

.breadcrumb {
background-color:#000;
border:none;
box-shadow:0 0 10px 0 rgba(0,0,0,0.6)
}

.breadcrumb .crust a.crumb {
background-color:#000;
border:none
}

.defaultGradient {
background:none
}

.discussionList .sectionHeaders {
background-color:#1e1e1e;
color:#999
}

.discussionList .sectionHeaders .main a,.discussionList .sectionHeaders .stats a,.discussionList .sectionHeaders .lastPost a {
color:#ccc
}

.discussionList .sectionHeaders .main a:hover,.discussionList .sectionHeaders .stats a:hover,.discussionList .sectionHeaders .lastPost a:hover {
color:#fff
}

.discussionListItem {
background:#1e1e1e;
border-bottom:1px solid #000
}

.discussionListItem .posterAvatar,.discussionListItem .stats {
background:#1e1e1e
}

.formPopup {
background-color:#1e1e1e
}

.formPopup .controlsWrapper {
background-color:#1e1e1e;
background:none
}

.heading,.xenForm .formHeader {
color:#fff
}

.mainNavigation .navigationTabs a.active {
color:#fff;
background-color:#424242
}

.message .messageContent,.postDateCount {
background:none;
border-left:1px solid #414141
}

.navPopup .PopupItemLinkActive:hover {
background-color:#373737
}

.nodeList .categoryStrip {
background-color:#1e1e1e
}

.profilePage .mast .followBlocks .primaryContent.avatarHeap {
border:none
}

.redactor_box,.redactor_toolbar {
background:none;
border:none
}

.secondaryContent {
background-color:#1e1e1e
}

.section {
background-color:#1e1e1e
}

.sectionFooter {
background-color:#000;
border:none;
color:#fff
}

.sharePage {
display:none
}

.signature {
max-height:none
}

.subHeading {
color:#fff;
background-color:#373737
}

.tabs {
background:none;
border:none
}

.tabs li a,.tabs.noLinks li {
background:#373737;
border-color:#fff
}

.textCtrl {
border:none
}

.textHeading,.xenForm .sectionHeader {
color:#fff
}

.textWithCount.subHeading .count {
background:none;
border:none
}

.textWithCount.subHeading .text {
color:#fff
}

.threadLabels,.Popup .PopupControl,.submitUnit .button,.PageNav a,a.callToAction,.button {
background-color:#424242;
border:none;
color:#fff;
box-shadow:0 0 10px 0 rgba(0,0,0,0.6)
}

.threadLabels,.Popup .PopupControl:hover,.submitUnit .button:hover,.PageNav a:hover,a.callToAction:hover,.button:hover {
color:#fff;
background-color:#373737;
box-shadow:0 0 10px 0 rgba(0,0,0,0.6)
}

a:link,a:visited {
color:#e6e6e6;
text-decoration:none
}

a:hover,a:focus {
background:none;
box-shadow:none;
color:#f90;
text-decoration:none
}

a[href]:hover {
text-decoration:none
}

.itemPageNav a:hover {
background:none;
color:#f90
}

.ugc a {
color:#d2a564;
text-decoration:none
}

.ugc a:hover,.ugc a:focus {
background:none;
color:#f90;
text-decoration:none;
box-shadow:none
}

body {
color:#ccc
}

footer {
display:none
}

html {
background-color:#232323
}

.new {
background-color:#3A3A3A
}

.unviewed {
background-color:#616161
}

.discussionListItem .itemPageNav {
visibility:visible
}

.discussionListItem .stats dl {
border:none
}

.itemPageNav a {
background:none;
border:none
}

.messageList .message {
border-top:1px solid #414141
}

.message .signature {
border-top:1px solid #414141
}

.quickReply {
border-top:1px solid #000
}

.titleBar h1 {
color:#e24b4b
}

.node .nodeLastPost {
background:none;
border:none
}

.nodeList .categoryStrip .nodeTitle a {
color:#e6e6e6
}

.Menu .secondaryContent {
background:none
}

#DiscussionListOptionsHandle a,.DiscussionListOptions {
border:none
}

.LikeText a {
color:#4b6fe2
}

.LikeText a:hover {
color:#f90
}

ul.autoCompleteList,ul.autoCompleteList {
background-color:#1e1e1e;
border:none
}

ul.autoCompleteList li:hover,ul.autoCompleteList li.selected {
background-color:#373737
}

.message .likesSummary {
background:none
}

.bbCodeBlock .type,.bbCodeBlock pre,.bbCodeBlock .code {
background:#151515
}

.xenOverlay>.section,.xenOverlay>.sectionMain {
background:#1e1e1e
}

#StackAlerts {
background:none
}

.Menu .sectionFooter {
background:#1e1e1e
}

.breadcrumb .crust a.crumb {
color:#e6e6e6;
background:none
}

.breadcrumb .crust a.crumb:hover {
color:#f90;
background:none
}

.breadcrumb .crust:last-child a.crumb {
color:#f90
}

.breadcrumb .crust .arrow {
display:none
}

.discussionList .sectionHeaders dd a[href]:hover {
background:none
}

.PanelScroller .panel,.PanelScrollerOff .panel {
background:#1e1e1e
}

.PanelScroller .scrollContainer,.PanelScrollerOff .panel {
background-color:#1e1e1e;
border:1px solid #000
}

.navPopup .listPlaceholder ol.secondaryContent.Unread {
background-color:#444
}

.profilePage .tabs.mainTabs {
background:none
}

.profilePage .tabs.mainTabs li.active a {
background-color:#424242
}

.tabs li a,.tabs.noLinks li {
color:#ccc
}

#StackAlerts .stackAlertContent {
border:#fff;
background:#424242
}

.PrefixMenu .PrefixGroup h3 {
background:none
}

.prefix {
background:none
}

.threadLabels,.Popup .PopupControl,.submitUnit .button,.PageNav a,a.callToAction,.button {
color:#ccc
}

.messageList .newMessagesNotice {
background:#424242;
border-color:#fff
}

.button.primary {
background-color:#373737
}

.Popup .PopupControl.PopupOpen,.Popup.PopupContainerControl.PopupOpen {
color:#ccc
}

.blockLinksList a:hover,.blockLinksList a:focus,.blockLinksList li.kbSelect a,.blockLinksList label:hover,.blockLinksList label:focus,.blockLinksList li.kbSelect label {
background-color:#373737
}

.searchResult .snippet a {
color:#ccc
}

.searchResult .meta a {
color:#969696
}

.searchResult .meta a:hover,.titleBar h1 em:hover,.mainNavigation .navigationTabs a:hover,.mainNavigation .navLink:hover,.Menu .menuHeader h3:hover {
color:#f90
}

.new {
background:none
}

.titleBar h1 em {
color:#fff
}

.searchResult {
border-bottom:1px solid #000
}

.tabs li.active a,.tabs.noLinks li.active {
background:#424242
}

.tabs li a:hover {
background:#000
}

.redactor_dropdown a {
color:#000
}

.redactor_dropdown a:hover {
color:#f90
}

.xenOverlay .section .heading,.xenOverlay .sectionMain .heading,.sectionFooter {
background-color:#282828;
border:none
}

.xenOverlay .section .messageContainer {
border:none
}

#exposeMask {
background-color:#000!important
}

.profilePage .mast,.profilePage .primaryUserBlock,.profilePage .mainProfileColumn,.profilePage .mast .section.infoBlock {
border:none
}

.profilePage .tabs.mainTabs li a:hover {
background-color:#424242
}

.side_medrec {
display:none
}

.mainContainer {
width:83%
}

.mainContent {
width:100%
}

.sidebar {
width:16%
}

.quick_reply_medrec {
display:none
}

.thread_view .threadAlerts {
background-image:none;
border:none
}

.thread_view .threadAlerts dd {
color:#4c77a2
}

.pollBlock .pollOption label:hover {
background:#373737
}

.ad_placement.ad_leaderboard {
display:none
}

#sugarad-300x250,#sugarad-side300x250,#sugarad-s300x250,#sugarad-halfpage {
display:none
}

.loginButton .inner {
background:none;
background-color:#424242;
color:#ccc;
box-shadow:0 0 10px 0 rgba(0,0,0,0.6)
}

.loginButton {
border:none;
background:none
}

.loginButton .inner:hover {
color:#fff;
background-color:#373737
}

#DiscussionListOptionsHandle a:hover {
color:#4a7db1
}

.textCtrl:focus,.textCtrl.Focus {
background:none;
background-color:#fff
}

#AccountMenu,.Menu.BottomControl {
box-shadow:0 0 10px 0 rgba(0,0,0,0.6);
background-color:#1e1e1e
}

body .itemPageNav a {
background:none;
background-color:#424242;
color:#ccc;
border:none
}

.-mhvfsorZopszg {
background-color:#373737
}

.sidebar .userList,#boardStats {
display:none
}

.sidebar .visitorPanel .avatar img {
padding-right:15px
}
}
