css:
@-moz-document url-prefix("https://jira"), url-prefix("http://jira") {
#logo :hover {
    background-color: #3b73af;
}

#project-name-val, .issue-link, .issueaction-viewworkflow, a, a.switcher-item {
    color: #3572b0;
}

.ajax-activity-content, .aui-button.aui-dropdown2-trigger.tempo-core-mode-links, .dropdown-text, .toolbar-trigger, .trigger-label, a.aui-button, a.aui-button.active, .aui-button.aui-button-subtle.aui-dropdown2-trigger.aui-button-icon, a.switcher-item.active {
    color: #333;
}

.aui-dropdown2-trigger, a.aui-nav-link, a.active, a.aui-nav-link.aui-dropdown2-trigger:hover, a.js-quickfilter-button.ghx-active {
    color: #fff;
}

a.js-quickfilter-button.last.ghx-active, a.js-quickfilter-button.first.ghx-active, a.js-quickfilter-button.ghx-active, .ghx-controls-filters dd a.ghx-active {
    background: #3572b0;
}

#aui-responsive-header-dropdown-trigger-0:hover, .aui-nav-link:hover, .gadget.color1, .dashboard-item-header, .gadget-colors, .color1>a, a#header-details-user-fullname.aui-dropdown2-trigger:hover, a.active, a.aui-dropdown2-trigger.app-switcher-trigger:hover, a.aui-nav-link.aui-dropdown2-trigger:hover, .aui-badge.watch-state-on {
    background-color: #3b73af;
}

.aui-header {
    background: #205081;
}

a.aui-list-item-link {
    background-color: #fff;
}

a.aui-list-item-link:hover {
    background-color: #e6e6e6;
}

a.switcher-item {
    background-color: #F5F5F5;
}

a.toolbar-trigger.js-default-dropdown.active, #work-toggle.active, #plan-toggle.active, #report-toggle.active {
    background-color: #ddd;
}

div.issue-body-content {
    background-color: #fcfbf9;
}

div.ui-draggable {
    display: none;
}

a.ghx-active {
    border-color: transparent;
    color: #fff;
}
}
