css:
@-moz-document url-prefix(www.kafic.net), domain(www.kafic.net)
{
body
{
	font-family: Comic Sans, Comic Sans MS, cursive !important;
	background:#BD1996!important;
}

#header #row1{
	

background: #BD1996!important;
border-right:2px solid #BD1996!important;
border-color:#BD1996!important;
}
	#header #row2{
background: #BD1996!important;
		border-color:#BD1996!important;
border-right:2px solid #BD1996!important;
}
#dockWrapper {
	
	border-top: 1px solid #bd1996!important;
}
#dock {
	

	background: #BD1996!important;
color: #ffffff!important;

}


	#header #row3, #footer{
		background:#BD1996!important;
		border-color:#BD1996!important;
border-right:2px solid #BD1996!important;

}
.submenu li a {
   background:  transparent none repeat scroll 0% !important;
   border: 0pt none !important;
   color:#000000 !important;
   display:block !important;
   float:none !important;
   font-size:10px !important;
   height:auto !important;
   line-height:normal !important;
   margin:0pt !important;
   padding:1px !important;
}

#container
{
background-color:#BD1996!important;
border-color:#333333 !important;
}
.windows-basic-medium, .windows-basic-small, .windows-medium, .panel-basic,
#forum, .forum-post-body, .forum-user-column, .forum_post_right_footer, .forum_post_left_footer,
h3, #forum table, .codetop, .htmltop, .quotemain, .windows-basic,
.windows-small
{
	color:#ffffff!important;
	background-color:#BD1996!important;
	
}

.windows-forum{
	background: #BD1996 none repeat scroll 0% !important;
	padding: 3px 3px 1px !important;
}

.panel-basic{
	color: #ffffff!important;
	background: #BD1996!important;
	border:1px solid #ffffff!important;
}

.panel-forum{
	background-color: #BD1996!important;
	border:0px solid #ffffff !important;
}

.win-head h1{
	color:#BD1996!important;
}

.ul_list a{
	background-color: #bd1996!important;
	border-color: #ffffff!important;
}

.ul_list a:hover, .ul_list .current_link a{
	background-color: #bd1996!important;
}

.calendar a, .calendar span{
	color: #ffffff !important;
}


#messages_table .new_msg{
	background: #353535 !important;
	border:2px solid #353535 !important;
}


.panel-user-guestbook{
	border: 2px solid #ffffff!important;
	background-color: #bd1996!important;
}

.top_bar{
	background: #bd1996!important;
	border-bottom:2px solid #ffffff!important;
}



#forum table{
	background-color: #bd1996!important;
	
	width:100% !important;
	border: 2px solid #ffffff!important;
	border-bottom: none !important;
}

#forum td{
	border-bottom:2px solid #bd1996!important;
}

#forum .forum-post-column{
	background-color:#bd1996!important;
	padding:4px !important;
}

.forum-post-body{
	color: #ffffff!important;
	font-size: 12px !important;
}

#forum .forum_post_left_footer{
	border-right:0px solid #bd1996!important;
}

.forum-user-column{
	border-right:0px solid #bd1996!important;
}

#forum .main-topic{
	color:#ffffff!important;
	background-color:#bd1996!important;
	border:2px solid #ffffff!important;
}


.forum-sub th{
	background-color:#bd1996!important;
	border-bottom:2px solid #ffffff!important;
}

.quotetop, .codetop, .htmltop {
	background-color: #bd1996!important;
	border-color: #ffffff!important;
	color: #ffffff!important;
}

.quotemain, .codemain, .htmlmain {
	background-color:#bd1996!important;
	border-color:#ffffff!important;
	color:#ffffff!important;
}

.thumb_img .title{
	background-color: #dc1996!important;
        float: left !important;

}
.windows-basic h5 { 
background-color: #bd1996!important;

}
.quotetop, #comments .top_bar
{
	border-color:#ffffff!important;
	background-color:#000000!important;
	font-size:12px !important;
	font-weight:600 !important;
	color: #ffffff;
}
.top_bar small
{
	color:#ffffff !important;
}
.quotemain
{
	border-color:#ffffff!important;
	border-left-width: 2px !important;
	background-color:#dc1996!important;
}
h1.title, h2.title, h3.title, h4.title, h5.title, h5, h1.title a:link, h2.title a:link,
h3.title a:link, h4.title a:link, h5.title a:link, h1.title a:visited, h2.title a:visited,
h3.title a:visited, h4.title a:visited, h5.title a:visited, h2 a
{
	color: #ffffff!important;
}
.forum-sub th, .forum-latest-topic a
{

	color: #ffffff!important;
}
h2, h5
{
	color:#ffffff!important;
        background:#bd1996!important;
}
#liveboard .panel-basic, .panel-basic, .selector
{
	background:none !important;
}
a:link.user_male, a:visited.user_male
{
	color:#3399FF !important;
}
a:link.user_female, a:visited.user_female
{
	color:#FF3333 !important;
}
.table_info .odd, .odd{
	background:#bd1996!important;
border-color:#ffffff!important;
}
.border_menu .icon a:hover, .border_menu .icon.current_link a, .border_menu .icon.current_link a:link, .border_menu .icon.current_link a:visited
{
		background-color:#bd1996!important;
               color:#ffffff!important;
                
}
.ul_list a
{
	color:#ffffff!important;
	background-color:#bd1996!important;
	border: 1px solid #ffffff!important;
}
.ul_list a:hover, .ul_list .current_link a
{
	color:#c9c9c9!important;
	background-color:#bd1996!important;
}
.table_info a:link, .table_info a:visited
{
	color:#bd1996!important;
}
#top_options h3
{
	color: #c9c9c9!important;
	background:#bd1996!important;
}
#online_list
{
	background-color: #bd1996!important;
	color: #ff0000!important;
border-bottom:2px solid #fc0000!important;
}


#live_online {



border-right:1px solid #ff0000!important;
	border-bottom:2px solid #ff0000!important;
	border-top:0px solid #ff0000!important;
border-left:1px solid #ff0000!important;
}
#broadcast_container
{
	font-size: 12px !important;
	font-weight:600 !important;
	background-color:#bd1996!important;
	color: #ffffff!important;
}
#video_panel img
{
	border: none !important;
}
.bordit
{
	border:1px solid #bd1996!important;
	padding:1px !important;
}
.border_menu div, .border_div, tr.border_div td
{
	border-bottom: 1px solid #bd1996!important; 
}
.panel-basic .option
{
	background-color:#bd1996!important;
	color:#ffffff!important;
border:1px #ff0000 solid !important;
}
#blog_panel h2, #blog_panel h3, #blog_panel a, .panel-basic h4
{
	color:#ffffff!important;
}
#footer
{
	visibility:hidden !important;
}
.forum-latest-topic h2
{
	font-size:14px !important;
	font-weight:600 !important;
}
#context_menu{
	margin: 0 !important; 
	border-left:1px solid #BD1996!important;
	border-right:0px solid #000000 !important;
	border-bottom:0px solid #000000 !important;
	border-top:1px solid #ffffff!important;
	background-color:#ffffff!important;
/*	padding:3px;	*/

}

#middlebar .menu li a:hover, .paginator span strong, .paginator a.current {

	background:#bd1996!important;

color: #ffffff!important;

}
}
