css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp('http://cutsberry.com/random.*') {
  body,html {
  background-image: url(data:image/png;base64,);
  margin: 0;
  
  }
  
  #vk,.link,.share_rand {
  display:none;
  }

  #Rpanel,.share_box {
  opacity:0;
  transition-property: opacity;
  transition-duration: 0.2s;
  }

  #videobcg {
  max-height:100%;
  max-width:100%;
  box-shadow: 0px 0px 15px #000;
  }

  #random_body {
  width:0;
  }

  .share_box {
  margin-left: calc(-67px);
  background:none;
  border-radius:0;
  width:148px;
  padding:0;
  opacity: 0;
  }

  .share_box:hover {opacity: 0.5;}
  
  #rand_btn {
  margin:0;
  }
}
