css:
@-moz-document url-prefix("http://atelier801.com/") {
  	body {background: url('http://www.transformice.com/wp-content/themes/Themice/images/fond.jpg') top repeat-x #1e1915;}
    .cadre-type-auteur-joueur{ color: #DDB290; text-shadow: none; }
    .cadre-sujet-titre-sombre{ color: #B0B0C0; margin-left: 3px; margin-right: 3px;  }
    .ligne-section-actu{ background-color: rgba(100, 81, 66, 1); border: 1px #887261 dashed; }
    .table-section{ width: 100%; background-color: #6F5D4F; border: 1px #887261 dashed; }
    .cadre-forum{ background-color: rgba(60, 46, 29, 0.41); padding-top: 5px; margin-bottom: 10px; height: 100%; }
    .cadre-sujet-date{ font-size: 14px; color: #97A2EA; }
    .footer{ background-color: #574433; color: #B0A8A0; box-shadow: 1px -3px 3px #2D241B; border-top:3px #261F18 solid; padding-top: 6px; }
    .cadre-sujet{ background-color: rgba(77, 58, 42, 1);vertical-align:middle;margin-bottom:10px; }
    .cadre-relief{ border: 3px #745F4F dashed; }
    .cadre-message{ background-color: rgba(79, 90, 27, 1); margin-bottom: 10px; padding-bottom: 5px; padding-left: 10px; overflow: visible; border: 3px #3A490F dashed; opacity: 0.8; transition: 500ms linear; -moz-transition: 500ms linear; -webkit-transition: 500ms linear; }
	.cadre-sondage{ background-color: rgba(79, 90, 27, 1); margin-bottom: 10px; padding-bottom: 5px; padding-left: 10px; overflow: visible; border: 3px #3A490F dashed; opacity: 0.8; transition: 500ms linear; -moz-transition: 500ms linear; -webkit-transition: 500ms linear; }
    .cadre-message:hover{ opacity: 1; }
	.cadre-sondage:hover{ opacity: 1; }
    .cadre-repondre{padding-top:10px;background-color: rgba(63, 79, 16, 0.46);overflow-x:auto;border: 0px;border: 3px #21270C dashed;}
    .lien-blanc{ color: #E7E7ED; text-decoration: none; }
	.cadre-jeu{ background-color: rgba(56, 46, 38, 0.73); overflow: hidden; margin-bottom: -10px; height: 100%; }
    .cadre-conversation{ background-color: rgba(56, 46, 38, 0.73); margin-bottom: 10px; }
	hr{ height: 0; border-top: 1px solid rgba(0, 0, 0, 0.49); border-bottom: 1px solid #5E6E22; }
    .cadre-quote{ background-color: #27270E; font-size: small; padding: 5px; margin: 1px; }
    .cadre-quote-inverse{ background-color: #1F1F0A; font-size: small; padding: 5px; margin: 1px; }
    .cadre-message-modere{ background-color: rgba(82, 43, 43, 1); border: 3px #A64545 dashed; opacity: 1; }
    .cadre-utilisateur{ background-color: rgba(42, 32, 28, 0.77); margin-bottom: 10px; border: 3px #73513C dashed; } 
}
