css:
@-moz-document url-prefix("http://twinoid.com/g/le-choc-les-habitants"), url-prefix("http://www.hordes.fr/tid/forum") {

/* Balise Double Italiques */

.tid_editorContent em em {
  color: #2BFAFA !important;
  text-shadow: 0px 0px 10px #2BFAFA !important;
}

/* Balise Italiques */

.tid_editorContent em {
  color: #2BFAFA !important;
  text-shadow: 0px 0px 10px #2BFAFA !important;
}

/* Balise Gras : **Texte** */

.tid_editorContent strong {
  color: red !important;
  opacity: 1 !important;
  text-shadow: 0px 0px 10px red !important;
}

/* Pour le forum twinoid */
#tid_forum .tid_bg1 { background-color:#965c36 !important; }
#tid_forum .tid_border1 { border-color:#965c36 !important; }
#tid_forum .tid_bg1 { color:white !important; }
#tid_forum .tid_bg2 { background-color:#4a261e !important; }
#tid_forum .tid_border2 { border-color:#4a261e !important; }
#tid_forum .tid_bg2 { color:white !important; }
#tid_forum .tid_bg3 { background-color:#4a261e !important; }
#tid_forum .tid_border3 { border-color:#4a261e !important; }
#tid_forum .tid_bg3 { color:#eba475 !important; }
#tid_forum .tid_bg4 { background-color:#321914 !important; }
#tid_forum .tid_border4 { border-color:#321914 !important; }
#tid_forum .tid_bg4 { color:white !important; }
#content { background-color: #7e4d2a; background-image: url(http://image.noelshack.com/fichiers/2015/25/1434720685-azerthj.png); background-repeat: repeat-x;
}
.scontainer.forum .tid_editorContent {
  color: white;
}

.scontainer.forum .tid_editorContent a {
  color: #ddab76;
}
.scontainer.forum .tid_editorContent a:hover {
  color: white;
}
/* Balisse RP */

.tid_editorContent .tid_roleplay .tid_wroleplay {
display: block;
margin-top: -10px;
padding-top: 10px;
background-repeat: no-repeat;
background-image: none;
background-position: 5px top;
}

.tid_editorContent .tid_roleplay {
display: block;
margin-top: 7px;
margin-bottom: 15px;
margin-left: 20px;
max-width: 350px;
padding: 3px;
color: white;
font-style: italic;
font-size: 11pt;
border-radius: 6px;
-moz-border-radius: 6px;
-webkit-border-radius: 6px;
background: #321914;
border: 1px solid #965C36;
border-top-color: #965C36;
-moz-box-shadow: 0px 0px 2px #965C36;
-webkit-box-shadow: 0px 0px 2px #965C36;
box-shadow: 0px 0px 2px #965C36;
}

.tid_editorContent .tid_preRoleplay {
display: block;
margin-top: 7px;
margin-left: 10px;
padding: 0px 5px;
padding-left: 20px;
max-width: 200px;
height: 18px;
overflow: hidden;
font-weight: bold;
font-size: 8pt;
font-style: italic;
line-height: 18px;
background-image: url("http://i.imgur.com/Jcw6oVb.png");
background-repeat: no-repeat;
background-position: 1px 0px;
}

.tid_editorContent .tid_roleplay .tid_wroleplay strong {
color: red !important;
}

#tid_forum .tid_postForm textarea {
width: 95%;
resize: vertical;
min-height: 250px;
background: #4A261E;
color: white;
}

/* Balise modération */

.tid_editorContent .tid_mod {
  background-image: url("http://data.twinoid.com/img/lang/fr/modAnnounceBg.png")!important;
  background-color: #bd3d00!important;
}
/* Balise Mod */

.tid_editorContent .tid_mod {
  background-position: 5px 5px !important;
  background-color: #290000 !important;
  color: red !important;
  text-shadow: 0px 0px 10px red !important;
}

.tid_editorContent .tid_mod {
  background-color: #290000 !important;
}

/* Agrandissement du div de pagination */
		#tid_forum .tid_forumThread .tid_actionBar .tid_pages {
		width: 248px !important;
	}
	/* Alignement divers */
	#tid_forum .tid_actionBar .tid_buttonBar {
		margin: 0px !important;
		margin-left: 1px !important;
		margin-top: -2px !important;
	}

	/* "bouton" de choix de page */
	.tid_cur.tid_tip.tid_parsed {
		background-color: transparent !important;
		background-image: url(http://i.imgur.com/FmfEiKZ.png) !important;
		border: 1px solid #98341c !important;
  		border-bottom: 1px solid black !important;
		margin-top: auto !important;
		color: #f0d79e !important;
        opacity: 1000 !important;
	}
	.tid_cur.tid_tip.tid_parsed:hover {
		border: 1px solid #98341c !important;
		border-top: 2px solid black !important;
		border-bottom: 0px solid #98341c !important; 
		outline: 1px solid #f0d79e !important;
		color: white !important;
	}

	/* Bouton de pagination et Répondre / suivre / Forum */
	.tid_buttonBar a {
		background-image: url(http://i.imgur.com/FmfEiKZ.png) !important;
		border: 1px solid #98341c !important;
        border-bottom: 1px solid black !important;
		margin-right: 1px !important;
		color: #f0d79e !important;
        opacity: 100 !important;
	}
	
	/* Effet "bouton appuyé" */
	.tid_buttonBar a:hover {
		opacity: 0.99 !important;
		border: 1px solid #98341c !important;
		border-top: 2px solid black !important;
		border-bottom: 0px solid #98341c !important; 
		outline: 1px solid #f0d79e !important;
		color: white !important;
	}
	/* Ne pas faire l'effet "bouton appuyé" au OFF */
	.tid_buttonBar .tid_off:hover {
		border: 1px solid #7c5a50 !important;
		opacity: 1 !important;
		outline: 1px solid #421d15 !important;
		color: #dddbd8 !important;
	}
	/* Gestion de l'affichage des boutons en "Off" */
	.tid_buttonBar .tid_off {
		background-image: url(http://i.imgur.com/FmfEiKZ.png) !important;
		border: 1px solid #7c5a50 !important;
		opacity: 1 !important;
		outline: 1px solid #421d15 !important;
		color: #dddbd8 !important;
	}
	.tid_buttonBar a.tid_off img {
		opacity: 0.4 !important;      
	}
	/* Gestion des boutons "Répondre" */
	.tid_postForm button {
		background-color: transparent !important;
		margin-top: 4px !important;
		background-image: url(http://i.imgur.com/FmfEiKZ.png) !important;
		border: 1px solid #98341c !important;
		outline: 1px solid black !important;
		color: #f0d79e !important;
		-moz-box-shadow : none !important;
		-webkit-box-shadow : none !important;
		box-shadow : none !important;
	}

	.tid_postForm button:hover {
		opacity: 0.99 !important;
		border: 1px solid #98341c !important;
		border-top: 2px solid #98341c !important;
		border-bottom: 0px solid #98341c !important;  
		outline: 1px solid #f0d79e !important;
		color: white !important;
		padding: 2px 20px !important;
		-moz-box-shadow : none !important;
		-webkit-box-shadow : none !important;
		box-shadow : none !important;
	}
	#tid_forum .tid_mainBar .tid_actionBar {
		opacity: 1000;
	}
}
