css:
@-moz-document domain("jeuxvideo.com") {
#header-bottom.affix .bloc-sticker
   {
     position: static;
   }
  
@-moz-document url-prefix(http://www.jeuxvideo.com/forums/)
  {
   .container 
    {
    min-width: 980px;   
    width: 70%;
    } 
   #page-profil .container 
    {
    width: 980px;
    } 
  }
#content
  {
    background-image: url("http://image.jeuxvideo.com/medias/142494/1424938919-1233-logo.jpg");
    background-color: black;
  }
.panel-jv-forum > .panel-heading 
  {
    background-color: #C00000;
  }
  .heading-pres1 
  {
    background: #C00000 none repeat scroll 0% 0%; 
  }

.btn-epingle-topic
  {
    border: 1px solid #70a13c;
    background: #8cca4a none repeat scroll 0% 0%;
  }
.btn-epingle-topic:hover
  {
    border: 1px solid #70a13c;
    background: #8cca4a none repeat scroll 0% 0%;
  }
  .btn-lock-topic
  {
    border: 1px solid #C00000;
    background: #e60303 none repeat scroll 0% 0%;
  }
   .btn-lock-topic:hover
  {
    border: 1px solid #C00000;
    background: #e60303 none repeat scroll 0% 0%;
  }
    .btn-select-msg-modo
  {
    border: 1px solid #CB6403;
    background: #F0A100 none repeat scroll 0% 0%;
  }
  .btn-topic-resolu
  {
    border: 1px solid #247a2e;
    background: #298c35 none repeat scroll 0% 0%;
  }
  .btn-topic-resolu:hover
  {
    border: 1px solid #247a2e;
    background: #298c35 none repeat scroll 0% 0%;
  }
  #table-liste-topic-forum tbody tr.topic-delete
  {
    background: #D6C6F0 none repeat scroll 0% 0%;
  }
  .bloc-message-forum.msg-supprime 
  {
    background: #E9DFF9 none repeat scroll 0% 0%;
    border: 1px solid #D6C6F0;
  }
  .bloc-message-forum.msg-supprime .bloc-outils-modo 
  {
    background: #D6C6F0 none repeat scroll 0% 0%;
  }
  .reponse-commentaire .bloc-message-forum.msg-supprime-gta, .bloc-message-forum.msg-supprime-gta 
  {
    background: #bcd5a1 none repeat scroll 0% 0%;
    border: 1px solid #548321;
  }
  #btn_moderation_delete_message
  {
    background: #e60303 none repeat scroll 0% 0%;
  }
  
  #table-liste-topic-forum .auteur-topic .text-modo, .bloc-message-forum .text-modo, .bloc-avis .text-modo, .form-control-tag-inner .text-modo, .list-msg .text-modo, .text-modo
  {
    color: #DB0F0F;
  }
  #table-liste-topic-forum .auteur-topic .text-admin, .bloc-message-forum .text-admin, .bloc-avis .text-admin, .form-control-tag-inner .text-admin, .list-msg .text-admin, .text-admin
  {
    color: #8e3eb7;
  }
  
  *[src="/img/forums/topic-supp-gris.png"] {
	height: 13px !important;
	width: 13px !important;
	padding-bottom: 16px !important;
	background:url(http://image.noelshack.com/fichiers/2015/26/1435177612-supp-13.png) !important;
  background-repeat: no-repeat !important;
}/* Change le bouton de suppression */ 

  *[src="/img/forums/topic-revert.png"] {
	height: 13px !important;
	width: 13px !important;
	padding-bottom: 16px !important;
	background:url(http://image.jeuxvideo.com/pics/forums/bt_forum_visible.gif) !important;
  background-repeat: no-repeat !important;
}/* Change le bouton de suppression */   
  
  *[src="/img/forums/topic-dossier1.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418128632-dossierjaune-jvshack.gif) !important;
}/* Change les dossiers jaunes des topics */

*[src="/img/forums/topic-dossier2.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 13px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129151-dossierrouge-jvshack.gif) !important;
}/* Change les dossiers rouges des topics */

*[src="/img/forums/topic-marque-on.png"] {
	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129272-marque-on-jvshack.gif) !important;
}/* Change l'épingle verte des topics */

*[src="/img/forums/topic-marque-off.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418129274-marque-off-jvshack.gif) !important;
}/* Change l'épingle rouge des topics */

*[src="/img/forums/topic-lock.png"] {
  	height: 10px !important;
	width: 16px !important;
	padding-bottom: 14px !important;
	background:url(http://image.noelshack.com/fichiers/2014/50/1418303551-cadenas-jvshack.gif) !important;
}/* Change le cadenas des topics */

}
