css:
@-moz-document domain("allrad-lkw-gemeinschaft.de") {


body { padding: 0 30px; background-color: #f9f9f9 !important; font-family: "Open Sans", sans-serif !important; font-weight: 400 !important;}

    a:link { text-decoration: none !important; color: #028BD0 !important; }
    a:visited { color: #777 !important; }
    a:hover, a:focus, a:active { color: #111 !important; border-bottom-color: #111 !important; text-decoration: underline !important; }

    form[name="viewtopic"] { background-color: #ddd !important; padding: 2px; margin-bottom: 20px; }

    table th { color: #fff !important; background-color: #028BD0 !important; background-image: none; }
    td { padding: 8px 0px !important;}
    th a, th a:visited { color: #fff !important; text-decoration: underline !important; }

    #pageheader { margin-top: 60px; }
    #pagecontent { margin-bottom: 60px; }
    #menubar { background-color: transparent; padding: 5px; margin-top: 2px; }
    #logodesc { display: none; }
    #pagecontent .tablebg tbody tr.row1:first-child, #pagecontent .tablebg tbody tr.row2:first-child {  background-color: transparent; padding: 0px !important; }
    #wrapfooter { margin-bottom: 20px; }
    #wrapfooter .copyright { display: none; }
    #wrapcentre { margin-top: 5px;}
    #datebar { display: none; }
    
    .tablebg { background-color: transparent; }
    
    .row1, .row2, .row3 { background-color: transparent; padding-bottom: 8px!important;}
    .profile .postdetails { font-size: 10px; color: #aaa !important; }
    .cat, .catdiv { background-color: transparent; background-image: none; }
    .cat form { background-color: #ddd; padding: 2px; border-top: 2px solid #028BD0; margin-bottom: 30px;}
    
    .quotetitle { border-bottom: none!important; }
    .quotetitle, .quotecontent { background-color: transparent; color: #888; border: 1px dashed #aaa; }
    .attachcontent,
    .attachtitle { border: none; background: none; }
    td.spacer { height: 0px; border-bottom: 2px solid #028BD0; padding: 0px; padding-top: 50px !important; background-color: transparent; }
    td.spacer img { display: none; }
    td.profile a { color: #777 !important; }
    .profile img { border-radius: 6px; }
    a.postlink { font-weight: normal; text-decoration: underline !important; background: transparent; border: none; }
    a.topictitle, .titles { font-family: inherit; font-size: 14px;  font-weight: 400 !important; }
    p.datetime { display: none; }
    .topicdetails { white-space: nowrap; }
    td.postdetails, .postbody { font-size: 14px; line-height: 22px; color: #444; padding-top: 20px!important; font-family: inherit !important; font-weight: 400 !important; }
    .username-coloured {font-weight: 400!important; }

}
