css:
@-moz-document domain("deviantart.com") {
/* NewdArk CSS */
#browse-sidemenu, .browse-left-bar-inner{background-color:#2a2a2a!important;}
.browse-facet a.selected{background-color:#606060!important;color:#9cb01c!important;}
.dev-view-about-content{color:#9cb01c;}
.dev-description{margin-bottom:30px;}
.h.cc-without-moods{margin-top:25px;}
.avatar{margin-top:5px;margin-left:3px;border-radius:5px!important;-webkit-border-radius:5px!important;-moz-border-radius:5px!important;}
.gmbutton2.reply{margin-bottom:5px;margin-left:5px;}
.pagination a{color:#606060!important;}
.dev-page-below-tabs{padding:8px;padding-top:15px;padding-bottom:0px;background-color:#202020!important;border-radius:10px!important;-webkit-border-radius:10px!important;-moz-border-radius:10px!important;}
.ccomment-form{padding-bottom:5px!important;}
.social a, .social a:hover{color:#0a880a!important;text-decoration:none!important;}
.social, .dev-right-col-bg, .details-section, .dev-view-meta-content, .dev-view-meta, .details-section.deviation-share.hh.deviation-share-new{background-color:#475c4d!important;}
.popup2.popup2-browse-mature-popup{background:none!important;}
#joinControl .checks{padding-top: 25px!important;}
#output{background-image:none!important;}
#join > img{background-color:#fafffa;margin-left:18px;padding-top:7px;padding-left:14px;border-radius:10px!important;-webkit-border-radius:10px!important;-moz-border-radius:10px!important;}
.bubbleview.h.login-page{background-color:#101010!important}
#login{background-color:#637768!important;}
.ieh.bubbleview{background-color:#101010!important}
.shinyheader.with-sub{color:#94ae13!important;}
h1{color:#a8c04a!important;}
.headercontent .headlines{z-index: 2!important;background: -webkit-linear-gradient(#637768, #101010, #637768)!important;height: 74px!important;position: absolute!important;width: 99%!important;margin-top: -12px!important;padding-top:8px;padding-left:10px;left:0;}
.headercontent{margin: 0 auto!important;position: relative!important;-moz-box-sizing: border-box!important;-webkit-box-sizing: border-box!important;box-sizing: border-box!important;z-index: 1!important;height: 57px!important;}
.headercontent .headerlinks{z-index:3!important;}
.headercontent:after{z-index:-1!important;}
.grid-thumb.uniform{margin:30px 60px 60px 60px!important;}
.tt-crop.lit{margin:40px!important;}
.tt-crop.tt-a.thumb{box-shadow:none!important;position:static!important;}
.grid-thumb.square{margin:10px 10px 10px 10px!important;}
.grid-layout.grid-layout-16{max-height:none!important;overflow:hidden!important;}
.grid-collection .info a{color:#637768;}

/* CSS from deviantDARKv2.6 */
a:hover, .tabInactive:hover, a.a:hover{color:#acca1c!important;text-decoration:underline!important;}
a, .tabInactive, a.selected:hover{color:#94ae13!important;text-shadow:0 0 0 transparent!important;text-decoration:none!important;}
h2{color:#818181!important;}
.gruze, #output, #depths, #submit{background-color:#101010!important;}
#gruze-columns, .browse2 td.f{background-color:#151515!important;}
#top, #output #deviant, #deviation, #deviation-links, #deviation-links strong, .browse2-results, #bubbleview-messages, .messages-right, .full .f, .altaltview, .mcbox-preview-journal .ch-ctrl{background:#1f1f1f none!important;text-shadow:0 0 0 transparent!important;color:#808080!important;}
#deviation-links *{background:transparent none!important;}
.bubbleprefix{display:none!important;}
.catbar, .messages-head{background: #5e5e5e url(http://trezoid.webs.com/deviantDARKv2/gradientgray1.jpg)repeat-x top left!important; color:#818181!important;border-color:#1f1f1f!important;}
.messages-head *{color:#808080!important;}
.gr-genericbox .gr-top .gr, .gr-genericbox .gr-top, .gr-grouplistbox .gr-top .gr, .gr-grouplistbox .gr-top, .gr-top-alone .gr, .gr-top-alone, .mczone-title, .hh, .messages-left, .mcbox-list-generic .ch-ctrl{background-color:#1f1f1f!important;border-color:#212121!important;-moz-border-radius-topleft:5px!important;-moz-border-radius-topright:5px!important;}
i.tri, i.mm{background:transparent url(http://trezoid.webs.com/deviantDARKv2/triforce2.gif)!important;}
.gr-configbox i.tri{background-position:0px 60px!important;}
.gr-body, .mczone, .mczone-inner, .page2, .dev-page-container{background:#2a2a2a none!important;border-color:#616161!important;}
.gr-body .gr{border-color:#616161!important;}
.gr-box i.gr1, .gr-box i.gr2, .gr-box i.gr3{display:none!important;}
ul.sitemap a i, a.sitemap i{
position: absolute;
width: 30px;
margin-top: -2px;
margin-right: 0pt;
margin-bottom: 0pt;
margin-left: -35px;
height: 160px;
background-image:url(http://trezoid.webs.com/dAnu.gif)!important;
background-color:transparent!important;
}
a.sitemap-surfer, #moremenu a, ul.sitemap li, .page2 a.f {background:transparent none!important;}
#moremenu a:hover{background-color:#94ae13!important; color:#141414!important;}
.gtab-selected, i.gtab-i, .gtabi-mc  {background-image:url(http://trezoid.webs.com/deviantDARKv2/tab.gif)!important;border-width:0!important;}
.blues-bar, .blues-bar-ctrl, .blues-bar-ctrl *{background-image:url(http://trezoid.webs.com/deviantDARKv2/ctrlbar.gif)!important;border-width:0!important;}
body .blues-bar span.bb a.gmbutton2, body .blues-bar span.bb a.gmbutton2 b, body .blues-bar span.bb a.gmbutton2 span.bluestip {background-image:url(http://trezoid.webs.com/deviantDARKv2/megabutton.png)!important;}
.gmbutton:active{background-position:0 -26px!important;}
li.f.a, .page2 a.f.selected{background-color:#404040!important;color:#1f1f1f!important;}
li.f{background-color:#202020!important;color:#808080!important;}
.cfill{background-image: url(http://trezoid.webs.com/deviantDARKv2/dawgj.gif)!important;}
.ctube{background: url(http://trezoid.webs.com/deviantDARKv2/toobs.gif)!important;-moz-border-radius:3px!important;}
.ctube i{display:none!important;}
.talk-tower{margin-top:-15px!important;background-color:#202020!important;}
.ccomment .ch-ctrl, .mcbox-full-comment .mc-ctrl, .mcbox-full-reply .ch-ctrl{background-color:#303030!important;border-width:0!important;-moz-border-radius:5px!important;}
.ctext .ch-ctrl, .talkmessage{background:#202020 none!important;border-width:0!important;color:#808080!important;}
.ccomment i{display:none!important;}
.nav2 a, .nav2 strong, .nav2 del {background-image:url(http://trezoid.webs.com/deviantDARKv2/pagenation.png)!important;border-color:#202020!important;}
#ddtable, tr.alt,.rr, .rr * {background:#242424!important;color:#808080!important;}
#dailyDevTopNav, #dailyDevs, #browseNavbar, #heybox *, #facetMenu-Browse{background:#2a2a2a none!important;border-color:#2a2a2a!important;color:#808080!important}
#facetMenu-Category{background:#3a3a3a!important;border-color:#202020!important;}
.noncollapseHeader, .header{background:transparent none!important;text-shadow: 0 0 0 transparent!important;color:#808080!important;}
.tt-a{padding:40px!important; padding-bottom:60px!important;}
.sliding-menu  ul li a:hover, #facetMenu-Browse li a:hover{background-color:#202020!important;}
#heybox{border-width:0!important;}
a.lit{color:#1f1f1f!important;}
.tabActive{color:#498091!important;text-shadow:0 0 0 transparent!important;text-decoration:none!important;}
.tabActive:hover{text-decoration:none!important;}
.mcb-midview .tt-a{padding:5px!important;}
.gallery-stream-row{height: 300px!important;}
#gmi-ResourceTV .tt-a{padding:3px!important;}
#footerThumbData{height:300px!important;}
.popup2, * .gr-body .gr .pbox{background:#404040!important;color:#808080!important;}
.galleryswitch{background:#202020!important;}
.gm-controls *{opacity:0.2!important;}
.gm-controls *:hover{opacity:0.7!important;}
.gr-headless .gr-body .gr i{display:none!important;}
.info_module_sidebar *{color:#1f1f1f!important;}
.details h3{background:#404040 none!important;text-shadow:0 0 0 transparent!important;}
}
