css:
@-moz-document url-prefix("http://perfectworld.vanillaforums.com/") {
/* apply new main font color (grey) */

a {
    color: #4e7edb;
    text-decoration: none;
}

a:hover {
  color: #ccc;
}

.WarningMessage {
    background: #4e7edb none repeat scroll 0 0;
    border: 1px solid #ccc;
}

.UserBox .Username, .MeBox .Username {
    font-size: 20px;
}

.MeButton {
    font-size: 22px;
    padding: 0 10px;
}

.CategoryTable .ChildCategories {
    font-size: 13px;
}

#PagerAfter {
    display:block !important;
}

div.UserSpoiler {
  background-color: #333;
}

.QnA-Tag-Answered, .QnA-Tag-Accepted, .QnA-Accepted {
    background: #090 none repeat scroll 0% 0%;
    color: #FFF;
}

.Tag-Closed {
    background: #900 none repeat scroll 0% 0%;
    color: #FFF;
}

.QnA-Tag-Question {
    background: #fba000 none repeat scroll 0% 0%;
    color: #000;
}

.Tag-Announcement {
    background: #4e7edb none repeat scroll 0% 0%;
    color: #FFF;
}

.ItemDiscussion > td:first-child:hover, .Comment > td:first-child:hover, .Activities > .Activity > td:first-child:hover, .MessageList > li > td:first-child:hover, .Item-Search > td:first-child:hover {
    border-left: 3px solid #4e7edb;
}

.ProfilePhotoSmall {
    display: none;
}

.DataTable .Title {
    font-size: 16px;
    font-weight: normal;
}

tbody td.BigCount {
    color: #777;
    font-size: 14px;
}

.BlockTitle {
    font-size: 13px;
    font-weight: normal;
}

.DataTable .FirstUser {
    text-align: right;
}

.Pager {
    font-weight: normal;
}

.DataTable .BlockColumn-User {
    width: 12%;
}

.UserLink {
    color: #fff;
}

.DiscussionsTable td.BigCount {
    width: 55px;
}

.Item .Author a {
    font-family: arial;
}

.CommentHeader .ProfilePhotoMedium, .DiscussionHeader .ProfilePhotoMedium {
    height: 75px;
    width: 75px;
}

.CommentHeader .PhotoWrap.Online .ProfilePhoto, .DiscussionHeader .PhotoWrap.Online .ProfilePhoto {
    border-bottom: 3px solid #0c0;
}

.CommentHeader .PhotoWrap.Offline .ProfilePhoto, .DiscussionHeader .PhotoWrap.Offline .ProfilePhoto {
    border-bottom: 3px solid #c00;
}

.CommentHeader .AuthorTitle {
    display: block;
}

.AuthorTitle {
    color: #fba000;
}

.MItem.Category {
    display: block;
}

.MItem {
    margin-left: 0px;
}

.DiscussionHeader .DiscussionMeta {
    margin-bottom: 20px;
    border-bottom: 1px solid #666;
    padding-bottom: 20px;
}

.DateCreated {
    font-size: 13px;
}

.ProfilePhoto {
    border: 0px solid #000;
    border-radius: 6px;
    -moz-border-radius: 6px;
    -webkit-border-radius: 6px;
}

.DataList .Item {
    background: #222 none repeat scroll 0 0;
    border: 1px solid #333;
}

blockquote.Quote {
    background: rgba(0, 0, 0, 0.3) none repeat scroll 0 0;
    border-left: 6px solid rgba(0, 0, 0, 0.3);
    color: #999;
}

.ReactSprite {
    background-image: url("http://perfectworld.vanillaforums.com/applications/dashboard/design/images/sprites-14-ffffff.png") !important;
}

.SiteSearch .InputBox {
    font-size: 9px;
}

#Form_Search {
    background-color: #333;
}
#Form_Search {
    border: 1px solid #666;
    color: #ccc;
}

.Bookmark::before {
    color: #777;
}

a.Bookmarked::before {
    color: #fba000;
}

.OptionsTitle, a.Bookmark, a.Bookmarked, a.Bookmarking {
    font-size: 22px!important;
    height: 22px!important;
    width: 22px!important;
}

div.PostEdited {
    color: #c00;
    font-size: 85%;
}

.UserSignature {
	border-top: 1px solid #333;   
}

#Head a {
    color: #fff;
    font-weight: normal;
}

.SiteMenu a {
    font-size: 13px;
}

code, pre {
    background: #444;
}

}
