css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("inoreader.com") {
  #sitecontent > #consent_bbb,
  .block_article_ad.leaderboard_ad.ad_size_leaderboard.sinner,
  #dash_2 > DIV:first-child,
  #sinner_container,
  .ar.article.ar_showed.ar_ad.ar_skip.article_tile.ar_fixed_width.radius.ad_stripe,
  #sb_rp_upgrade{display:none!important;}
  
  #all_read_message{left:auto !important;}

  #sitecontent{z-index: 0 !important; position:absolute !important;height:auto!important;top:0px!important;bottom:0px!important;width:auto!important; left:0px !important; right:0px !important;}
  
      #splitter{position:absolute !important;height:auto!important;top:0px!important;bottom:0px!important;width:auto!important;right:0px !important;left:0px !important;} 
  
          #tree_pane{position:absolute !important;height:auto!important;top:0px!important;bottom:0px!important;} 
  
          #reader_pane{position:absolute!important;padding-right: 0px !important;
                       height:auto!important; top:40px!important;bottom:0px!important;
                       overflow-y:scroll!important;
                       width:auto!important;right:0px !important;}
  
              #reader_pane > DIV{position:relative!important; max-width:none!important; width:auto!important; margin-left:50px!important; margin-right: 50px!important;}
  
              #reader_pane > DIV:hover{box-shadow: 0px 0px 10px rgba(0,0,0,0.8) !important; border-radius:5px!important;}
  
  #footer{position:absolute !important;
          z-index: 1 !important;    
          bottom:0px!important; width:auto!important; left:0px !important; right:0px !important; opacity: 0!important;
  }
  #footer:hover{opacity: 1!important;}
}
