.csb,.n div,#logo span,.ss,img[src*="/images/nav_logo3.png"]{

  background-image: url("http://img399.imageshack.us/img399/5040/minigoogle1ef5.png") !important;

}

img[src*="/images/nav_logo3.png"] {

  width: 0 !important;

  padding-left: 150px !important;

  height: 105px !important;

}





}
