css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jetbrains.com"), domain("jetbrains.org") {
body {
  font-family: Helvetica,Arial,sans-serif;
}
h1, h2, h3 {
  font-family: Georgia,Times,'Times New Roman',serif;
}
.wrapper > .panel {
    color: #000000;
    font-size: 12px;
    line-height: 19px;
}
.navigation-links._top {
  top: -4px;
}
h1 {
  font-size: 26px;
  line-height: 26px;
}
h2 {
  font-size: 26px;
  line-height: 26px;
  margin-left: 30px;
}
.panel__header h3 {
  font-size: 16px;
  line-height: 24px;
  margin-left: 30px;
}
.article h3 {
  font-size: 22px;
  line-height: 46px;
  margin-left: 30px;
}
.article h4 {
  font-size: 20px;
  line-height: 42px;
}
.panel._main .container {
  margin-left: 8px;
  margin-right: 5px;
  padding-right: 0;
}
.panel__header._fixed + .panel__header._emulator {
  display: none;
}
.panel__header._fixed {
  position: static;
}
.panel__content {
  font-size: 14px;
  line-height: 17px;
  position: relative;
}
.panel._main .panel__header {
  margin-bottom: 0;
  padding-bottom: 0px;
  padding-top: 0px;
}
.panel-trigger {
  left: 0;
}
.article h1 {
  margin-bottom: 6px;
  margin-top: 6px;
}
.article p {
  margin-bottom: 6px;
}
.article ol, .article ul {
  margin-bottom: 3px;
  padding-bottom: 0;
}
.article p + ul {
  margin-top: -2px;
}
.last-modified {
  margin-right: 0;
}
.seealso {
  margin-right: 0;
}
.article table {
  line-height: 17px;
  margin-top: 15px;
}
.article table td, .article table th {
  padding-bottom: 2px;
  padding-top: 2px;
}
.panel._nav {
  width: 340px;
  background-color: #cccccc;
  padding-top: 30px;
}
.panel._main {
  left: 340px;
}
.nav-tree__item > .nav-tree__link {
  color: #0000FF;
  line-height: 14px;
  padding-bottom: 0px;
  padding-left: 0;
  padding-right: 16px;
  padding-top: 2px;
}
.nav-tree__item._active > .nav-tree__link {
  background-color: #dA5D22;
  margin-left: -12px;
  padding-left: 12px;
}
.panel._nav .container {
  padding: 0 0 0 0px
}
.panel._nav .panel__header {
  top: 0;
  height: 30px
}
.search-box {
  height: 20px;
  margin-bottom: 5px;
  margin-left: 7px;
  margin-right: 8px;
  margin-top: 0px;
  padding-bottom: 3px;
  padding-left: 0px;
  padding-top: 2px;
}
.search-box__label {
  padding-right: 4px;
}
.nav-tree__item mark, .nav-tree__item p b,
.search-box__input {
  color: #000000;
  background-color: #ffffff;
}
.nav-tree {
  padding-left: 0px;
}
.nav-tree__list {
  padding-left: 12px;
}
.nav-tree__item-expander {
  top: -3px;
  padding-left: 14px;
}
.nav-tree__item a:hover, .nav-tree__item._hover > .nav-tree__link {
  margin-left: -10px;
  padding-left: 10px;
  color: #0000aa;
}
.panel._nav .container._nav {
  margin-bottom: -40px;
  padding-bottom: 20px;
}
.panel._nav .container._footer {
  padding: 0 0px 0 0px;
  margin-left: 170px;
}
.panel._nav .panel__footer {
  height: 40px;
  font-size: 10px;
  line-height: 12px;
  color: #888;
}
.panel._nav .panel__footer p{
  margin-bottom: 4px;
}
.panel._nav .panel__footer a {
    color: #8080FF;
}
.panel._nav._search {
  background: #dddddd;
}
.nav-tree__button {
  margin: 15px 0px;
  padding: 0;
  background: #aaaaaa;
}
.article .sideblock {
    background-color: #FFDDDD;
    margin-left: 0 !important;
    margin-right: -3px !important;
  width: 220px;
  padding: 2px 1px 0 5px;
}
.article table .note, .article table .tip, .article table .warning {
    color: #990000;
    font-size: 0.929em;
    line-height: 1.1;
    margin: 5px 0;
}
}
