  @namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("gazeta.ru") {
  
  .incut,
  .intro,
  .idea {
    color: #000000 !important;
  }
  
  .text,
  .intro,
  .idea
  {
          font-size: 1.0em !important;
        font-family: Arial !important;
  }
  
  .incut,
  div.incut article.article h3,
  div p.h5.mb5.pl10.pr10
  {
          font-size: 0.9em !important;
        font-family: Arial !important;
  }
  
  h2.article_name,
  article.uho h2.h3.mb10,
  article.wb.p_cmn h2.h4.mb10,
  article.wb.p_cmn.mb10.f13 h2.h5.mb10,
  article.uho div.no_float h2.h5.mb5,
  article.uho h2.h5.mb5,
  article.uho h2.h4.mb10,
  article.uho.big_picture h2.h1.mb10,
  div.wb.p10 h2.h5.mb5,
  article.wb.p10 h2.h4.mb10,
  article.common h2.h5.mb10,
  h2.h4.mb10,
  .sb-similar__item-title,
  h2.h5.mb5,
  article h3,
  h1,
  h2,
  h3
  {
          font-size: 1.1em !important;
        font-family: Arial !important;
  }
  
.b-social_like_buttons.cf,
  .mt5,
  .new_import_news,
  #player,
  .ml10
  {
          display: none !important; 
 }
  
  .b-headlines ul li {
    color: #000;
    display: inline;
    float: left;
    font: 12px/18px Arial;
    margin-left: 10px !important;
    padding: 0;
    position: relative;
    width: 525px !important;
}

  .c710 {
    width: 990px !important;
}
  
  .c170 {
    width: 220px !important;
}
  
  .c510 {
    width: 760px !important;
}

  .c530 {
    width: 760px !important;
}
  
.hotnews_slider_elem {
    float: left;
    padding: 0 2px !important;
    width: 218px !important;
}
  
  .c330 {
    width: 500px !important;
} 
 .main_frame {
    border: 1px dashed #edeeef;
    border-radius: 0px !important;
    margin: 4px auto !important;
    padding: 0 4px 4px !important;
    position: relative;
    width: 1250px;
}
  
  .pic330,
  .pic510
  {
    width: 120px;
}
  
  .mb20 {
    margin-bottom: 5px;
}
  
  .c150, .c170, .c170p, .c210, .c230, .c230p, .c240, .c240p, .c330, .c350, .c350p, .c430, .c430p, .c510, .c510p, .c530, .c530p, .c710, .c710p, .c960, .c960p {
    display: inline;
    float: left;
    margin: 0 0 2px 2px !important;
}
  
  .text {
     padding: 0px 20px 0px 20px !important;
    margin: 0px 20px 0px 20px !important;
}
  
  .text > p {
    margin: 0 0 4px 8px !important;
}
  
  .text,
  p,
  div {
  -webkit-hyphens: auto;
  -moz-hyphens: auto;
  -ms-hyphens: auto;
}
  
  .pic240 {
    width: 360px;
}
  
  .uho_new210 {
    float: left;
    margin: 0;
    width: 365px;
}
}
