css:
@-moz-document domain("youtube.com") {
	#player {
		max-width: 100% !important;
		top: 0 !important;
	}
	
	.watch-stage-mode #player,
	.watch-stage-mode #placeholder-player {
		margin-top: 0 !important;
	}
	
	.player-api,
	.watch-stage-mode .player-width {
		height: 720px !important;
		margin: 0 auto !important;
		position: static !important;
		width: 100% !important;
	}
	
	#placeholder-player {
		display: none;
	}
	
	.html5-video-container {
		height: 100%;
	}
	
	.html5-video-player video {
		height: 100% !important;
		width: 100% !important;
	}
	
	video.video-stream.html5-main-video {
		top: 0 !important;
	}
	
	#content {
		top: 0 !important;
	}
	
	#watch-sidebar-spacer {
		height: 0 !important;
	}
	
	#watch7-sidebar {
		margin-top: 0 !important;
		top: 0 !important;
	}
	
	div.ytp-tooltip.ytp-bottom.ytp-preview {
		bottom: 45px !important;
		top: auto !important;
	}
	
	@media screen and (min-width: 1294px) and (max-height: 629px) {
		#page.watch .content-alignment {
			max-width: 1280px;
		}
		
		#watch7-content {
			width: 854px;
		}
		
		#watch7-sidebar {
			margin-left: 864px;
		}
	}
}
