css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("https://www.youtube.com/") {

  /* Hide unimportant elements */
  #masthead-appbar {
    display:none!important;
  }
  
  #page-container {
    display:none!important;
    height:1px!important;
  }
  
  #footer-container {
    display:none!important;
    height:1px!important;
  }
  
  #appbar-guide-button-container {
    display:none!important;
  }
  
  #ticker {
    display:none!important;
  }
  
  .skip-nav {
    display:none!important;
  }
  
  .content-region {
    display:none!important;
  }

  /* Set background color */
  body {
    background-color:white!important;
    height:100px!important;
    display:block!important;
  }
  
  /* Center align page */
   #masthead-positioner {
    vertical-align:middle!important;
    position: absolute;               /* 2 */
    top: 20%;                         /* 3 */
    transform: translate(0, -20%)   /* 4 */
  }
  
  /* Remove footer */
  #yt-masthead-container {
    margin: 0px!important;
    float:none!important;
    background-color:white!important;
    border:none!important;
    height:1px!important;
  }
  
  /* Centre search bar */
  #yt-masthead {
    width:0!important;
    width:500px!important;
     display:block!important;
  }
  
  #yt-masthead-content {
    display:block!important;
    width: 500px!important;
  }
  
  /* Replace small YT logo with a larger one from this nice place */
  .logo.masthead-logo-renderer-logo.yt-sprite {

    background: url("https://cdn.downdetector.com/static/uploads/c/300/2f211/youtubelogo.png") no-repeat scroll !important ;
    width:300px!important;
    height:212px!important;
  }
  
  /* Fixed width buttons to match the search button */
  .yt-uix-button {
    width:65px!important;
    margin-right:2px!important;
  }
  
  /* Position search bar below logo */
  .yt-masthead-logo-container {
    display:block!important;
    float:none!important;
    vertical-align: middle!important;
    text-align:center!important;
    width:auto!important;
  }
  
  /* Hide upload button when not signed in */
  #yt-masthead-signin #upload-btn {
    display:none!important;
  }

  /* Make sign in button match search button */
  .signin-container .yt-uix-button{
    background-color:#F8F8F8!important;
    border-color: #D3D3D3!important;
    border-radius: 2px!important;
    color: #333!important;
  }
  
  /* Position signed in elements */
  #yt-masthead-user .yt-uix-clickcard{
    margin-right:90px!important;
  }
  
  #sb-button-notify {
    margin-left: -100px!important;
  }
  
  .notifications-container {
    margin-right:-163px!important;
  }
}
