@-moz-document domain("twitter.com") {
  /* Don't Copy, This is Under the terms of Subhankar Mondal */
  /* Created By Subhankar Mondal */
  /* India (27/06/2015) */

/* Stutas Borer Radius */
  .stream-item.separated-module                  {border-radius: 0px;}
  .top-timeline-tweetbox .tweet-user             {border-radius: 0px 0px 0px 0px;}
  .top-timeline-tweetbox
      .timeline-tweet-box                        {border-radius: 0px;}
  .DashboardProfileCard                          {border-radius: 0px;}
  .Trends                                        {border-radius: 0px;}
  .module ul:last-child li:last-child
  .list-link, .module ul:last-child li.last-item
  .list-link, .module .list-link.last,
  .module .flex-module:last-child,
  .module .contrast-flex-module:last-child       {border-radius: 0px;}
  .module ul:first-child li:first-child
  .list-link, .module .flex-module:first-child,
  .module .contrast-flex-module:first-child      {border-radius: 0px;}
  input, textarea,
  div[contenteditable="true"], .t1-select        {border-radius: 0px;}
  .DashboardProfileCard-avatarLink               {border-radius: 100%;}
  .DashboardProfileCard-avatarImage              {border-radius: 100%;}
  .avatar                                        {border-radius: 100%;}
  .with-media-forward
  .card2[data-card2-name="animated_gif"],
  .with-media-forward .expanded-content
  .card2[data-card2-name="animated_gif"]         {border-radius: 0px;}
  .has-autoplayable-media
  .card2[data-card2-name="animated_gif"]
  .animated-gif                                  {border-radius: 0px;}
  .with-media-forward .cards-base .media-forward {border-radius: 0px;}
  .multi-photos.photos-2 .photo-1,
  .multi-photos.photos-3 .photo-1,
  .multi-photos.photos-4 .photo-3                {border-radius: 0px;}
  .multi-photos.photos-2 .photo-2,
  .multi-photos.photos-3 .photo-3,
  .multi-photos.photos-4 .photo-4                {border-radius: 0px;}
  .multi-photos .photo-1                         {border-radius: 0px;}
  .multi-photos .photo-2                         {border-radius: 0px;}
  .tweet-btn                                     {border-radius: 0px;}
  .global-nav .search-input                      {border-radius: 0px;}
  .nav .session .dropdown-toggle                 {border-radius: 100%;}
  .dropdown.open .dropdown-menu                  {border-radius: 0px;}
  .btn                                           {border-radius: 0px;}
  .modal-content                                 {border-radius: 0px;}
  .before-expanded                               {border-radius: 0px;}
  .stream-item.open                              {border-radius: 0px;}
  .after-expanded:not(.separation-module),
  .after-expanded .tweet:hover,
  .after-expanded .tweet.focus                   {border-radius: 0px;}
  .Gallery-content                               {border-radius: 0px;}
  .Gallery-media                                 {border-radius: 0px;}
  .content-main .expansion-container
  .tweet.descendant
  .stream-item-header .avatar                    {border-radius: 100%;}
  .ProfileAvatar-image,
  .ProfileAvatar-placeholderImage                {border-radius: 100%;}
  .ProfileAvatar                                 {border-radius: 100%;}
  .PhotoRail-mediaBox .media-thumbnail           {border-radius: 0px;}
  .WhoToFollow                                   {border-radius: 0px;}
  .ProfileHeading-content                        {border-radius: 0px;}
  .ProfileCard                                   {border-radius: 0px;}
  .ProfileCard-bg                                {border-radius: 0px;}
  .ProfilePage .timeline-end.has-items
  .stream-end-inner, .ProfilePage
  .timeline-end .stream-end-inner                {border-radius: 0px;}
  .ProfilePage .timeline-end
  .stream-end-inner, .ProfilePage
  .timeline-end .stream-end                      {border-radius: 0px;}
  .ProfileCard-avatarLink                        {border-radius: 100%;}
  .ProfileCard-avatarImage                       {border-radius: 100%;}
  .PageDownload .SmsAppLinkForm
  .input-prepend .add-on.btn                     {border-radius: 0px;}
  .front-welcome, .front-signin, .front-signup   {border-radius: 0px;}
  .content-main, .content-narrow,
  .page-canvas, .permalink                       {border-radius: 0px;}
  .sign-up-box .signup                           {border-radius: 0px;}
  .StartCongratulations-image                    {border-radius: 0px;}
  .StartPage .content-main > .StartPage-module   {border-radius: 0px;}
  .StartSetupProfile-profileCardPhoto            {border-radius: 100%;}
  .StartSetupProfile-profileCardPhoto img        {border-radius: 100%;}
  .StartSetupProfile-profileCardBackground       {border-radius: 0px;}
  .Banner .flex-module                           {border-radius: 0px;}
  .DashboardProfileCard-bg                       {border-radius: 0px;}
  .ProfileTweetbox                               {border-radius: 0px;}
  .message                                       {border-radius: 0px;}
  .QuoteTweet                                    {border-radius: 0px;}
  .stream-end-inner                              {border-radius: 0px;}
  .content-header, .content-no-header            {border-radius: 0px;}
  .content-header .header-inner,
  .content-no-header .no-header-inner            {border-radius: 0px;}
  .content-inner.no-stream-end                   {border-radius: 0px;}
  .NavigationItem.NavigationItem--search
  .NavigationItem-searchBox                      {border-radius: 0px;}
  .page-products-list .devices
  .item-list ul li a.get-the-app                 {border-radius: 0px;}
  .form-search .input-append .search-query       {border-radius: 0px;}
  .form-search .input-append .btn                {border-radius: 0px;}
  .alert                                         {border-radius: 0px;}
  .Button                                        {border-radius: 0px;}
  .poster-image, .poster-image img, 
  .poster-image-container                        {border-radius: 0px;}
  .stream-items > .stream-item.open:first-child  {border-radius: 0px;}
}
