css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("ask.fm") {
  html
  {
    background: #DADADA;
  }
  body
  {
    font-family: 'Open Sans', sans-serif;
  }
  .body-content
  {
    margin-top: 42px;
  }
  #logo
  {
    -moz-transition: all .1s ease-in;
  }
  #logo:hover
  {
    -moz-transition: all .1s ease-in;
  }
  #menu
  {
    background: #446CB3;
  }
  a.link-menu
  {
    -moz-transition: all .1s ease-in;
    background: rgba(0,0,0,0);
    color: #E4F1FE;
    border-bottom: solid 0 rgba(0,0,0,0.2);
  }
  a.link-menu:hover
  {
    -moz-transition: all .1s ease-in;
    background: rgba(0,0,0,0.1);
    color: #FFFFFF;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  a.link-menu-active
  {
    -moz-transition: all .1s ease-in;
    background: rgba(0,0,0,0.1);
    color: #FFFFFF;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  input.link-logout
  {
    -moz-transition: all .1s ease-in;
  }
  input.link-logout:hover
  {
    -moz-transition: all .1s ease-in;
  }
  #leaderboard
  {
    height: 0;
    border: 0;
    margin: 0;
    padding: 0;
  }
  #wrapper
  {
    background: rgba(255,255,255,0.65);
    border-radius: 0;
  }
  a.blueFriendsButton.submit-button-active
  {
    -moz-transition: all .1s ease-in;
    background: #446CB3;
    border: 0;
    border-radius: 0;
    padding: 10px;
    border-bottom: solid 2px #3D61A1;
  }
  a.blueFriendsButton.submit-button-active:hover
  {
    -moz-transition: all .1s ease-in;
    background: #3D61A1;
    border-bottom: solid 2px #314E81;
  }
  a.blueFriendsButton.submit-button-disabled
  {
    -moz-transition: all .1s ease-in;
    background: #67809F;
    border: 0;
    border-radius: 0;
    padding: 10px;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  #shareLinkReminder
  {
    background: rgba(0,0,0,0.1);
    border-radius: 0;
    border: 0;
  }
  a.blueBox_service
  {
    -moz-transition: all .1s ease-in;
    color: #446CB3;
  }
  a.blueBox_service:hover
  {
    -moz-transition: all .1s ease-in;
    color: #3D61A1;
  }
  #blueBox.blueBox-large
  {
    -moz-transition: all .1s ease-in;
    background: rgba(0,0,0,0.1);
    border: none;
    border-radius: 0;
  }
  a.appPromoter
  {
    -moz-transition: all .1s ease-in;
    background-image: url("http://madeorsk.fr/ftp/ask.fm-appPromoterBackground-modif.png");
  }
  a.appPromoter:hover
  {
    -moz-transition: all .1s ease-in;
  }
  .yourLink
  {
    background: #F4D03F;
  }
  .yourLinkBox a
  {
    background: #F4D03F;
  }
  #recommended_users_box
  {
    background: rgba(0,0,0,0);
  }
  .recommendedBox
  {
    background: rgba(0,0,0,0.1);
    border-radius: 0;
    border: 0;
  }
  a.recommendedName
  {
    -moz-transition: all .1s ease-in;
    color: #000000;
  }
  a.recommendedName:hover
  {
    -moz-transition: all .1s ease-in;
    color: #446CB3;
  }
  .recommendedLogin
  {
    color: #343434;
  }
  a.recommendedFollow
  {
    -moz-transition: all .1s ease-in;
    background: #26A65B;
    border: 0;
    border-radius: 0;
  }
  a.closeBlueBox
  {
    -moz-transition: all .1s ease-in;
    background: url("http://madeorsk.fr/ftp/ask.fm-closeBox-modif.png");
  }
  a.closeBlueBox:hover
  {
    -moz-transition: all .1s ease-in;
  }
  a.recommendedIgnore
  {
    -moz-transition: all .1s ease-in;
    background: url("http://madeorsk.fr/ftp/ask.fm-ignoreX-modif.png");
  }
  a.recommendedIgnore:hover
  {
    -moz-transition: all .1s ease-in;
  }
  a.link-headline-active
  {
    color: #446CB3;
  }
  a.link-headline
  {
    -moz-transition: all .1s ease-in;
  }
  a.link-headline:hover
  {
    -moz-transition: all .1s ease-in;
    color: #446CB3;
  }
  a.link-blue
  {
    -moz-transition: all .1s ease-in;
    color: #446CB3;
  }
  a.link-blue:hover
  {
    -moz-transition: all .1s ease-in;
    color: #3D61A1;
  }
  
  /*
  WELCOME
  */
  .body-welcome
  {
    font-family: 'Open Sans', sans-serif;
  }
  #screen
  {
    background: rgba(255,255,255,0.65);
  }
  div.topFixer div#menu
  {
    background: #446CB3;
  }
  #loginTip
  {
    color: #FFFFFF;
  }
  a.link-login
  {
    display: block;
    -moz-transition: all .1s ease-in;
    background: rgba(0,0,0,0.1);
    border: 0;
    border-radius: 0;
    border-bottom: solid 2px rgba(0,0,0,0);
    margin: 0;
    padding: 10px;
    padding-top: 13px;
    padding-bottom: 13px;
    vertical-align: middle;
  }
  a.link-login:hover
  {
    background: rgba(0,0,0,0.2);
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  #signupSlogan
  {
    color: #446CB3;
    text-shadow: 1px 1px rgba(0,0,0,0.2);
  }
  a.signupButton
  {
    -moz-transition: all .1s ease-in;
    color: #FFF;
    background: #26A65B;
    border: 0;
    border-radius: 0;
    border-bottom: solid 2px rgba(0,0,0,0);
    box-shadow: 0 0 rgba(0,0,0,0);
    text-shadow: 0 2px rgba(0,0,0,0.2);
  }
  a.signupButton:hover
  {
    -moz-transition: all .1s ease-in;
    background: #229552;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  #whosHere-label
  {
    color: #000;
  }
  a.mobileAppBadge
  {
    -moz-transition: all .1s ease-in;
    background: #446CB3;
    border: 0;
    border-radius: 0;
    border-bottom: solid 2px #446CB3;
  }
  a.mobileAppBadge:hover
  {
    -moz-transition: all .1s ease-in;
    background: #3D61A1;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  .text-black
  {
    color: #2B4572;
  }
  #heads
  {
    background: none;
  }
  #language
  {
    background: none;
    border: none;
  }
  #footer_old
  {
    background: none;
    border-radius: 0;
    border: none;
  }
  #whosHere-label
  {
    background-image: url("http://madeorsk.fr/ftp/ask.fm-here-arrow_0402-modif.png");
  }
  /*
  WELCOME
  */
  
  /*
  LOGBOX
  */
  #logBox_top.logBox_sprite
  {
    width: 306px;
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logbox_sprite_010813-modif.png");
  }
  #logBox_bottom.logBox_sprite
  {
    width: 306px;
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logbox_sprite_010813-modif.png");
  }
  #logBox_content
  {
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logBox-content-modif.png");
  }
  a.logBox_sprite
  {
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logbox_sprite_010813-modif.png");
  }
  .signinFrame
  {
    background: none;
    border: none;
    border-radius: 0;
  }
  .signinTile
  {
    background: rgba(255,255,255,1);
    border: none;
  }
  .signinTileMask
  {
    background: rgba(255,255,255,1);
  }
  input.logBox_submit
  {
    -moz-transition: all .1s ease-in;
    color: #FFF;
    background: #26A65B;
    border: 0;
    border-radius: 0;
    border-bottom: solid 2px rgba(0,0,0,0);
    box-shadow: 0 0 rgba(0,0,0,0);
    text-shadow: 0 2px rgba(0,0,0,0.2);
  }
  input.logBox_submit:hover
  {
    -moz-transition: all .1s ease-in;
    background: #229552;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  a.logBox_service.logBox_sprite
  {
    -moz-transition: all .1s ease-in;
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logbox_sprite_010813-modif.png");
  }
  a.logBox_service
  {
    -moz-transition: all .1s ease-in;
  }
  a.logBox_service:hover
  {
    -moz-transition: all .1s ease-in;
  }
  #logBox_submit-spinner.logBox_sprite
  {
    background-image: url("http://madeorsk.fr/ftp/ask.fm-logbox_sprite_010813-modif.png");
  }
  /*
  YOLO
  */
  input.submit-button-more.submit-button-active
  {
    -moz-transition: all .1s ease-in;
    background: #446CB3;
    color: #FFF;
    border: 0;
    border-radius: 0;
    padding: 10px;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  input.submit-button-more.submit-button-active:hover
  {
    -moz-transition: all .1s ease-in;
    background: #3D61A1;
    color: #FFF;
    border-bottom: solid 2px rgba(0,0,0,0.2);
  }
  .text-headline
  {
    color: #446CB3;
  }
  a#select_photo_button.camera
  {
    -moz-transition: all .1s ease-in;
    color: #446CB3;
    background-image: url("http://madeorsk.fr/ftp/ask.fm-addPhotoIcon-modif.png");
  }
  a#select_photo_button.camera
  {
    -moz-transition: all .1s ease-in;
    color: #3D61A1;
  }
  textarea#post-input-pre.growable-textarea
  {
    -moz-transition: all .1s ease-in;
    background: rgba(255,255,255,0.5);
    border: none;
    border-left: solid 1px #446CB3;
    border-right: solid 1px #446CB3;
    border-radius: 0;
  }
  textarea#post-input-pre.growable-textarea:focus
  {
    -moz-transition: all .1s ease-in;
    background: rgba(255,255,255,0.75);
    border-left: solid 1px #3D61A1;
    border-right: solid 1px #3D61A1;
  }
