.more-tweets-btn {display:none} 


.avatar {
    border-radius:50%; /* rounded avatar */
    -webkit-box-shadow: 0px 0px 3px 1px rgba(0,0,0,0.75); /* wooohouuuw box shadow, it's like a shadow but on a box, it's magical */
    box-shadow: 0px 0px 3px 1px rgba(0,0,0,0.75);
        }

/* TINADECK ANIMATIONS, AWESOME THINGS LIKE CATS JUMPING OUT OF YOUR SCREEN ETC (not yet) */

.avatar,.tweet,.tweet-detail-wrapper,.tweet.avatar,.tweet.account-link{-webkit-transition: 0.5s all;transition: 0.5s all;} /* add transition timed at the same tempo for every element that has a transition effect */

.tweet .account-link,.tweet-detail-wrapper:hover .account-link {text-shadow: 0px 0px 0px rgba(23, 183, 242,1);} /* waaah and it also creates a fancy shadow effect on the TN/@ of the guy who tweeted it */
.tweet:hover .account-link,.tweet-detail-wrapper:hover .account-link {text-shadow: 0px 0px 1px rgba(23, 183, 242,1);} 
.compose-account, .prf-img {
    border-radius:50%;
    border: 1px darkblue solid; /* waouhou dark blue double border in account selection so tricky */
}

