css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("skreemr.com") {

/* Deletions */
  span.snap_noshots,
  [src="images/rank.gif"],
  [bgcolor="yellow"],
  .js-kit-rating,
  .niftycorners,
  [src="images/corner-topleft.gif"],
  [src="images/corner-topright.gif"],
  [src="images/corner-bottomleft.gif"],
  [src="images/corner-bottomright.gif"],
  [onmouseover="Tip('Find music by this artist at Amazon.com', BALLOON, true, ABOVE, true, OFFSETX, 0);"],
  [onmouseover="Tip('Get ringtones by this artist on Thumbplay!', BALLOON, true, ABOVE, true, OFFSETX, 0);"],
  [name="searchForm2"],
  [size="-1"],
  [size="-2"],
  [href="http://www.addthis.com/bookmark.php"],
  .darkback .info
      {display: none !important;}

[name="searchForm"] {
  background: #ddd !important;}

[alt="skreemr logo"] {
  -moz-outline: 10px #B6CAE1 dashed !important;
  -moz-outline-radius: 20px !important;
  opacity: 0.6 !important;}

[alt="skreemr logo"]:hover {
  opacity: 0.8 !important;}

.resultbox tbody tr td {
  background: none !important;
  padding-left: 7% !important;}

.bottomrow {
  height: 30px !important;
  border-bottom: 10px #DCE9F7 solid !important;
  opacity: 0.95 !important;}

.results {
  background: #DCE9F7 !important;
  padding: 3px 8px 3px 8px !important;
  margin: 0 0 -5px 50px !important;
  -moz-border-radius: 5px 5px 0 0 !important;}

table tbody tr td[width="500"] {
  background-color: #C4DBEF !important;
  border: 10px #DCE9F7 solid !important;}

body {
  background: #DCE9F7 url("data:image/gif;base64,") !important;
  padding-left: 25% !important;
  width: 49.4% !important;}

.previousnext {
  margin: -30px 0 -60px 33% !important;
  width: 33% !important;}

.lightback .info {
  padding-bottom: 6px !important;}

.info span {
  font-size: 14pt !important;
  font-family: lucida grande !important;
  color: #516B84 !important;}
.info span[class="title"] {
  font-size: 14pt !important;
  color: #EEF4FB !important;}
.info span[class="sharemenu"] {
  font-size: x-small !important;}
.dropbutton {
  background-color: #c4dbef !important;
  color: #516B84 !important;}
}
