css:
@-moz-document url("http://www.eldarya.com/") {
::-webkit-scrollbar{
    width:14px;
    background:transparent;
}
::-webkit-scrollbar-track-piece {						/* Fond */
	background: transparent none;
	border: solid 4px transparent;
	border-right-width: 7px;
	margin: 4px;
	-webkit-box-shadow: inset 1px 1px 0 0 royalblue, inset -1px -1px 0 0 aliceblue;
}
::-webkit-scrollbar-track-piece:horizontal {			/* Fond pour la barre du bas */
	border-right-width: 4px;
	border-bottom-width: 7px;
}
::-webkit-scrollbar-thumb{
    background-image:url(http://nsa38.casimages.com/img/2015/06/28/150628035711697823.png);
    border-radius:20px;
}
}
