css:
@-moz-document domain("www.chineseetymology.org") {
/*http://demo.phpgang.com/facebook-style-scroll-bar-with-jquery-css/js/jquery-1.8.0.min.js
http://demo.phpgang.com/facebook-style-scroll-bar-with-jquery-css/js/jquery.nicescroll.js*/

::-webkit-scrollbar {
    width: 5px;
    height: 0px;
    background: none;
    border: none;
}
::-webkit-scrollbar-thumb {
    border-radius: 3px;
    background-color: rgba(0, 0, 0, 0.4);
    height: auto;
}
html::before {
    content: "";
    position: fixed;
    left: 0;
    top: 0;
    font-size: 80px;
    font-weight: 500;
    line-height: 80px;
    color: #fff;
    background-color: #f44;
    background-image:url(http://orig10.deviantart.net/2166/f/2015/210/e/f/yuan_by_volcanoid-d93bhij.png);
    background-repeat:no-repeat;
    background-position:center center;
    background-size:auto 80px ;
    height:105px;
    box-sizing:border-box;
    width: 100%;
    /*padding: 12.5px;
    padding-left: 520px;
    padding-right: 0px;*/
    z-index: 100;
    text-align: left;
    text-shadow: 0px -1px 1px #900;
    box-shadow: 0 -2px 10px #000;
}
body {
    background: #fcfcfc!important;
    text-align: justify
}
* {
    font-family:DIN Next LT Pro,'source han sans cn', 'noto sans cjk sc', sans-serif;
    color: #222;
    background: none!important;
    box-sizing: border-box!important;
    border: none!important;
    line-height: 1.2em;
}
*:focus {
    outline-width:0;
}
::selection,a:active,input[type="submit"]:active {color:#f44;}
body > * {
    position: relative!important;
    top: 105px;
    width: 80%!important;
    left: 10%;
    margin: 0!important;
}
img[alt*="Chinese Etymology"] {
    display: none;
}
tr[style="border-bottom: 0px"] {
    display: none;
}
p[style="text-align:center; font-size: 12pt"] {
    display: none;
}
td[style*="rgb(164, 209, 220)"]:hover {
    overflow-y: auto;
    width: calc(38.196601% + 5px);
}
td[style*="rgb(164, 209, 220)"] {
    position: absolute;
    top: 0px;
    left: 0;
    padding: 1em;
    overflow: hidden;
    height: 100%;
    width: 38.196601%;
}
td[colspan="3"] {
    position: absolute;
    top: 0px;
    right: 0;
    padding: 1em 0px;
    height: 537.5px;
    width: 61.803399%;
    overflow-y: hidden;
}
td[style="background-color: rgb(237, 226, 203)"] {
    display: none;
}
#EnglishExplanation + p,
#simplificationLabel + p {
    margin: 0.4em 0 0;
    float: right;
    font-size: 2em;
    max-width: 275px;
    overflow: visible;
    overflow-x: hidden;
    white-space: nowrap;
    position: absolute;
    left: 120px;
}
#EnglishExplanation + p,
#simplificationLabel + p,
*[id$="Hex"],
*[id$="Hex"] * {
    font-family: DIN Next LT Pro, 'source han sans tw', 'noto sans cjk tc', microsoft jhenghei, Heiti TC, sans-serif;
    font-weight: 300;
    text-decoration: none;
    text-shadow: 0 1px 0 #FFF;
    text-rendering: optimizeLegibility;
    z-index: 10;
}
#SimplifiedChar[style],
#TraditionalChar[style] {
    float: left;
    width: 100px;
    height: 100px;
    font-size: 100px!important;
    line-height: 100px;
    /*font-family:Adobe Heiti Std,'noto sans cjk sc'!important;*/
    position: absolute;
    text-align: center;
    overflow: hidden;
    font-weight: 200;
    text-shadow: 0 1px 1px rgba(17, 17, 17, 0.4);
}
#simplificationLabel,
#simplificationLabel p {
    position: absolute;
    top: 135px;
    width: calc(100% - 14px);
}
p[style*="color: red; font-size: 10pt;"] {
    display: none;
}
#SimplifiedChar,
#EnglishExplanation + p {
    top: 75px;
}
#TraditionalChar,
#simplificationLabel + p {
    top: 175px;
}
#SimplifiedChar {
    font-family: Heiti SC, 'noto sans cjk sc', 'source han sans CN', microsoft yahei;
}
#TraditionalChar {
    font-family: Heiti TC, 'noto sans cjk tc', 'source han sans tw', microsoft jhenghei;
}
#EnglishExplanation {
    margin-top: 315px;
    line-height: 1.05em;
    margin-bottom: -1em;
    display: block;
}
#charPhon {
    position: absolute;
    top: 305px;
    left: 14px;
    width: calc(100% - 2em);
    white-space: nowrap;
    overflow: hidden;
    z-index: 99;
    word-spacing: -2px;
}
#charPhon::before {
    content: "Phonetic: "
}
input {
    border: none
}
input[type="submit"] {
    text-transform: uppercase;
    font-weight: bold;
    font-size: 11px;
    font-family:helvetica neue;
    color: #666;
}
input[type="text"] {
    position: absolute;
    left: 80px;
    z-index: 9;
    background: none;
    border-bottom: 1px solid #ccc!important;
    width: 67%;
    padding: 0 .33em 0;
    color: #444;
}
form {
    position: absolute;
    width: calc(100% - 2em);
    color: transparent!important;
    -webkit-text-stroke: 0px;
    left:5%;
}
form:nth-of-type(2) {
    top: 2.4em;
}
form:nth-of-type(3) {
    top: 3.8em;
}
form[method="get"],
*[id$="Images"] {
    -webkit-user-select: none;
}

td[style*="rgb(164, 209, 220)"] > p:last-child::after {
    content: "Copyright (c) 2003, 2008, 2011, 2013 Richard Sears         richard.sears.tn@gmail.com";
    font-size:.9em;
    color:#f34;
}

/*right side below*/

#PictureImages {
    display: none
}
#PictureImages + p {
    position: absolute;
    top: calc(20% - 2.5em);
}
#etymologyLabel {
    position: absolute;
    top: 0;
    left: 7.5%;
    padding: 1em;
    width: 85%;
    height: 20%;
}
#etymologyLabel p {
    margin: 0;
    height: 100%;
    overflow: auto;
}
*[id$="Images"] {
    position: absolute;
    width: 85%;
    height: 20%;
    left: 7.5%;
}
*[id$="Images"] > * {
    position: absolute;
    top: 0;
    left: 0;
    height: 100%;
    width: 100%;
    margin: 0;
}
*[id$="Images"] tbody {
    position: absolute;
    left: 104px;
    top: 0;
    height: 90px;
    width: 495px;
    padding: 0;
    overflow: hidden;
}
*[id$="Images"] tbody:hover {
    width: 490px;
    overflow: scroll
}
*[id$="Images"] tbody *:not(img) {
    position: relative;
    float: left;
    margin: 0px;
}
*[id$="Images"] * {
    vertical-align: bottom;
}
*[id$="Images"] td {
    position: relative;
    height: 90px;
    width: 90px;
}
*[id$="Images"] img {
    height: 80px;
    width: 80px;
    -webkit-filter: brightness(.663) opacity(0.87) contrast(3);
}
td[style="background-color: rgb(237, 226, 203)"] {
    display: none;
}
#SealImages {
    top: 20%;
}
#SealImages::after {
    content: "篆";
}
#SealImages::before {
    content: "說文 SEAL";
}
#LstImages {
    top: 40%;
}
#LstImages::after {
    content: "篆";
}
#LstImages::before {
    content: "六书通 SEAL";
}
#BronzeImages {
    top: 60%;
}
#BronzeImages::after {
    content: "金";
}
#BronzeImages::before {
    content: "BRONZE";
}
#OracleImages {
    top: 80%;
}
#OracleImages::after {
    content: "甲";
}
#OracleImages::before {
    content: "ORACLE";
}
/*說文解字裏 六書通裏 金文编 甲骨文*/

*[id$="Images"]::after {
    position: absolute;
    left: 12.5px;
    bottom: 12.5px;
    line-height: 75px;
    font-size: 75px;
    font-weight: 300;
}
*[id$="Images"]::before {
    margin-top: -5px;
    top: 0;
    position: absolute;
    left: 12.5px;
    height: 1.5em;
    width: 80px;
    text-align: center!important;
    -webkit-user-select: none;
    font-family: Adobe Heiti Std;
}
pre p,
caption,
*[id$="Images"] > p {
    display: none
}
*[id$="Images"] pre::after {
    content: "none known";
    position: absolute;
    left: calc(104px + 40px);
    top: 37px;
    font-size: 1.5em;
}
div table {
    width: 100%!important
}
body > form {
    max-height: 538px;
    overflow: auto;
}
td[width="100"],
td[width="150"] {
    width: 363px!important
}
body > video {
    top: 50%;
    height: 0
}
html::after {
    content: '';
    display: block;
    position: fixed;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    pointer-events: none;
    opacity: .1;
    background-image: url(data:image/png;base64,);
}





#TraditionalChar, #simplificationLabel + p {
    top: 177px;
}
#EnglishExplanation + p, #simplificationLabel + p, *[id$="Hex"], *[id$="Hex"] * {
    font-family: brix sans,roboto,noto sans cjk tc;
    /* font-weight: 300; */
    text-decoration: none;
    text-shadow: 0 1px 0 #FFF;
    text-rendering: optimizeLegibility;
    z-index: 10;
    line-height: 1.1em;
}
#EnglishExplanation + p, #simplificationLabel + p {
    margin: 12px 0 0;
    float: right;
    font-size: 25px;
    max-width: 250px;
    overflow: hidden;
    white-space: nowrap;
    position: absolute;
    left: 112px;
}
* {
    font-family: roboto,'noto sans cjk sc demilight', sans-serif;
    color: #222;
    background: none!important;
    box-sizing: border-box!important;
    border: none!important;
    line-height: 1.2em;
}

input[type="submit"] {
    text-transform: none;
    font-weight: normal;
    font-size: 24px;
    font-family: roboto;
    color: #666;
}

form:nth-of-type(2) {
    top: 39px;
}

form:nth-of-type(3) {
    top: 64px;
}

#SimplifiedChar[style], #TraditionalChar[style] {
    float: left;
    width: 100px;
    height: 100px;
    font-size: 77px!important;
    line-height: 100px;
    /* font-family: Adobe Heiti Std,'noto sans cjk sc'!important; */
    position: absolute;
    text-align: center;
    overflow: hidden;
    font-weight: 200;
    text-shadow: 0 1px 1px rgba(17, 17, 17, 0.4);
}

#SimplifiedChar, #EnglishExplanation + p {
    top: 92px;
}


input[type="text"] {
    position: absolute;
    left: 132px;
    z-index: 9;
    background: none;
    border-bottom: 1px solid #888!important;
    width: 67%;
    padding: 0 .33em 0;
    color: #444;
    font-size: 21px;
    font-family: dengxian;
}


#charPhon {
    position: absolute;
    top: 276px;
}

#EnglishExplanation {
    margin-top: 300px;
    
}

#simplificationLabel, #simplificationLabel p {
    position: absolute;
    top: 140px;
}
}

@-moz-document url-prefix("http://www.chineseetymology.org/CharacterEtymology.aspx") {
body > table {
    height: calc(100% - 105px);
    position: absolute!important;
}
body > p {
    position: absolute!important;
    left: 10%!important;
    width: auto!important;
    height: 1.5em!important;
    top: initial;
}
body > p:last-child {bottom: 0em;}
body > p:nth-last-child(2) {display: none}
}
