css:
@-moz-document domain(corazondemelon.es) {
/****                     BUBBLE GUM                        ****/
/*                        **********                           */
/* 

Descripción: Tema para CdM creado por Liz (@ eldarya-fairy.tumblr.com)
Reglas:
    * No quites el credito ^
    * Iconos del menú: Font Awesome.
    * Puedes editarlo tanto como quieras.
    * La mayoría de las imágenes pertenecen a Beemoov/Chinomiko, solamente la del menú fue hecha por mí.
    * Cualquier duda/problema, aquí: http://eldarya-fairy.tumblr.com/ask
*/

/**** GENERAL ****/

div#cadre_ext_body, div#body {
   font-size:11px!important;
   font-family:Calibri!important;
   color:#777777!important;
}

a {
   font-family:Calibri;
   text-transform:uppercase;
   letter-spacing:1px;
   text-decoration:none;
}

span {
   color:#bcc5ff;
}

span a {
   color:#bbc6ff;
}

h1, h2, h3, h4, h5 {
   font-family:Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#8edbff!important;
}

h2 .firstword, .integrated-title2 .firstword {
   font:11px Calibri;
   color:#68ccff;
   font-style:normal;
   text-transform:uppercase;
   letter-spacing:1px;
}

.integrated-title1, .integrated-title2, .integrated-title3, #current-episode h2 {
   font-family:Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   font-style:oblique;
}

.integrated-title1 {
   color:#4fc3ff!important;
}

.integrated-title2 {
   color:#dad5ff!important;
}

.integrated-title3 {
   color:#d5e6ff!important;
}

#avatarGirl {
   padding-left:44px!important;
}

#right-informations h3 {
   font:14px Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#9bddff!important;
   padding:10px 18px 10px 4px!important;
   text-align:center;
}

#right-informations, #counter, .number, .firstword {
   font:16px Calibri!important;
   text-transform:uppercase;
   letter-spacing:1px;
   color:#4fc3ff!important;
}

.firstword {
   margin-right:16px; 
}

#container{
   background-image:url(http://36.media.tumblr.com/b5e0a6942af6b42ecbe3b285d7a75ae9/tumblr_nhf4rs1xGK1tjrq1to2_250.jpg)!important;
   background-repeat:repeat;
}

#container.connected h2 {
   font-family:Calibri;
   margin:12px 6px;
   border-bottom:1px dashed #cfcfcf;
   padding-bottom:4px;
}

#news-list .news .date {
   float:left;
   width:59px;
   height:28px;
   background-color:#ceeeff;
   border-radius:5px;
   background-image:none;
   color:#68ccff;
   font:bold 14px Calibri;
   padding:6px 0px 0px 10px;
}

.text a {
  color:#82d4ff!important;
}

/**** OBJETIVOS ****/

#current-episode h2 {
  font-size:15px;
}

#current-episode li.completed {
   background:transparent url(https://41.media.tumblr.com/54805c0428ed29cd1f440cf847a81a80/tumblr_nocnv05iwJ1urksn7o6_75sq.png)left center no-repeat;
}

.episode-progression .progress-left {
   position:absolute;
   background:url(https://41.media.tumblr.com/e3208895d0f668b48b6590434aa59795/tumblr_nocnv05iwJ1urksn7o5_75sq.png)!important;
   width:5px;
   height:14px;
   left:-5px;
}

.episode-progression .progress-inner {
   position:absolute;
   background:url(https://40.media.tumblr.com/511101bce4c0cb84ed821e263a4d01e5/tumblr_nocnv05iwJ1urksn7o4_75sq.png)!important;
   height:14px;
   left:0px;
   right:0px;
}

.episode-progression .progress-right {
   position:absolute;
   background:url(https://40.media.tumblr.com/9576ea527bcc35a3f60c8f9ad68ee22a/tumblr_nocnv05iwJ1urksn7o7_75sq.png)!important;
   width:5px;
   height:14px;
   right:-5;
   z-index:9999;
}

/**** JUEGO ****/

.scene .sentence {
  display:block;
  border:0;
  background-color:#fff4f6!important;
  opacity:0.8;
  filter: alpha(opacity=80);
  border-top:4px solid #9bddff!important;
  border-left:4px solid #9bddff!important;
  border-top-left-radius:25px;
  padding:5px 15px 0 37px;
  text-align:left;
}

.scene .responses {
  border-left:4px solid #9bddff!important;
}

.scene .responses .response {
  color:#9babff!important;
}

.scene .responses .response:hover {
  background-color:#4fc3ff!important;
  color:#e8f7ff!important;
}

.scene .responses .response:hover .text {
  color:#ffffff!important;
}
            
.scene .player {
  background:transparent url(https://40.media.tumblr.com/215391b1377190edab74d558f9aa0fcc/tumblr_nocnv05iwJ1urksn7o2_250.png) -6px top no-repeat;
  width:175px;
  height:130px;
  padding-left:13px;
}
                  
.text{
  color:#02a9ff!important;
}

/*[[Love]]*/

/*[[bee]]*/

/*[[ver]]*/

/**** COPYRIGHT ****/

#copyright, #copy2 {
  text-align:center;
}

#copy2 .copy2d {
  text-align:center;
  color: #b1a59c;
  bottom:0;
  right:0;
  margin-bottom:5px;
  padding-right:280px;
}

/**** MINIJUEGOS ****/

#minigame {
  padding-left:30px;
}

#minigame h1 {
  text-align:center;
   margin-left:-20px;
}

p.description a.auto-button-pink {
  margin:10px 10px 10px 30px;
}
                          
/**** HEADER ****/

#logged-episode {
   padding:2px 4px;
   color:#8296ff;
   text-shadow:none;
   font-weight:bold;
   text-transform:uppercase;
}

#logged-pseudo a {
   color:#9babff;
}

#logged-pseudo span {
   float:left;
   color:#b5c1ff;
   text-shadow:none;
   font:bold 14px Calibri;
   letter-spacing:1px;
}

#astrological-sign a {
   text-decoration:none;
   font:oblique 11px Calibri;
   color:#b5c1ff!important;
   text-transform:uppercase;
   letter-spacing:1px;
}

#header a, div#logged-money, div#logged-pa, span.actionpoints, div#logged-moral {
   font-size:11px!important;
   letter-spacing:0px!important;
   font-family:Calibri!important;
}

#header a:hover {
   font-style:oblique;
}

#container.connected #header {
   background-image:url(https://41.media.tumblr.com/cada9d9f6099e94878855bdc6ea1ceca/tumblr_nocnv05iwJ1urksn7o1_1280.png)!important;
}

#sous-menu {
   width:100%;
   height:40px;
   position:relative;
   margin-top:0px;
   background:url(https://41.media.tumblr.com/f9be6d2755f26b6d9549b186e56a8327/tumblr_nompyvpyGJ1urksn7o1_1280.png) no-repeat scroll top center transparent;
}

.sm0,.sm1, .sm2, .sm3, .sm4, .sm5 {
   opacity:0;
}

/**** CHICOS Y CHICAS ****/

.stamp {
   color:#cee2ff!important;
   text-shadow:none;
   border-color:#cee2ff!important;
}

.boyfriend .lom span {
   background-color:#b5c1ff;
   color:#ffffff;
   text-transform:uppercase;
   font:9px Calibri;
   letter-spacing:1px;
   padding:4px 6px;
   border-radius:10px;
   -moz-border-radius:10px;
   -webkit-border-radius:10px;
}

.boyfriend .inLove {
   margin:5px 10px 5px -10px!important;
   margin-right:20px!important;
}

.css-button.pink {
   background: linear-gradient(to bottom,#56c8ee 0%,#66d6fa 60%,#2ba6cd 100%)!important;
}

.css-button {
   border:solid #ffffff 1px!important;
}

.friend .player-name a {
   color:#9fe0ff;
   font-weight:normal;
   font-style:oblique;
   margin-bottom:2px;
   display:block;
   font-size:12px;
}

/**** MENSAJES ****/

#discussion table {
   background:#e5ecff!important;
   border:1px solid #99b4ff!important;
}

#discussion table tr {
   border-bottom:1px solid #99b4ff!important;
}

#discussion table tr.read_0 {
   background:#e5ecff!important;
   border:1px solid #99b4ff!important;
}

#discussion table tr td a, #discussion table .author_pseudo {
   color:#b5c1ff!important;
   font-size:11px!important;
}

#discussion table tr.read_0 .author_avatar, #discussion table .author_avatar {
   background:#ccd9ff!important;
}

#discussion .player-name, .backToMessageList {
   float:none!important;
   padding-left:55px;
}

#discussion span {
   color:#9babff;
}

#discussion .warning p:nth-of-type(1) {
   text-align:center;
}

.backToMessageList {
   float:right!important;
   padding:3px 1px;
}

#discussion .message_bloc .type-1 {
   background:#ebe5ff!important;
}

#discussion .message_bloc .content {
   border:1px solid #e8ebff!important;
}

#discussion .message_bloc .delete-option {
   opacity:0;
   position:absolute;
   bottom:1px;
   right:138px;
   cursor:pointer;
   transition:opacity .3s;
   -webkit-transition:opacity .3s;
}

#discussion .colorized {
   color:#8296ff!important;
}

#discussion .message_bloc .delete-option span, #discussion .message_bloc .report-option span {
   transition:color .3s;
   -webkit-transition:color .3s;
   color:#e399ff;
   font:88% Calibri;
   text-transform:uppercase;
   letter-spacing:1px;
   padding:1px 6px;
}

/**** TARJETAS ****/

.idcard-admin {
   background-image:url(https://40.media.tumblr.com/882d06f4d9f4a980874bea92c18b3925/tumblr_nn47ntbIbP1te5zwko1_400.png)!important;
   padding-left:4px;
}

.idcard-admin span {
   color:#ffe5ec;
}


.idcard-moderator {
   background-image:url(https://41.media.tumblr.com/e8d7218562ae5dea112226f7759d4115/tumblr_nn47ntbIbP1te5zwko3_400.png)!important;
   padding-left:4px;
}

.idcard-moderator span {
   color:#e399ff;
}

.idcard-member { 
   background-image:url(https://40.media.tumblr.com/13e76a32ea2b9510e798e602f56330d5/tumblr_nn47ntbIbP1te5zwko2_400.png)!important;
   padding-left:4px;
}

.idcard-member span {
   color:#ff8ea2;
}
}
